#define loop 5.0

float func(vec3 p, float t){
    float f = 0.0;
    float a  = atan(p.y,p.x)*2.;
    float d = length(p);

    p.xy *= mat2(cos(a+t*.1)*d,sin(a)*d,-sin(a)*d,cos(a+t*.1)*d);

    f+=length(sin(p));
    f*= sin(p.z+t+cos(length(p.xy)+t));
    f = abs(f);
    f = pow(f,p.z);
    f = sin(f*d+t);
    return f;
}

