const vec3 worldUp = vec3(0.0,-1.0,0.0);
const float minStep = 0.1;
const float maxStep = 80.0;
const float delta = 0.01;
const float damping = 0.9;
const int numSteps = 100;

bool onALantern = false;

mat3 getViewMatrix (vec3 t, vec3 d, vec3 k)
{
	vec3 z = normalize(d);
    vec3 x = normalize(cross(d,k));
    vec3 y = normalize(cross(z,x));
    return mat3(x,y,z);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

float sdCone( vec3 p, vec2 c )
{
    // c must be normalized
    float q = length(p.xy);
    return dot(c,vec2(q,p.z));
}


float opRep( vec3 p, vec3 c )
{
    vec3 q = mod(p,c)-0.5*c;
    float sphereDistf = sdSphere(q,  0.8);
    float cylDistf = sdCappedCylinder(q,  vec2(0.4,1.0));
    float coneDistf = udBox(vec3(q.x,q.y+1.6,q.z), vec3(0.2,0.6,0.01));

    float summed = min(cylDistf , sphereDistf);
    summed = min(summed, coneDistf);

	onALantern = summed == sphereDistf;
    return summed;
}


float distf (vec3 pos)
{
	return opRep(pos,vec3(10.0));
}

vec3 normal (vec3 p)
{
    vec2 dm = vec2(delta, 0.0);
	return normalize(vec3(
    	distf(p+dm.xyy) - distf(p-dm.xyy),
        distf(p+dm.yxy) - distf(p-dm.yxy),
        distf(p+dm.yyx) - distf(p-dm.yyx)
    ));
}

float castRay ( vec3 pos, vec3 dir, out vec3 norm)
{
    float dist = minStep;
    for(int step = 0; step < numSteps; step++)
    {
        norm = pos + dir*dist;
        float normL = distf(norm);
        if(normL > delta || dist > maxStep){
            dist += normL*damping;
        }
    }
    return dist;
}


