		}

		float ship( vec3 p)
		{
		    return dot(vec3(0.9,0.3,0.5),abs(p))-1.0;
		}

		vec4 combine(vec4 val1, vec4 val2 )
		{
			if ( val1.w < val2.w ) return val1;
			return val2;
		}

		vec4 map( vec3 pos)
		{
