		void mainImage( out vec4 fragColor, in vec2 fragCoord ){

			vec2 position = ( fragCoord.xy / iResolution.xy );
			position -= .5;

			vec2 rot_t = vec2(iTime + cos(iTime*0.33) - length( position *sin(iTime*0.21)));
			mat2 rot_x = mat2(cos(rot_t.y), -sin(rot_t.x),sin(rot_t.x), cos(rot_t.y)) * 2.0;

			int sec = int(mod(iTime,10.));

			float dir2 = 1.0;
			if(sec>=4) {
				if(sec>=7)
				{
					dir2 = -1.0;
				}
				position = position*rot_x*dir2;
			}

			vec3 dir = vec3( position, 1.0 );
			dir = normalize(dir);

			vec3 pos = vec3( .0, 3.5, 15.0);

			vec4 result;
			for (int i =0; i < ITER; i++)
			{
				result = map( pos);
				if (result.w < NEAR || result.w > FAR) break;
				pos += result.w * dir;
			}

			vec3 col = map(pos).xyz;
			if ( pos.z> 100. )
			{
				vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
				if(sec>=4) {
					p = p*rot_x*dir2;
				}

				p.y += 0.1;
				vec2 uv;

				float r = sqrt(dot(p,p));
				float a = atan(p.y,p.x);

				uv.x = 0.1 * (iTime*3.0) - .1 / r;
				uv.y = 1. * a / 3.1416;

				vec2 position = floor(uv*20.0+sin(iTime)*2.0)/20.0;

				float ca = sin(3.14 * 20.0 * fract(sin(dot(position.xy,vec2(11.9898,78.233)))*43758.5453) + 2.0 * iTime);
				float cb = sin(3.14 * 30.0 * fract(sin(dot(position.xy,vec2(11.9898,78.233)))*43758.5453) + 3.0 * iTime);
				float cc = sin(3.14 * 20.0 * fract(sin(dot(position.xy,vec2(11.9898,78.233)))*43758.5453) + 1.0 * iTime);
				float color = ca*cb+cc-1.0;

				// took effect from http://glslsandbox.com/e#24979.0
			    float a2 = fract(atan(p.x, p.y) / Tau);
			    float d = length(p);

			    vec2 coord = vec2(pow(d, shape), a2) * 256.;
			    vec2 delta = vec2(-iTime * speed * 256., .5);

			    float c = 0.;
				coord += delta;
				c = max(c, Cell(coord));

			    col = vec3(0.3+color, 0.5+color, 0.7+color)+vec3(c * d);
			}
			else
			{
				vec3 lightPos = vec3(20.0, 20.0, 20.0 );
				vec3 light2Pos = normalize( lightPos - pos);
				vec3 eps = vec3( .1, .01, .0 );
				vec3 n = vec3( result.w - map( pos - eps.xyy ).w,
					       result.w - map( pos - eps.yxy ).w,
					       result.w - map( pos - eps.yyx ).w );
				n = normalize(n);

				float lambert = max(.0, dot( n, light2Pos));
				col *= vec3(lambert);

				// specular : 
				vec3 h = normalize( -dir + light2Pos);
				float spec = max( 0., dot( n, h ) );
				col += vec3(pow( spec, 16.)) ;
			}

			vec2 p2 = -1.0 + 2.0 * fragCoord.xy / iResolution.xy - vec2(0.7,1.1);
			float digT=0.;
			digT+=digN(p2*5.-vec2(-.2,-1.),int(fract(iTime)*3.0))?1.:0.;
			digT+=digN(p2*5.-vec2(.2,-1.),int(fract(iTime)*6.0))?1.:0.;
			digT+=colon(p2*5.-vec2(.5,-1.))?1.:0.;
			digT+=digN(p2*5.-vec2(.8,-1.),int(fract(iTime)*9.0))?1.:0.;
			digT+=digN(p2*5.-vec2(1.2,-1.),int(mod(getScs(),10.)))?1.:0.;

			fragColor = vec4( col+vec3(digT), 1.0);
		}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float yZoom = 5.0+sin(iTime);
    vec2 pos = vec2(60.0*uv.x-60.0,
                    yZoom * uv.y + float(5.0*iTime)- yZoom);
    vec2 res = vec2(0.0);

    for (float n = 0.9; n < 100.0; n += 1.0) {
        res.x += pow(n, pos.x)*cos(pos.y*log(n));
        res.y += pow(n, pos.x)*sin(pos.y*log(n));        
    }
    fragColor = vec4(sin(res.x), sin(res.y), cos(res.x*res.y), 1.0);
}

const int MAX_REFLECT = 5;
const vec3 black = vec3(0.,0.,0.);
const vec3 white = vec3(1.,1.,1.);
const vec3 red = vec3(1.,0.,0.);
const vec3 blue = vec3(0.,0.,1.);

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Light {
    int type;

    vec3 position;
    vec3 direction;
    float range;

    vec3 color;
    float intensity;
};

struct Sphere {
    vec3 center;
    float radius;

    int material;
    vec3 color;
    float reflectiveness;
};

struct Plane {
    vec3 normal;
    float distance;

    int material;
    vec3 color;
    float reflectiveness;
};

struct IntersectionResult {
    float distance;
    vec3 position;
    vec3 normal;

    int material;
    vec3 color;
    float reflectiveness;
};

vec3 theEye = vec3(0, 0, 0);

Light theLight = Light(3, vec3(5.,5.,0.), normalize(vec3(-5.,-7.,5.)), 50., white, 1.);
//Light theLight = Light(2, vec3(0.,5.,5.), normalize(vec3(0.,-1.,0.)), 50., white, 1.);

float spotLightCosHalfPhi = cos(radians(60.));
float spotLightCosHalfTheta = cos(radians(10.));
float spotLightFalloff = 2.;


Sphere redSphere = Sphere(vec3(-2.5, 0, 5), 2., 11, red, 0.25);
Sphere blueSphere = Sphere(vec3(2.5, 0, 5), 2., 11, blue, 0.25);
Plane thePlane = Plane(vec3(0.,1.,0.), -2., 1, white, 0.5);

vec3 specColor = white;
float shininess = 10.;


IntersectionResult minIntersection(IntersectionResult a, IntersectionResult b)
{
    if (a.distance < 0.) return b;
    if (b.distance < 0.) return a;
   	if (a.distance < b.distance) return a; else return b;
}

float intersectPlane(Ray ray, Plane plane) {
    float a = dot(ray.direction, plane.normal);
    if (a >= 0.0) return -1.;

    vec3 position = plane.normal * plane.distance;
    float b = dot(plane.normal, ray.origin - position);
    if (b <= 0.0) return -1.;

    return -b / a;
}

float intersectSphere(Ray ray, Sphere sphere) {
    vec3 co = ray.origin - sphere.center;
    float discriminant = dot(co, ray.direction) * dot(co, ray.direction)
            - (dot(co, co) - sphere.radius * sphere.radius);

    if (discriminant >= 0.0)
        return -dot(ray.direction, co) - sqrt(discriminant);
    else
        return -1.;
}

IntersectionResult intersectScene(Ray ray)
{
    float dis = -1.;

    dis = intersectSphere(ray, redSphere);
    IntersectionResult redSphereIntersection;
    redSphereIntersection.distance = dis;
    if (dis >= 0.)
    {
        redSphereIntersection.position = ray.origin + redSphereIntersection.distance * ray.direction;
        redSphereIntersection.normal = normalize(redSphereIntersection.position - redSphere.center);
        redSphereIntersection.material = redSphere.material;
        redSphereIntersection.color = redSphere.color;
        redSphereIntersection.reflectiveness = redSphere.reflectiveness;
    }

    dis = intersectSphere(ray, blueSphere);
    IntersectionResult blueSphereIntersection;
    blueSphereIntersection.distance = dis;
    if (dis >= 0.)
    {
        blueSphereIntersection.position = ray.origin + blueSphereIntersection.distance * ray.direction;
        blueSphereIntersection.normal = normalize(blueSphereIntersection.position - blueSphere.center);
        blueSphereIntersection.material = blueSphere.material;
        blueSphereIntersection.color = blueSphere.color;
        blueSphereIntersection.reflectiveness = blueSphere.reflectiveness;
    }

    dis = intersectPlane(ray, thePlane);
    IntersectionResult planeIntersection;
    planeIntersection.distance = dis;
    if (dis >= 0.)
    {
        planeIntersection.position = ray.origin + planeIntersection.distance * ray.direction;
        planeIntersection.normal = thePlane.normal;
        planeIntersection.material = thePlane.material;
        planeIntersection.color = thePlane.color;
        planeIntersection.reflectiveness = thePlane.reflectiveness;
    }

    IntersectionResult ret = minIntersection(redSphereIntersection, blueSphereIntersection);
    ret = minIntersection(planeIntersection, ret);
    return ret;
}

float calcShadow(vec3 position, vec3 lightDir)
{
    Ray shadowRay = Ray(position, lightDir);
    IntersectionResult ret = intersectScene(shadowRay);
    if (ret.distance >= 0.) return 0.;
    else return 1.;
}

void calcLight(out vec3 lightDir, out vec3 lightColor, vec3 position)
{
    float intensity = theLight.intensity;
    if (theLight.type == 1)
    {
        lightDir = -theLight.direction;
        lightColor = theLight.color * intensity;
    }
    else if (theLight.type == 2 || theLight.type == 3)
    {
        lightDir = theLight.position - position;
        float distance = length(lightDir);
        lightDir /= distance;

        float atten = theLight.range / (0.5 * distance * distance + 2. * distance + 1.);

        if (theLight.type == 3)
        {
            float rio = dot(-theLight.direction, lightDir);
            float factor = 1.;
            if (rio <= spotLightCosHalfPhi) factor = 0.;
            else if (rio > spotLightCosHalfTheta) factor = 1.;
            else
            {
                factor = (rio - spotLightCosHalfPhi) / (spotLightCosHalfTheta - spotLightCosHalfPhi);
                factor = clamp(pow(factor, spotLightFalloff), 0., 1.);
            }
            intensity *= factor;
        }

        lightColor = theLight.color * (intensity * atten);
    }

    lightColor *= calcShadow(position, lightDir);
}

vec3 calcColor(int material, vec3 position, vec3 normal, vec3 color)
{
    if (material == 0) return black;

    vec3 lightDir;
    vec3 lightColor;
    calcLight(lightDir, lightColor, position);

    if (material == 1)
    {
        float tmp = abs(floor(position.x * 1.) + floor(position.z * 1.));
        return ((mod(tmp, 2.) < 1.) ? black : white) * lightColor;
    }
    else if (material == 2)
    {
        vec2 texCoord = vec2(position.x * 0.2, position.z * 0.2);
        return texture(iChannel0, texCoord).xyz * lightColor;
    }
    else if (material == 10)
    {
        return color * (max(0.0, dot(normal, lightDir)) * .8 + .2) * lightColor;
    }
    else if (material == 11)
    {
        float lambertian = max(0.0, dot(normal, lightDir));
        float specular = 0.0;
        if (lambertian > 0.0)
        {
            vec3 reflectDir = normalize(normal * dot(normal, lightDir) * 2. - lightDir);
            float specAngle = max(dot(reflectDir, normalize(theEye - position)), 0.);
            specular = pow(specAngle, shininess);
        }

        return (color * lambertian + specColor * specular) * lightColor;
    }
    else if (material == 12)
    {
        float lambertian = max(0.0, dot(normal, lightDir));
        float specular = 0.0;
        if (lambertian > 0.0)
        {
            vec3 halfDir = normalize(lightDir + normalize(theEye - position));
            float specAngle = max(dot(halfDir, normal), 0.);
            specular = pow(specAngle, shininess * 4.);
        }

        return (color * lambertian + specColor * specular) * lightColor;
    }
    return black;
}

vec3 rayTrace(Ray ray)
{
    vec3 color = black;
    float reflectiveness = 1.0;
    for (int i=0; i<MAX_REFLECT; ++i)
    {
        IntersectionResult ret = intersectScene(ray);
        if (ret.distance >= 0.)
        {
            vec3 sampleColor = calcColor(ret.material, ret.position, ret.normal, ret.color);
            color += sampleColor * (1. - ret.reflectiveness) * reflectiveness;
            reflectiveness *= ret.reflectiveness;

            if (reflectiveness > 0.)
            {
                vec3 reflectDir = normalize(ret.normal * (-2. * dot(ret.normal, ray.direction)) + ray.direction);
                ray = Ray(ret.position, reflectDir);
            }
            else break;
        }
        else break;
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float posPixelX = fragCoord.x / iResolution.x * 2.0 - 1.0;
    float posPixelY = fragCoord.y / iResolution.y * 2.0 - 1.0;
    float radio = iResolution.x / iResolution.y;

    theEye = vec3(sin(iTime*0.2) * 0.2, sin(iTime*0.3) * 0.2, -1.);
    vec3 rayDir = normalize(vec3(posPixelX * radio, posPixelY, 0) - theEye);
    Ray theRay = Ray(theEye, rayDir);

    fragColor.xyz = rayTrace(theRay);
}

const float pi = 3.14159;
const float pi2 = pi * 2.;

mat2 rot2D(float r)
{
    float c = cos(r), s = sin(r);
    return mat2(c, s, -s, c);
}

// convert distance to alpha
float dtoa(float d, float amount)
{
    float a = clamp(1.0 / (clamp(d, 1.0/amount, 1.0)*amount), 0.,1.);
    return a;
}



// distance functions: ------------------------------------------------



// circle
float sdCircle(vec2 uv, vec2 origin, float radius)
{
    float d = length(uv - origin) - radius;
    return d;
}

// signed distance to segment of 1D space. like, for making a vertical column
float sdSegment1D(float uv, float a, float b)
{
    return max(a - uv, uv - b);
}
float sdAxisAlignedRect(vec2 uv, vec2 tl, vec2 br)
{
  	vec2 d = max(tl - uv, uv - br);
    return length(max(vec2(0.0), d)) + min(0.0, max(d.x, d.y));
}

// the big question is what is the best way to INPUT a free rect? tl+br+angle? p1,p2,p3?
float sdRect(vec2 uv, vec2 a, vec2 b, float angle)
{
    // flatten the line to be axis-aligned.
    vec2 rectDimensions = b - a;
    mat2 rotMat = rot2D(-angle);
    a *= rotMat;
    b *= rotMat;
	return sdAxisAlignedRect(uv * rotMat, a, b);
}

// really a line segment with line width is just a rect expressed differently
float sdLineSegment(vec2 uv, vec2 a, vec2 b, float lineWidth)
{
    // flatten the line to be axis-aligned.
    vec2 rectDimensions = b - a;
    float angle = atan(rectDimensions.x, rectDimensions.y);
    mat2 rotMat = rot2D(-angle);
    a *= rotMat;
    b *= rotMat;
    float halfLineWidth = lineWidth / 2.;
    a -= halfLineWidth;
    b += halfLineWidth;
	return sdAxisAlignedRect(uv * rotMat, a, b);
}

// union of line segment and 2 circles
float sdLineSegmentRounded(vec2 uv, vec2 a, vec2 b, float lineWidth)
{
    // flatten the line to be axis-aligned.
    vec2 rectDimensions = b - a;
    float angle = atan(rectDimensions.x, rectDimensions.y);
    mat2 rotMat = rot2D(-angle);
    a *= rotMat;
    b *= rotMat;
    float halfLineWidth = lineWidth / 2.;

    uv *= rotMat;
    vec2 tl = vec2(a.x - halfLineWidth, a.y);
    vec2 br = vec2(b.x + halfLineWidth, b.y);

    return min(min(sdAxisAlignedRect(uv, tl, br),
                   sdCircle(uv, a, halfLineWidth)),
                   sdCircle(uv, b, halfLineWidth));
}


// squircle
// http://en.wikipedia.org/wiki/Squircle
float sdSquircle(vec2 uv, vec2 origin, float radius, float power, float rot_)
{
    mat2 rot = rot2D(rot_);
	vec2 v = abs((origin*rot) - (uv*rot));
    float d = pow(v.x,power) + pow(v.y, power);
    d -= pow(radius, power);
    return d;
}

// distance to edge of hexagon
float sdHexagon(vec2 p, vec2 hexPos, float hexRadius, float hexRotation)
{
    mat2 rot = rot2D(hexRotation);
	vec2 v = abs((hexPos*rot) - (p*rot));

	vec2 topBottomEdge = vec2(0., 1.);
	const vec2 sideEdges = vec2(0.86602540358, 0.5);// cos(radians(30)), sin(radians(30))

	float dot1 = dot(v, topBottomEdge);
	float dot2 = dot(v, sideEdges);
    float dotMax = max(dot1, dot2);

    return dotMax - hexRadius;
}


// signed distance to a 2D triangle
// thank you iq: https://www.shadertoy.com/view/XsXSz4
float sdTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2)
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    vec2 d = min( min( vec2( dot( pq0, pq0 ), v0.x*e0.y-v0.y*e0.x ),
                       vec2( dot( pq1, pq1 ), v1.x*e1.y-v1.y*e1.x )),
                       vec2( dot( pq2, pq2 ), v2.x*e2.y-v2.y*e2.x ));

	return -sqrt(d.x)*sign(d.y);
}




