#define STEPS 80
#define ERROR_LIMIT 0.001
#define STEPMUL 0.25 //smaller values give more precis results, if combined with higher STEPS

#define SLICE_START 0.0 //(sin(iTime*1.0)*0.5+1.0)*3.0

struct Sample {
    float dis;
    vec2 ao;
    vec3 no;
};

vec2 do_ao(float dis) {
    float r = abs(dis)/AO_DIST;

    r = smoothstep(0.0, 1.0, r);
    r = 1.0 - pow(r*0.7, 0.4);

    return vec2(r*0.9, 1.0); //min(r, 1.0);
    //return pow(min(r*3.0, 1.0), 0.5);
}

vec2 combine_ao(vec2 a, vec2 b) {
    float r1 = a[0]+b[0];
    float r2 = a[1]+b[1];
    return vec2(r1, r2);
}

Sample subtract(Sample a, Sample b) {
    float num = a.ao[0] > b.ao[0]+0.01 ? a.ao[1] : b.ao[1];
    vec2 ao = vec2(0.0, 0.0);

    if (a.dis < 0.05) {
    	ao = a.ao + b.ao;
    } else {
        ao = b.ao;
    }

    ao = b.ao;
    //ao = a.ao + b.ao;

    vec3 no = -a.dis > b.dis ? a.no : b.no;

    return Sample(
        max(-a.dis, b.dis),
        ao,
        no);
}


Sample add(Sample a, Sample b) {
    vec3 diff;

    vec3 no = a.dis < b.dis ? a.no : b.no;

    return Sample(min(a.dis, b.dis), combine_ao(a.ao, b.ao), no);
}

//*
Sample sphere(vec3 p, vec3 c, float radius) {
    p -= c;
    float w = 2.5; //(pow(radius, 0.1));
    return Sample(length(p) - radius, w*do_ao(length(p)-radius), -p);
}

Sample box(vec3 p, vec3 c, vec3 size) {
    p = abs(p-c) - size;

    float d = max(max(p[0], p[1]), p[2]);
    float d1 = d;

    //*
    d = max(d, p[0]+p[1]);
    d = max(d, p[0]+p[2]);
    d = max(d, p[1]+p[2]);
    //*/

    vec3 no = vec3(0.0);

    float b1 = float(p[0] > p[1] && p[0] > p[2]);
    float b2 = float(p[1] > p[0] && p[1] > p[2]);
    float b3 = float(p[2] > p[0] && p[2] > p[1]);

    no = vec3(b1, b2, b3);

    return Sample(d, do_ao(d), no);
}

