vec3 rotz(vec3 p, float th, float sfac) {
    th += iTime*2.0*sfac+0.9;
    return vec3(
        sin(th)*p[0] - cos(th)*p[1],
        sin(th)*p[1] + cos(th)*p[0],
        p[2]
    );
}
vec3 twist(vec3 p, float sfac) {
    float tfac = 10.0; //(1.0-sin(iTime)*0.25-0.75)*10.0; //10.0
    return rotz(p, p[2]*tfac+sfac*6.0, sfac); //vec3(p[0]+(sin(p[2]*20.0))*0.1, p[1]+cos(p[2]*20.0)*0.01, p[2]);
}

Sample bcross(vec3 p, float w) {
    return add(add(box(p, vec3(0), vec3(w, w, 1000.0)),
               box(p, vec3(0), vec3(w, 10000.0, w))),
               box(p, vec3(0), vec3(10000.0, w, w)));
}

float rep(float p, float step) {
    //return mod(p, step);

    float t = mod(abs(p), step)/step;
    float t2 = mod(floor(p/(step*2.0)), 2.0);

    if (sign(p) > 0.00)
        t = 1.0 - t;

    if (t2 != 0.0) {
//        t = 1.0 - t;
    }    

    return t*step; //sign(p)*step;
}

vec3 rep(vec3 p, float step) {
    return vec3(
        rep(p[0], step),
        rep(p[1], step),
        rep(p[2], step)
    );
}

Sample sample1(Sample cur, vec3 p, vec3 rd, float t) {
    vec3 b = abs(p)-vec3(0.4, 0.4, 0.1);
    Sample ret = cur;

    Sample ret2 = box(p, vec3(0.0), vec3(1.0, 1.0, 1.0)); 
    float w = 1.0/DIV, w2 = w;
    vec3 op = p;
    float fac = 1.0, off = 0.0;

    for (int i=0; i<LEVELS; i++) {
        Sample crs = bcross(p, w);

        ret2 = subtract(crs, ret2);

        fac *= 1.0/w;
        off = -w;

        p = rep(op*fac+vec3(off,off,off), 2.0)*0.5;
    }

    ret = ret2;
    return ret;
}
