// DDOODM's first raymarched distance field!
// Derived from:
// "monkey saddle by joates (Nov-2014)"
//
// Deinyon Davies & Joates, November 2014

// Thank you, IQ!
vec3 hash3( float n ) { return fract(sin(vec3(n,n+1.0,n+2.0))*43758.5453123); }

//#define IS_BOXED
const float size = 0.45;

// iq's Smooth Minimum
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float map( in vec3 p )
{
    float plane = p.y + 0.5 + 0.025*(sin(p.x*10.0+sin(p.z*14.0))+cos(p.z*10.0+sin(p.x*20.0)));
    plane = max(plane, length(p + vec3(0.0,0.5,0.0)) - 3.0);

    float d1 = length(p) - size;

    float d2 = max(p.y + p.x/2.0, p.y - p.x/2.0);

    float d3 = length(p) - size * 0.85;

    float d4 = length(p - vec3(0.0, 0.25*cos(iTime)-0.25, 0.0)) - size * 0.25*cos(iTime*1.2);

    float hem = max(d1,d2);

    float bow = max(hem, -d3);

    float metabowl = smin(d4, bow, 8.0);
    metabowl *= 0.5;

    return min(metabowl, plane);
}

vec3 calcNormal( in vec3 p ) {
    vec2 e = vec2( 0.01, 0.0 );
    return normalize( vec3( map( p + e.xyy ) - map( p - e.xyy ),
                            map( p + e.yxy ) - map( p - e.yxy ),
                            map( p + e.yyx ) - map( p - e.yyx ) ) );
}

// Thank you, mu6k!
float amb_occ(vec3 p)
{
	float acc=0.0;
	#define ambocce 0.1

	acc+=map(p+vec3(-ambocce,-ambocce,-ambocce));
	acc+=map(p+vec3(-ambocce,-ambocce,+ambocce));
	acc+=map(p+vec3(-ambocce,+ambocce,-ambocce));
	acc+=map(p+vec3(-ambocce,+ambocce,+ambocce));
	acc+=map(p+vec3(+ambocce,-ambocce,-ambocce));
	acc+=map(p+vec3(+ambocce,-ambocce,+ambocce));
	acc+=map(p+vec3(+ambocce,+ambocce,-ambocce));
	acc+=map(p+vec3(+ambocce,+ambocce,+ambocce));
	return 0.5+acc;
}

vec3 ACESFilm(vec3 x)
{
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = -1.0 + 2.0*(fragCoord.xy/iResolution.xy);
	uv.x *= iResolution.x/iResolution.y;

	float time = iTime*0.1;

    // view origin & direction
	vec3 ro = vec3( size );
	vec3 tp = vec3( 0.0 );
    tp.y = -0.25*size;

    // camera orbit
    float m = iMouse.x/iResolution.x * 6.0;
    ro.x = cos(time+m)*length(ro);
    ro.z = sin(time+m)*length(ro);
    ro.y = iMouse.y/iResolution.y;

    vec3 lit = vec3( 200.0, 75.0, 200.0 );
    vec3 ldir = normalize(lit);
    vec3 col = vec3( 0.0 );

    // camera view
	vec3 cw = normalize( tp-ro );
	vec3 cp = vec3( 0.0, 1.0, 0.0 );
	vec3 cu = normalize( cross(cw, cp) );
	vec3 cv = normalize( cross(cu, cw) );
	vec3 rd = normalize( uv.x*cu + uv.y*cv + 1.5*cw );

    float tmax = 200.0;
    float h = 1.0;
    float t = 0.0;
    float iterations = 0.0;
    for( int i = 0; i < 100; i++ ) {
        if( h < 0.0001 || t > tmax ) break;
        h = map( ro + t*rd );
        t += h;
        iterations = float(i);
    }

    if ( t < tmax ) {
        vec3 pos = ro + t*rd;
       	vec3 nor = calcNormal( pos );
        vec3 viw = normalize(-pos);

        // Ambient
       	col = vec3( 0.45, 0.23, 0.2 );

        // Diffuse
       	col += vec3( 1.3, 0.5, 0.2 ) * clamp( dot( nor, ldir ), 0.0, 1.0 );
        if(pos.y > -0.4)
        {
        	col *= texture(iChannel0, pos.xz * 4.0).xyz;

            // Reflection
        	//col += 0.2 * texture(iChannel2, -cw-viw).xyz;

        	// Specular
        	col += vec3( 0.5 ) * pow( clamp( dot( -reflect(nor, rd), ldir), 0.0, 1.0 ), 255.0);
        }
        else
        {
            col *= texture(iChannel1, pos.xz).xyz;

            // Fog?
            col *= clamp(2.5 - length(pos), 0.0, 1.0);
        }

        // Sky light
       	col += vec3( 0.0, 0.1, 0.18 ) * clamp( nor.y, 0.0, 1.0 );

        // AO
        col *= amb_occ(ro + (t-0.1)*rd);
    }
    else {
        // Hit the sky
        col = vec3( 0.30, 0.15, 0.1 ) * 0.7;
        col += clamp(vec3( 0.9, 0.4, 0.3 ) * dot(rd, ldir), 0.0, 1.0);
        col += clamp(vec3( 0.0, 0.1, 0.18 ) * dot(rd, -ldir), 0.0, 1.0);
    }

    // Contrast
    col = smoothstep( 0.0, 1.0, col );

    // Vigneting
    vec2 q = fragCoord.xy/iResolution.xy;
    col *= 0.2 + 0.8*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.2);

    col += vec3(0.1,0.15,0.2) * iterations/150.0;

    col = ACESFilm(col);

    // Gamma
    col = pow(col, vec3(0.8));

    fragColor = vec4( col, 1.0 );
}

#define CHECKERED_SQUARE_SIZE 0.25
#define MAX_DEPTH 2
#define EPSILON 0.001

struct Ray
{
	vec3 pos;
    vec3 dir;
};

struct Triangle
{
    vec3 a;
    vec3 b;
    vec3 c;
};

struct Sphere
{
 	vec3 pos;
    float radius;
    vec4 color;
};

struct Camera
{
	vec3 pos;
    vec3 forward;
    vec3 up;
    vec3 right;
};

struct DirectionalLight
{
  	vec3 dir;
    vec4 color;
};

struct IntersectionInfo
{
	vec3 point;
    vec3 normal;
    vec4 color;
};

Triangle triangles[2];
Sphere sphere = Sphere(vec3(0,0,2), 0.7, vec4(1,0,0,0));
Camera camera = Camera(vec3(0,2,-1), normalize(vec3(0,-1,1)), normalize(vec3(0,1,1)), vec3(1,0,0));
vec4 background = vec4(0);
DirectionalLight dirLight = DirectionalLight(vec3(0,0,1.0), vec4(1));
vec4 ambientLight = vec4(0.2,0.2,0.2, 1.0);

void InitTriangles()
{
    vec3 verts[4];
    verts[0] = vec3(-2,-2,0);
    verts[1] = vec3(2,-2,0);
    verts[2] = vec3(-2,-2,4);
    verts[3] = vec3(2,-2,4);

    triangles[0] = Triangle(verts[0], verts[1], verts[2]);
    triangles[1] = Triangle(verts[3], verts[2], verts[1]);    
}

mat4 GetCameraTransform(Camera camera)
{
	mat4 ret = mat4(
        vec4(normalize(camera.right), 0),
        vec4(normalize(camera.up), 0),
        vec4(normalize(camera.forward), 0),
        vec4(camera.pos, 1));
    return ret;
}

void Lookat(in vec3 point)
{
    camera.forward = normalize(point - camera.pos);

    // TODO; subtract component along forward
    //vec3

    camera.up = normalize(cross(camera.right, camera.forward));
	camera.right = normalize(cross(camera.forward, camera.up));
}

float AngleBetween(vec3 a, vec3 b)
{
	float angle = acos(dot(normalize(a), normalize(b)));

    return angle;
}

void SolveQuadratic(float a, float b, float c, out float sol1, out float sol2)
{
   	float discriminant = pow(b,2.0) - 4.0 * a * c;

    if (discriminant < 0.0)
    {
    	sol1 = sol2 = -1.0;
        return;
    }

 	sol1 = (-b + sqrt(discriminant))/(2.0*a);
    sol2 = (-b + sqrt(discriminant))/(2.0*a);
}

vec4 GetSphereColor(vec3 intersectionPoint)
{
    vec3 distFromCenter = intersectionPoint - sphere.pos;
 	return
        vec4(normalize(intersectionPoint.xy) /
             (min(intersectionPoint.x, min(intersectionPoint.y, intersectionPoint.z))/1.0),
             0.5+sin(intersectionPoint.x + intersectionPoint.y + intersectionPoint.z)    
             , 1.0);

/*		vec4(
             0.5+sin(distFromCenter.x + distFromCenter.y), 
             0.5+sin(distFromCenter.y * 2.0),   
             0.8+sin(distFromCenter.x + distFromCenter.y + distFromCenter.z)    
             , 1.0); 
*/
}

void RaycastBackground(Ray ray, out vec4 color)
{
    color = vec4(0);
}

// https://www.cs.princeton.edu/courses/archive/fall00/cs426/lectures/raycast/sld018.htm
float RaycastTriangle(Ray ray, Triangle triangle, out IntersectionInfo intersection)
{
    vec3 ap = triangle.a - ray.pos;
    vec3 bp = triangle.b - ray.pos;
    vec3 cp = triangle.c - ray.pos;
    vec3 normal = normalize(cross(triangle.b - triangle.a, triangle.c - triangle.a));
    float d = -dot(triangle.a, normal);
    float dist = -(dot(ray.pos, normal) + d) / dot(normal, ray.dir);

    if (dot(ray.dir, -normalize(cross(bp, ap))) < 0.0 ||
        dot(ray.dir, -normalize(cross(cp, bp))) < 0.0 ||
        dot(ray.dir, -normalize(cross(ap, cp))) < 0.0)
    {
        return -1.0;  
    }

    intersection.normal = normal;
    intersection.point = ray.pos + dist * ray.dir;
    vec3 pa = intersection.point - triangle.a;
    vec3 pc = intersection.point - triangle.c;

    float distab = dot(pa, normalize(triangle.b - triangle.a));
    float distac = dot(pc, normalize(triangle.c - triangle.a));

    if ((mod(distab, CHECKERED_SQUARE_SIZE * 2.0) > CHECKERED_SQUARE_SIZE &&
         mod(distac, CHECKERED_SQUARE_SIZE * 2.0) > CHECKERED_SQUARE_SIZE)
        ||
		(mod(distab, CHECKERED_SQUARE_SIZE * 2.0) < CHECKERED_SQUARE_SIZE &&
         mod(distac, CHECKERED_SQUARE_SIZE * 2.0) < CHECKERED_SQUARE_SIZE))
    {
    	intersection.color = vec4(1);   
    }
    else
    {
    	intersection.color = vec4(0);   
    }

    return dist;
}

Ray GetReflectedRay(Ray inRay, IntersectionInfo intersection)
{
	vec3 reflectedDir = reflect(inRay.dir, intersection.normal);
    return Ray(intersection.point + EPSILON * reflectedDir, reflectedDir);
}

float RaycastSphere(Ray ray, Sphere sphere, out IntersectionInfo intersection)
{
    vec3 sphereCenterToRayOrigin = ray.pos - sphere.pos;
    float a = 1.0;
    float b = 2.0*dot(sphereCenterToRayOrigin, ray.dir);
    float c = dot(sphereCenterToRayOrigin,sphereCenterToRayOrigin) - pow(sphere.radius,2.0);

    float sol1, sol2, closeIntersection;
    SolveQuadratic(a,b,c, sol1, sol2);

    closeIntersection = min(sol1, sol2);

    if (closeIntersection < 0.0)
    {
     	return -1.0;   
    }

    intersection.point = ray.pos + closeIntersection * normalize(ray.dir);
    sphere.color = GetSphereColor(intersection.point);

    intersection.normal = normalize(intersection.point-sphere.pos);

    float diffuse = clamp(dot(intersection.normal, normalize(dirLight.dir)), 0.0, 1.0);
    float specular = pow(clamp(dot(reflect(dirLight.dir, intersection.normal), -ray.dir), 0.0, 1.0), 25.0);
    vec4 result = sphere.color * diffuse + specular;
    intersection.color = result + ambientLight * sphere.color;

    return closeIntersection;
}

bool RaycastSingle(Ray ray, out IntersectionInfo intersection)
{
    float dist = 0.0;
    float minDist = 100.0;
    IntersectionInfo currIntersection;

    dist = RaycastSphere(ray, sphere, currIntersection);
    if (dist > 0.0)
    {
        minDist = dist;
        intersection = currIntersection;
    }

    for (int i = 0; i < 2; i++)
    {
        dist = RaycastTriangle(ray, triangles[i], currIntersection);
        if (dist >= 0.0 && dist < minDist)
        {
            minDist = dist;
            intersection = currIntersection;
        }
    }

    if (minDist == 100.0)
    {
    	return false;
    }

	return true;
}

vec4 Raycast(Ray ray)
{
    Ray currentRay = ray;
    IntersectionInfo intersection;
    vec4 color;
    if (RaycastSingle(ray, intersection))
    {
        color = intersection.color;
    	currentRay = GetReflectedRay(currentRay, intersection);

    	if (RaycastSingle(currentRay, intersection))
        {
    		color = color + 0.1 * intersection.color;
        }
    }
    else
    {
    	RaycastBackground(ray, color);    
    }

        vec4 reflectedColor;
	for (int i = 0; i < MAX_DEPTH; i++)
    {
        //color = color + ;   
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitTriangles();

    float animationDuration = 10.0;
    float timeMod = mod(iTime,animationDuration);
    float timeModPercent = timeMod / animationDuration;
    //dirLight = DirectionalLight(normalize(vec3(timeMod-2.0, 1.0, 1.0)), vec4(1.0));

    float angleAroundSphere = timeModPercent * 2.0 * 3.14159;
    camera.pos.x = sphere.pos.x + 4.0 * cos(angleAroundSphere);
    camera.pos.z = sphere.pos.z + 4.0 * sin(angleAroundSphere);

//    Lookat(sphere.pos);
    Lookat(vec3(0));

    float minResolution = min(iResolution.x, iResolution.y);
    vec2 normalizedResolution = iResolution.xy/minResolution;
    vec2 pixelSizeCamSpace = normalizedResolution / iResolution.xy;
    vec3 imageCoordCamSpace = vec3(fragCoord/minResolution - (normalizedResolution / 2.0) + (pixelSizeCamSpace / 2.0), 1.0);
	vec3 imageCoordWorldSpace = (GetCameraTransform(camera) * vec4(imageCoordCamSpace, 1)).xyz;
    Ray ray = Ray(camera.pos, normalize(imageCoordWorldSpace - camera.pos));

    //sphere.color = vec4(imageCoord.xy + 0.5,0.5+sin(imageCoord.x + imageCoord.y),1.0);    

    fragColor = Raycast(ray);
}

// Based on IQ code
float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float sdTorus( vec3 p, vec2 t )
{
  return length( vec2(length(p.xz)-t.x,p.y) )-t.y;
}

float sdHexPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
#if 0
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
#else
    float d1 = q.z-h.y;
    float d2 = max((q.x*0.866025+q.y*0.5),q.y)-h.x;
    return length(max(vec2(d1,d2),0.0)) + min(max(d1,d2), 0.);
#endif
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
#if 0
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
#else
    float d1 = q.z-h.y;
