	//vec3 ta = vec3(0.0, 1.0, 0.0); //vec3( -0.5, -0.4, 0.5 );
    float t = 3.0;
    float x = mod(time, t) > t * 0.5 ? 
        		time - t  + mod(time, t): 
    			time - mod(time, t);

