vec2 pixelSpaceToUVSpace(vec2 p, vec2 resolution) {
  vec2 uv = 2.0*p/resolution-1.0;
  if (resolution.x > resolution.y)
      uv[0] *= resolution.x/resolution.y;
  else
      uv[1] *= resolution.y/resolution.x;
  return uv;
}

float signedDistanceToLine(vec2 p, vec2 n,float d) {
 	// the line passes point d*n and has n as its normal
    return dot(p-d*n,n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = pixelSpaceToUVSpace(fragCoord.xy,iResolution.xy); // get pixel position
    vec2 m_uv = pixelSpaceToUVSpace(iMouse.xy,iResolution.xy); // get mouse position

    vec2 n = normalize(m_uv); // define the normal of the line

    // Calculate the scanning range required to cover the whole screen
    float D = max(abs(signedDistanceToLine(vec2(iResolution.x/iResolution.y,-1.0),n,0.0)),
                  abs(signedDistanceToLine(vec2(iResolution.x/iResolution.y,1.0),n,0.0)));

    float d;
    if (iMouse.z < 0.01) {
    	d = D*sin(iTime); // if mouse is released, let the line scan
    }
    else {
        d = length(m_uv); // if mouse is clicked, let the line pass the mouse's position
    }

    float a = smoothstep(0.0,0.001,signedDistanceToLine(uv,n,d)); // define region
    float c = mix(0.0,0.4,a); // color the region
    fragColor = vec4(c,c,c,1.0); 
}

