void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*(fragCoord.xy / iResolution.y -vec2(.9,.5)); 
    float luv = length(uv)/sqrt(2.);


    float t = cos(.5*iTime);
    a = a/n*pow(t,3.);   float  c=cos(a),s=sin(a);  
    float l=1.; 
    float k = 2.+(.5+.5*cos(.1*iTime));
    mat2 m=mat2(c,-s,s,c)*k;
    vec4 paint = vec4(pow(.4,1.),pow(.15,1.),pow(.06,1.),1.), col=vec4(1.), p=vec4(1.);

    for (float i=0.; i<5.; i++) {
        //if (l<luv) break;
        float w = l/n;
        p *= pow(paint,vec4(w,w,w,1.));
        float d = max(abs(uv.x),abs(uv.y));
        vec4 col0 = smoothstep(.9+.008*l,.9-.008*l,d)*p; 
           col0.a = smoothstep(.9+.008*l,.9-.008*l*(1.-abs(t)),d);
        col = col0 + (1.-col0.a)*col;
        l *= k;
        uv *= m; uv -= sign(uv);
    }

	fragColor = col;
}

const int MAGIC_BOX_ITERS = 13;
const float MAGIC_BOX_MAGIC = 0.55;

float magicBox(vec3 p) {
    // The fractal lives in a 1x1x1 box with mirrors on all sides.
    // Take p anywhere in space and calculate the corresponding position
    // inside the box, 0<(x,y,z)<1
    p = 1.0 - abs(1.0 - mod(p, 2.0));

    float lastLength = length(p);
    float tot = 0.0;
    // This is the fractal.  More iterations gives a more detailed
    // fractal at the expense of more computation.
    for (int i=0; i < MAGIC_BOX_ITERS; i++) {
      // The number subtracted here is a "magic" paremeter that
      // produces rather different fractals for different values.
      p = abs(p)/(lastLength*lastLength) - MAGIC_BOX_MAGIC;
      float newLength = length(p);
      tot += abs(newLength-lastLength);
      lastLength = newLength;
    }

    return tot;
}

// A random 3x3 unitary matrix, used to avoid artifacts from slicing the
// volume along the same axes as the fractal's bounding box.
const mat3 M = mat3(0.28862355854826727, 0.6997227302779844, 0.6535170557707412,
                    0.06997493955670424, 0.6653237235314099, -0.7432683571499161,
                    -0.9548821651308448, 0.26025457467376617, 0.14306504491456504);



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv are screen coordinates, uniformly scaled to go from 0..1 vertically
	vec2 uv = fragCoord.xy / iResolution.yy;

    // scroll a certain number of screenfuls/second
    uv.x += iTime*0.4;

    // Rotate uv onto the random axes given by M, and scale
    // it down a bit so we aren't looking at the entire
    // 1x1x1 fractal volume.  Making the coefficient smaller
    // "zooms in", which may reduce large-scale repetition
    // but requires more fractal iterations to get the same
    // level of detail.
    vec3 p = 0.5*M*vec3(uv, 0.0);

    float result = magicBox(p);
    // Scale to taste.  Also consider non-linear mappings.
    result *= 0.03;

	fragColor = vec4(vec3(result),1.0);
}

void mainImage(out vec4 c, vec2 q)
{
    float a=iTime*.1+1.,b=.5,g,e,t=0.,s;
    vec3 r=vec3(0.,0.,3.),w=normalize(vec3((q-iResolution.xy/2.)/iResolution.y,-.5)),p;

    mat2 x=mat2(cos(a),sin(a),sin(a),-cos(a)),y=mat2(cos(b),sin(b),sin(b),-cos(b));

    w.xz=y*w.xz;
    r.xz=y*r.xz;

    w.yz=x*w.yz;
    r.yz=x*r.yz;

    c.rgb=vec3(0.,0.,.02);

    for(int i=0;i<150;++i)
    {
        p=r+w*t;

        float f=.25,d=1e4;
        for(int j=0;j<2;++j)
        {
            s=.2*dot(p,p);
            p=p/s;
            f*=s;
            g=p.z;
            e=atan(p.y,p.x);
            p=(mod(p,2.)-1.)*1.25;
        }

        d=min((length(abs(p.xy)-1.3)-.1)*f,1e2);

        if(d<1e-3)
            break;

        c.rgb+=vec3(.3,.4,.8)*(pow(.5+.5*cos(g*.5+a*77.+cos(e*10.)),16.))*
            (1.-smoothstep(0.,1.,70.*d))*.25;

        t+=d;
    }
}

const float pi = 3.14159;

mat3 xrot(float t)
{
    return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrot(float t)
{
    return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrot(float t)
{
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}

float box(vec3 p, vec3 s)
{
	vec3 q = clamp(p, -s, s);
    return length(p - q);
}

float wscl = 6.0;

float map(vec3 p)
{
    float h = wscl * 2.0;
	vec3 g = (fract((p+vec3(h*0.5))/h) * 2.0 - 1.0) * h;
    vec3 q = g;
    for (int i = 0; i < 4; ++i) {
        vec3 r = sign(q);
        q = r + reflect(q - r, normalize(r));
        vec3 aq = abs(q);
        q -= sign(q) * min(aq.x, min(aq.y, aq.z)) * 0.5;
    }
    float d = box(q, vec3(1.0));
    float tr = 0.5 + 0.5 * sin(p.z*0.5);
    tr = mix(2.5, 6.0, tr);
    float c = length(g.xy) - tr;
    float e = length(g.xz) - tr;
    float f = length(g.yz) - tr;
    float cv = min(min(c, e), f);
    return max(d,-cv);
}

vec3 normal(vec3 p)
{
	vec3 o = vec3(0.01, 0.0, 0.0);
    return normalize(vec3(map(p+o.xyy) - map(p-o.xyy),
                          map(p+o.yxy) - map(p-o.yxy),
                          map(p+o.yyx) - map(p-o.yyx)));
}

float trace(vec3 o, vec3 r)
{
    float t = 0.0;
    for (int i = 0; i < 42; ++i) {
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.35;
    }
    return t;
}

vec3 textex(vec3 p)
{
	vec3 ta = texture(iChannel0, vec2(p.y,p.z)).xyz;
    vec3 tb = texture(iChannel0, vec2(p.x,p.z)).xyz;
    vec3 tc = texture(iChannel0, vec2(p.x,p.y)).xyz;
    return (ta + tb + tc) / 3.0;
}

vec3 shade(vec3 o, vec3 r, vec3 w, vec3 sn, float t)
{
    float fd = map(w);

    float fog = 1.0 / (1.0 + t * t * 0.01 + fd * 100.0);
    vec3 diff = textex(w);

    vec3 lit = vec3(0.5);
    vec3 spec = vec3(0.0);

    for (int i = -1; i <= 1; i+=2) {
        float j = float(i) * 0.5 + 0.5;

        vec3 loff = vec3(0.0, 0.0, 4.0) * sign(float(i));

        vec3 ldir = normalize(o + loff - w);

        vec3 lcol = mix(vec3(1.0,1.0,0.5), vec3(0.5,1.0,1.0), j);

        lit += max(dot(sn,ldir), 0.0) * lcol;

        vec3 refl = reflect(ldir, -sn);
        float spr = clamp(dot(refl, r), 0.0, 1.0);
        spr = pow(spr, 4.0);

        spec += lcol * spr;
    }

    diff *= lit;
    diff += spec * 0.5;
    return diff * fog;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 o = vec3(0.0, 0.0, iTime*2.0);
    vec3 r = normalize(vec3(uv, 1.2));
    r *= zrot(iTime*0.25);

    float t = trace(o, r);
    vec3 w = o + r * t;
    vec3 sn = normal(w);
	vec3 sc = shade(o, r, w, sn, t);

	fragColor = vec4(sqrt(sc), 1.0);
}

#define time iTime

const float INTERSECTION_PRECISION = .001;
const float MAX_TRACE_DISTANCE     = 10.;
const int NUM_TRACE_STEPS          = 130;

const vec3 lightPos = vec3( 3. , 0.  , 0. );

vec3 lookPos;


float smin_2_3(float a, float b, float k) {
  float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
  return mix(b, a, h) - k * h * (1.0 - h);
}



// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){

	return (d1.x<d2.x) ? d1 : d2;

}

float opU( float d1, float d2 )
{
    return min(d1,d2);
}


void doCamera( out vec3 camPos , out vec3 camTar , in float time ){

  camPos = vec3( 0. , 0. , 1.5 );
  camTar = vec3( 0. );

}

mat3 calcLookAtMatrix( vec3 camPos , vec3 camTar , float roll ){

  vec3 up = vec3( sin( roll ) ,cos( roll ) , 0. );
  vec3 ww = normalize( camTar - camPos );
  vec3 uu = normalize( cross( ww , up ) );
  vec3 vv = normalize( cross( uu , ww ) );

  return mat3( uu , vv , ww );

}
float sdPlane( vec3 pos ){
 return pos.y;   
}
float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdCappedCone( in vec3 p, in vec3 c )
{
    vec2 q = vec2( length(p.xy), -p.z - c.z );
    vec2 v = vec2( c.z*c.y/c.x, -c.z );

    vec2 w = v - q;

    vec2 vv = vec2( dot(v,v), v.x*v.x );
    vec2 qv = vec2( dot(v,w), v.x*w.x );

    vec2 d = max(qv,0.0)*qv/vv;

    return sqrt( dot(w,w) - max(d.x,d.y) )* sign(max(q.y*v.x-q.x*v.y,w.y));
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

mat3 matInverse( mat3 m ){


    vec3 a = vec3(

        m[1][1] * m[2][2] - m[2][1] * m[1][2],
        m[0][2] * m[2][1] - m[2][2] * m[0][1],
        m[0][1] * m[1][2] - m[1][1] * m[0][2]

    );

    vec3 b = vec3(

        m[1][2] * m[2][0] - m[2][2] * m[1][0],
        m[0][0] * m[2][2] - m[2][0] * m[0][2],
        m[0][2] * m[1][0] - m[1][2] * m[0][0]

    );

     vec3 c = vec3(

        m[1][0] * m[2][1] - m[2][0] * m[1][1],
        m[0][1] * m[2][0] - m[2][1] * m[0][0],
        m[0][0] * m[1][1] - m[1][0] * m[0][1]

    );


    return mat3( 

       a.x , a.y , a.z ,
       b.x , b.y , b.z ,
       c.x , c.y , c.z

    );




}

float face( vec3 pos , vec3 p , mat3 rot ){

    mat3 iRot = matInverse( rot );

    vec3 headPos  = iRot * ( pos - p );
    vec3 nosePos  = iRot * ( pos - p );
    vec3 lePos    = iRot * ( pos - p + rot * vec3( -.1 , -.1 , 0.));
    vec3 rePos    = iRot * ( pos - p + rot * vec3( .1 , -.1 , 0.));
    vec3 mouthPos = iRot * ( pos - p + rot * vec3( 0. , .12 , 0.));


   	float nose = sdCappedCone( nosePos , vec3( .1 , .04 , .1 ) );
    float head = udBox( headPos , vec3( .2 , .2 , .01 ));

    float re = sdSphere( rePos , .03 );
    float le = sdSphere( lePos , .03 );

    float mouth = sdTorus( mouthPos , vec2( .04 , .02 ));

    float f = head;

    f = smin_2_3( f , nose  , .04 );
    f = smin_2_3( f , le    , .04 );
    f = smin_2_3( f , re    , .04 );
    f = smin_2_3( f , mouth , .04 );

    return f;

}

float fire( vec3 pos ){

    vec3 p = vec3( 0. , -.3 , 0.5 );
    float f = sdSphere( pos - p  , .1 ); 


    for( int i  = 0; i < 10; i++ ){

       float h = mod( time * (abs( sin( float( i + 5 ))) +.3) * .05 , .3 );
       p  = vec3( 
         sin( ( h + .2) * float( i + 5 ) ) * .8 * h,
         h * 2. - .3,
         cos(( h +.3) * float( i + 5 ) ) * .8 * h + .5
       );   

       float r = abs( sin( p.x * time * .01 )  + sin( p.y * float( i +4 ) * time * .001 ));
       float v = sdSphere( pos - p  , (.3 - h ) * .2 ); 
       f = smin_2_3( f , v  , .04 );


	}


    return f;

}



vec2 map( vec3 pos ){


  vec2 res = vec2( sdSphere( pos - lookPos , .01 ) ,1. );
  vec2 res2;
  for( int i = 0; i < 4; i ++ ){


    vec3 p = vec3( (((float( i )+.5) / 4. ) - .5 ) * 2., 0.,0.);

    mat3 rot = calcLookAtMatrix( p , lookPos , 0. );

    res2 = vec2(face( pos , p , rot) , float( i ) ); 

    res = opU( res , res2 );

  }  

  res2 = vec2( fire( pos ) , 10. );

  res = opU( res , res2 );

  res2 = vec2( sdPlane( pos  - vec3( 0. , -.5 , 0. ) ), 100. );
  res = opU( res , res2 );


  return res;



}


// res = result;
vec2 calcIntersection( in vec3 ro , in vec3 rd ){

  float h     = INTERSECTION_PRECISION * 2.;
  float t     = 0.;
  float res   = -1.;
  float id    = -1.;

  for( int i = 0; i < NUM_TRACE_STEPS; i++ ){

    if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;

    vec2 m = map( ro + rd * t );

    h  = m.x;
    t += h;
    id = m.y;

  }

  if( t < MAX_TRACE_DISTANCE ) res = t;
  if( t > MAX_TRACE_DISTANCE ) id = -1.;

  return vec2( res , id ); 

}

// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<50; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 20.*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}


float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.612*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.5;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}


vec3 render( vec3 ro , vec3 rd ){

  vec2 res = calcIntersection( ro , rd );
  vec3 col = vec3( 1. );

  if( res.x > 0. ){

	vec3 pos = ro + rd * res.x;
      vec3 nor = calcNormal( pos );

      float AO = calcAO( pos , nor );

      float fix = pow( AO , .4 );

      vec3 coloa = vec3( .6 , .2 , .1 );

      // fire
      if( res.y > 9.9 ){
          coloa = vec3( .6 , .4 , .2 ); 
      }

      if( res.y > 99.9 ){
      	coloa = vec3( .2 , .2 , .5 );    
      }

      coloa *= nor * .5 + .5;
      col = mix( vec3( 1. ) , coloa , 1. - fix );

  }

  return col;

}


