// using the base ray-marcher of Trisomie21: https://www.shadertoy.com/view/4tfGRB#

#define T iTime
#define r(v,t) { float a = t*T, c=cos(a),s=sin(a); v*=mat2(c,s,-s,c); }

void mainImage( out vec4 f, vec2 w ) {
    vec4 p = vec4(w,0,1)/iResolution.yyxy-.5, d,c; p.x-=.4; // init ray 
     r(p.xz,.13); r(p.yz,.2); r(p.xy,.1);   // camera rotations
    d = p;                                 // ray dir = ray0-vec3(0)
    p.z += 5.*T;

    for (float i=1.; i>0.; i-=.01)  
    {
        vec4 u = floor(p/8.), t = mod(p, 8.)-4.; // objects id + local frame
         r(t.xy,u.x); r(t.xz,u.y); r(t.yz,1.);    // objects rotations
        u = sin(78.*(u+u.yzxw));                 // randomize ids
        t -= u;                                  // jitter positions
        u = .5+.5*u;

        t = abs(t);     // here for relief X symmetry
        //c = texture(iChannel0, t.zxy-3.);  // c for color, c.x for displacement
        c = p/p;                            // just the canonical shape           
        // c = mix(p/p,c,.5+.5*sin(T)); 

        // t = abs(t);  // here for no relief symmetry
        // float x = min(t.y, length(t.xz) -1.5 + 1.*c.x); // pilars
        float x1 = length(t.xyz) -3.6 + c.x;           // spheres
        float x2 = max(t.x,max(t.y,t.z)) -3. + c.x;      // bricks
        float x = max(-x1,x2);
        // float x = ( u.x>.6? length(t.xyz)-.5 :max(t.x,max(t.y,t.z)) ) -2. + c.x; // sphs+bricks

        // f = p/p*p.w*.01;                // show depth
        // f = p/p*x*100.;                 // show dist
        f = c*mix(1.,i*i*4.,.5+.5*sin(T));      // color texture + black fog (try c alone !)
        // f *= (.5+.5*u)*vec4(1,1,.005,1);     // gradient block coloring (set off random id)
        f *= u*.5;                              // random block coloring

        if(x<.01) break;    // hit !
        p -= d*x;           // march ray
     }
}

const float degreesToRadians = 3.14159 / 180.0;
const float rayEpsilon = 0.01;
const int rayIterations = 50;
const float exposure = 1.5;
const float tau = 6.2831853071;

// http://filmicgames.com/archives/75
vec3 TonemapAndGammaCorrection( vec3 color )
{
  color *= exposure;

  vec3 x = max(vec3( 0 ),color- vec3( 0.004 ) );
  vec3 retColor = (x*(6.2*x+.5))/(x*(6.2*x+1.7)+0.06);
  return retColor;
}

float Sphere( vec3 position, vec3 spherePosition, float sphereRadius )
{
  return length(  position - spherePosition ) - sphereRadius;
}

float Plane( vec3 position, vec4 plane ) // ax + by + cz + d = 0
{
  return dot(  position, plane.xyz ) + plane.w;
}


float map( vec3 position )
{   

  float sphereRadius = 0.5;
  vec3 spherePosition = vec3( 
      0,
      0,
      sphereRadius + abs(sin(iTime*5.0)) );
  float sphereDist = Sphere( position, spherePosition, sphereRadius );
  float dist = min( 
      sphereDist,
      Plane( position, vec4( 0, 0, 1, 0 ) ) );
  return dist;
}

vec3 GetNormal( vec3  position )
{
  float epsilon= 0.1;
  vec3 dx = vec3(epsilon,0,0);
  vec3 dy = vec3(0,epsilon,0);
  vec3 dz = vec3(0,0,epsilon);

  return normalize( vec3(
        map( position + dx )- map( position - dx ),
        map( position + dy )- map( position - dy ),
        map( position + dz )- map( position - dz ) ) );
}

// https://www.shadertoy.com/view/lsf3zr
// stolen shamelessly from iq catacombs.
// TODO: look at how this works
float calcAO( in vec3 pos, in vec3 nor )
{
  float ao = 0.0;
  float sca = 15.0;
  for( int i=0; i<5; i++ )
  {
    float hr = 15.0*(0.01 + 0.015*float(i*i));
    float dd = map( pos + hr*nor );
    ao += (hr-dd)*sca/15.0;
    sca *= 0.5;
  }
  return 1.0 - clamp( ao, 0.0, 1.0 );
}

void Raymarch(
    vec3 rayPosition,
    vec3 rayDirection,
    out float rayDepth,
    out int iteration )
{
  rayDepth = 0.0;
  iteration = 0;
  for( int i = 0; i < rayIterations; ++i )
  {
    vec3 rayPoint = rayPosition + rayDirection * rayDepth;
    float rayDist = map( rayPoint );
    rayDepth += rayDist;
    if( rayDist < rayEpsilon )
    {
      iteration = i;
      break;
    }

    ++iteration;
  }
}

vec3 AddLight(
    vec3 lightPosition,
    vec3 lightColor,
    float lightIntensity,
    vec3 geometryNormal,
    vec3 geometryPosition )
{
  vec3 lightToGeometry = geometryPosition - lightPosition;
  float distLightToGeometry = length( lightToGeometry );

  float attenuation = 1.0 / ( distLightToGeometry * distLightToGeometry );

  vec3 lightDirection = lightToGeometry / distLightToGeometry;
  float ndotl = max( 0.0, dot( geometryNormal, -lightDirection ) );

  return ndotl * lightColor * attenuation * lightIntensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
