  float aspectRatio = iResolution.x / iResolution.y;
  vec3 wsUp = vec3( 0, 0, 1 );

  // camera parameters
  vec3 wsCamPos = vec3( 0, -6 , 1 );
  vec3 wsCamDirection = vec3( 0, 1, 0 );
  vec3 wsCamRight = -normalize( cross( wsUp, wsCamDirection ) );
  vec3 wsCamUp = cross( wsCamRight, wsCamDirection );
  float camNear = 0.1;
  float camFar = 100.0;
  float camFovY = degreesToRadians * 60.0;

  float wsHalfProjH = camNear * tan( camFovY / 2.0 );
  float wsHalfProjW = wsHalfProjH * aspectRatio;

  vec3 wsCenterPixel = wsCamPos + wsCamDirection * camNear;
  vec3 wsCurrentPixel = 
    wsCenterPixel + 
    wsCamRight * wsHalfProjW * ndcPixel.x +
    wsCamUp * wsHalfProjH * ndcPixel.y;

  vec3 rayPosition = wsCurrentPixel;
  vec3 rayDirection = normalize( wsCurrentPixel - wsCamPos );

  float rayDepth;
  int iteration;
  Raymarch( rayPosition, rayDirection, rayDepth, iteration );

  vec3 wsHitPoint = rayPosition + rayDirection * rayDepth;
  vec3 wsHitNormal = GetNormal( wsHitPoint );


  vec3 color = vec3( 0, 0, 0 );

  for( int i = 0; i < 3; ++i )
  {
    float offset = float( i ) * tau / 3.0;
    vec3 lightPosition = vec3(
        2.0 * sin( iTime + offset ), 
        2.0 * cos( iTime + offset ), 
        2.0 );
    vec3 lightColor = vec3(0,0,0);
    lightColor[ i ] = 1.0;
    float lightIntensity = 1.0;

    color += AddLight(
        lightPosition,
        lightColor,
        lightIntensity,
        wsHitNormal,
        wsHitPoint );
  }


  color *= calcAO( wsHitPoint, wsHitNormal );
  color = TonemapAndGammaCorrection( color );
  fragColor = vec4( color, 1.0 );
}

const float MAGIC_BOX_MAGIC = 0.56;

float magicBox(vec3 p) {
    // The fractal lives in a 1x1x1 box with mirrors on all sides.
    // Take p anywhere in space and calculate the corresponding position
    // inside the box, 0<(x,y,z)<1
    p = 1.0 - abs(1.0 - mod(p, 2.0));

    float tot = 0.0;
    float L = length(p), L2;

    // This is the fractal.  More iterations gives a more detailed
    // fractal at the expense of more computation.
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;
    p = abs(p)/(L*L) - MAGIC_BOX_MAGIC; L2 = length(p); tot += abs(L2-L); L = L2;


    return tot;
}

