void doCamera( out vec3 camPos , out vec3 camTar , in float time ){

  camPos = vec3( 0. , 0. , 2.5 );
  camTar = vec3( 0. );

}

mat3 calcLookAtMatrix( vec3 camPos , vec3 camTar , float roll ){

  vec3 up = vec3( sin( roll ) ,cos( roll ) , 0. );
  vec3 ww = normalize( camTar - camPos );
  vec3 uu = normalize( cross( ww , up ) );
  vec3 vv = cross( uu , ww ); // already normalized

  return mat3( uu , vv , ww );

}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdCappedCone( in vec3 p, in vec3 c )
{
    vec2 q = vec2( length(p.xy), -p.z );
    vec2 v = vec2( c.z*c.y/c.x, -c.z );

    vec2 w = v - q;

    vec2 vv = vec2( dot(v,v), v.x*v.x );
    vec2 qv = vec2( dot(v,w), v.x*w.x );

    vec2 d = max(qv,0.0)*qv/vv;

    return sqrt( dot(w,w) - max(d.x,d.y) )* sign(max(q.y*v.x-q.x*v.y,w.y));
}

mat3 matInverse( mat3 m ){


    vec3 a = vec3(

        m[1][1] * m[2][2] - m[2][1] * m[1][2],
        m[0][2] * m[2][1] - m[2][2] * m[0][1],
        m[0][1] * m[1][2] - m[1][1] * m[0][2]

    );

    vec3 b = vec3(

        m[1][2] * m[2][0] - m[2][2] * m[1][0],
        m[0][0] * m[2][2] - m[2][0] * m[0][2],
        m[0][2] * m[1][0] - m[1][2] * m[0][0]

    );

     vec3 c = vec3(

        m[1][0] * m[2][1] - m[2][0] * m[1][1],
        m[0][1] * m[2][0] - m[2][1] * m[0][0],
        m[0][0] * m[1][1] - m[1][0] * m[0][1]

    );


    return mat3( 

       a.x , a.y , a.z ,
       b.x , b.y , b.z ,
       c.x , c.y , c.z

    );




}

float lookAtCone( vec3 p , vec3 pos   , vec3 lookP , vec3 c ){

    mat3 rot = calcLookAtMatrix( p , lookPos , 0. );
    rot = matInverse( rot );
    //vec2 res2 = vec2(udBox( rot * (pos - p) , vec3( .01 , .01 , .2 )  ) , float( i ) ); 

    float v = sdCappedCone( rot * ( pos - p ) ,c  ); 
	return v;


}

vec2 map( vec3 pos ){


  vec2 res = vec2( sdSphere( pos - lookPos , .1 ) ,1. );

  float fRes = res.x;
  for( int i = 0; i < 10; i ++ ){

    vec3 p  = vec3( 
      .8 * sin( ( float( i +1)+ time * .01 ) * 2. ) ,
      .8 * sin( ( float( i +1)+ time * .01 ) * 5. ) ,
      .8 * sin( ( float( i +1)+ time * .01 ) * 9. ) 
    );   


    vec2 res2 = vec2( lookAtCone(p , pos , lookPos ,  vec3( .1 , .02  , .3 ) ) , float( i ) ); 


    res = opU( res , res2 );

  }  

  return res;



}


// res = result;
vec2 calcIntersection( in vec3 ro , in vec3 rd ){

  float h     = INTERSECTION_PRECISION * 2.;
  float t     = 0.;
  float res   = -1.;
  float id    = -1.;

  for( int i = 0; i < NUM_TRACE_STEPS; i++ ){

    if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;

    vec2 m = map( ro + rd * t );

    h  = m.x;
    t += h;
    id = m.y;

  }

  if( t < MAX_TRACE_DISTANCE ) res = t;
  if( t > MAX_TRACE_DISTANCE ) id = -1.;

  return vec2( res , id ); 

}

vec3 calcNormal( vec3 pos ){

  vec3 eps = vec3( 0.01 , 0. , 0. );

  vec3 nor = vec3(  
    map( pos + eps.xyy ).x - map( pos - eps.xyy ).x,
    map( pos + eps.yxy ).x - map( pos - eps.yxy ).x,
    map( pos + eps.yyx ).x - map( pos - eps.yyx ).x
  );

  return normalize( nor );


}


void mainImage( out vec4 fragColor , in vec2 fragCoord){

  vec2 p = ( -iResolution.xy + 2.0 * fragCoord.xy ) / iResolution.y;

  // Getting mouse position!
  vec2 m =  ( -iResolution.xy + 2.0 * iMouse.xy ) / iResolution.y;;

  vec3 ro , ta;

  doCamera( ro , ta , time  );

  mat3 camMat = calcLookAtMatrix( ro , ta , 0. ); 

  // z = lens length 
  vec3 rd = normalize( camMat * vec3( p.xy , 2. ) ); 

  // Getting our mouse ray direction
  vec3 md = normalize( camMat * vec3( m.xy , 2. ) ); 

  lookPos = ro + md * 3.;

  vec2 res = calcIntersection( ro , rd );
  vec3 col = vec3( 0. );

  if( res.x > 0. ){

    vec3 pos = ro + rd * res.x;

    vec3 lightDir = normalize( pos - lightPos );
    vec3 nor = calcNormal( pos );

    float lightMatch = max( 0. , dot( nor , lightDir ) );

    vec3 refl = reflect( lightDir , nor );
    float reflMatch = max( 0. , dot( refl , rd ) );

    float rimMatch =  1. - max( 0. , dot( nor , -rd ) );


    vec3 norCol = (nor * .5 + .5);

    vec3 lambCol = ((nor * .5 + .5)* lightMatch);
    vec3 ambiCol = ( vec3( 1. ) -norCol )*.1;
    vec3 specCol = vec3( 1. , .8 , 0. ) * pow( reflMatch , 20. );
    vec3 rimCol  = vec3( .4 , 1. , .8 ) * pow( rimMatch, 4. );

    col = lambCol + ambiCol + specCol + rimCol;

  }

  fragColor = vec4( col , 1. );

}

//noise function taken from https://www.shadertoy.com/view/XslGRr

float hash( float n ){
	return fract(sin(n)*43758.5453);
}

//this noise function was originally 3D noise, 
//but I am just setting z to 0 for the sake of simplicity here
//also cause most effects only care about 2D noise
float noise( vec2 uv ){
	vec3 x = vec3(uv, 0);

	vec3 p = floor(x);
	vec3 f = fract(x);

	f       = f*f*(3.0-2.0*f);
	float n = p.x + p.y*57.0 + 113.0*p.z;

	return mix(mix(mix( hash(n+0.0), hash(n+1.0),f.x),
				   mix( hash(n+57.0), hash(n+58.0),f.x),f.y),
			   mix(mix( hash(n+113.0), hash(n+114.0),f.x),
				   mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
}

float ring( float angle, float dist, float ANG, float THICKNESS, float POS, float SIZE ) {
    //angles between 4 and 15 are good
    //negative thickness makes it black, values around 0.02 are good

    float ZIGZAG = abs( mod( angle, ANG ) - ANG * 0.5 ) * SIZE;
    return ceil( dist - POS + ZIGZAG) - ceil( dist - (POS+THICKNESS) + ZIGZAG);   
}
float burst( float angle, float dist, float ANG ) {
    float B = abs( mod( angle, ANG ) - ANG * 0.5 );
    return B;
}
float lim( float IN, float amount ) {
	return IN * amount + (1.0 - amount);   
}
float inv( float IN ) {
 	return 1.0 - IN;   
}
float ppp( float IN ) {
 	return IN * IN * IN;   
}
float dots( float angle, float dist, float ANG, float POS ) {
    return ppp(7.5*burst( angle, dist, ANG )/ANG) * ppp(inv(ANG*1.5*distance( dist, POS )));
}


