vec3 tex(vec2 uv)
{
	vec3 c = vec3(fract(uv.xyy));
	if(mod(uv.x * 2.0, 2.0) < 0.9) return vec3(0);
	if(mod(uv.y * 1.0, 1.0) < 0.9) return vec3(0);
	return c;
}

