	     uv  *= vec2(iResolution.x / iResolution.y, 1.0);
	     uv   = rot(uv, -iMouse.y * 0.015);
	vec3  c   = vec3(0);
	for(int i = 0 ; i < ITE_MAX; i++) {
		c = tex(vec2(uv.x / abs(uv.y / (float(i) + 1.0)) + M + iMouse.x * 0.015, abs(uv.y)));
		if(length(c) > 0.5) break;
		uv   = uv.yx * 1.3;
        uv.y -= M * 5.0;
