void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 percent = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    percent.x *= aspectRatio;

    vec2 mouse = (iMouse.xy / iResolution.xy) - vec2(0.25,0.5);
    mouse.x *= aspectRatio;
    vec2 A = vec2(0.5,-0.4);
    vec2 B = vec2(0.5,0.0);
    vec2 C = length(iMouse.xy) > 0.0 ? mouse : vec2(-0.3,0.2);

    vec3 color = vec3(1.0,1.0,1.0);


    float dist = approx_distance(percent, A, B, C);
    if (dist < EDGE + SMOOTH)
    {
        dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
        color *= vec3(dist);
    }

    dist = approx_distance2(percent, A, B, C);
    if (dist < EDGE + SMOOTH)
    {
        dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
        color = vec3(1.0,1.0,0.);
    }    

	fragColor = vec4(color,1.0);
}

