// A simple way to create color variation in a cheap way (yes, trigonometrics ARE cheap
// in the GPU, don't try to be smart and use a triangle wave instead).

// See http://iquilezles.org/www/articles/palettes/palettes.htm for more information


vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    // animate
    p.x += 0.01*iTime;

    // compute colors
    vec3                col = pal( p.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
    if( p.y>(1.0/7.0) ) col = pal( p.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.10,0.20) );
    if( p.y>(2.0/7.0) ) col = pal( p.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.3,0.20,0.20) );
    if( p.y>(3.0/7.0) ) col = pal( p.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,0.5),vec3(0.8,0.90,0.30) );
    if( p.y>(4.0/7.0) ) col = pal( p.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,0.7,0.4),vec3(0.0,0.15,0.20) );
    if( p.y>(5.0/7.0) ) col = pal( p.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(2.0,1.0,0.0),vec3(0.5,0.20,0.25) );
    if( p.y>(6.0/7.0) ) col = pal( p.x, vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(2.0,1.0,1.0),vec3(0.0,0.25,0.25) );


    // band
    float f = fract(p.y*7.0);
    // borders
    col *= smoothstep( 0.49, 0.47, abs(f-0.5) );
    // shadowing
    col *= 0.5 + 0.5*sqrt(4.0*f*(1.0-f));
    // dithering
    col += (1.0/255.0)*texture( iChannel0, fragCoord.xy/iChannelResolution[0].xy ).xyz;

	fragColor = vec4( col, 1.0 );
}

// the core technique here is a simple extension of raymarching - instead of the render
// time being a fixed global constant, it is advanced as rays are marched through the scene.

// this produces some interesting phenomena:
// * distorted shape of both spheres
// * doppler effect on vertical oscillation of orbiting sphere
// * propogation of light from src->surface->camera evident as light changes intensity
// * light propagation also evident in shadows, causing weird lava lamp effects

// the orbiting sphere appears broken up or sliced at slow photon speeds because the
// sphere is moving away from the ray and the raymarch struggles to catch it. there
// may be elegant ways to inform the raymarch of the motion in the scene so that the
// step size takes motion into account, but i couldnt think of an elegant and general
// solution.
// EDIT: over relaxation should help convergence in theory but i havent tried it:
// http://erleuchtet.org/~cupe/permanent/enhanced_sphere_tracing.pdf
// https://www.shadertoy.com/view/ldSSDV

// disable this if the framerate is low
#define MOTIONBLUR

#define MOUSEX (iMouse.x/iResolution.x)
#define MOUSEY (iMouse.y/iResolution.y)
#define PI (3.14159265)
#define TWOPI (2.*PI)
#define FLOORY -3.
#define ZFAR 1000.

// vol texture
float checker( vec3 pos, float scale );

// time it takes a photon to travel 1m. easier to deal with than light speed.
float light_time_per_m()
{
    return (iMouse.z > 0.) ? (min(1.,max((MOUSEY-0.25)/0.7,0.))*0.06) : (sin(iTime*.25)*.5+.5)*.06 ;
}

// light pos
vec3 light( float t )
{
    return vec3(2.,3.5*sin(4.*t),2.);
}
float lightI( float t )
{
    // fade light when close to floor to avoid harsh pop
    return clamp((light(t).y-FLOORY)/2.,0.,1.);
}

// scene
float sdf( vec3 pos, float t, out vec4 objPos )
{
    float d = ZFAR;

    float r = 1.;

    float stepR = 1.;

    vec3 c1 = vec3(.0,-0.5,0.);
    float d1 = stepR * length(pos-c1) - r;
    if( d1 < d )
    {
        objPos.xyz = c1;
        objPos.w = 1.;
        d = d1;
    }

    vec3 c2 = vec3(5.*cos(2.*t), sin(8.*t), 5.*sin(2.*t));
    float d2 = stepR * length(pos-c2) - r;
    if( d2 < d )
    {
        objPos.xyz = c2;
        objPos.w = 1.;
        d = d2;
    }

    vec3 c3 = vec3(0.,FLOORY,0.);
    vec3 n3 = vec3(0.,1.,0.);
    float d3 = abs(dot(pos-c3,n3));
    if( d3 < d )
    {
        objPos.xyz = c3;
        objPos.w = 5.;
        d = d3;
    }

    return d;
}
vec3 normal( vec3 pos, float t )
{
    vec2 dd = vec2(0.01,0.);
    vec4 c;
    return normalize(
        vec3(sdf(pos+dd.xyy,t,c), sdf(pos+dd.yxy,t,c), sdf(pos+dd.yyx,t,c) ) - vec3(sdf(pos,t,c))
                    );
}

// 4D raymarch
float raymarch( vec3 o, vec3 target, float start_time, float timeDir, out vec4 objPos )
{
    objPos = vec4(0.,0.,0.,1.);
    vec3 delta = target - o;
    float dist = length( delta );

    vec3 d = delta / dist;

    float eps = 0.001;

    float x = 0.;
    float t = start_time;

    for( int i = 0; i < 150; i++ )
    {
        float dx = sdf( o + x * d, t, objPos );

        if( abs(dx) < eps )
        {
            return x;
        }

        dx *= .7;
        x += dx;
        if( x >= dist )
            break;

        // progress time as ray advances to simulate flight time of photon
        t += timeDir * light_time_per_m() * dx;
    }

    return ZFAR;
}
float raymarch_againstphotons( vec3 o, vec3 target, float start_time, out vec4 objPos )
{
    return raymarch( o, target, start_time, -1., objPos );
}
float raymarch_withphotons( vec3 o, vec3 target, float start_time, out vec4 objPos )
{
    return raymarch( o, target, start_time, 1., objPos );
}

// given a surface position and time - when would a photon have set off from
// the light to arrive at the surface? not straightforward for a moving light
float lightPhotonStartTime( vec3 finalPos, float finalTime )
{
    float startTime = finalTime;

    // my old friend FPI
    for( int i = 0; i < 3; i++ )
    {
        startTime = finalTime - light_time_per_m() * length( light( startTime ) - finalPos );
    }

    return startTime;
}

vec3 render( vec3 o, vec3 d, float ti )
{
    vec4 objPos;
	float geom = raymarch_againstphotons( o, o + ZFAR * d, ti, objPos );

    vec3 amb = vec3(1.,0.9,0.8) * .3;

    if( geom == ZFAR )
        return amb * .5;

    float geomT = ti - geom * light_time_per_m();

    vec3 pt = o + geom * d;
    vec3 n = normal( pt, geomT );
    vec3 col = amb * checker(pt-objPos.xyz,objPos.w);

    // raymarch from light to surface for shadow
    float lightT = lightPhotonStartTime( pt, geomT );
    float lenToLight = length(light(lightT) - pt);

    vec4 dummy;
    float lightGeom = raymarch_withphotons( light(lightT), pt, lightT, dummy );
    if( lightGeom > lenToLight-.1 )
    {
        vec3 lightDir = normalize( light(lightT) - pt );
        col = (amb + lightI(lightT)*vec3(.7) * clamp(dot( n, lightDir ), 0., 1.)) * checker(pt-objPos.xyz,objPos.w);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2. - 1.;
    uv.x *= iResolution.x/iResolution.y;
    float fov = .5;
    uv *= fov;

    // cam setup
    float r = 15.;
    vec3 o = vec3( r*cos(MOUSEX*TWOPI*1.5), 0., r*sin(MOUSEX*TWOPI*1.5) );
    vec3 d = normalize(-o);
    vec3 ri = vec3( -sin(MOUSEX*TWOPI*1.5), 0., cos(MOUSEX*TWOPI*1.5) );
    d += uv.x * ri + uv.y * vec3(0.,1.,0.);
    d = normalize(d);

   	fragColor = vec4(.0);

    // shoot 5 rays for some brute force MB
   	fragColor += vec4(render(o,d,iTime), 1.);

    #ifdef MOTIONBLUR
   	fragColor += vec4(render(o,d,iTime-0.006), 1.);
   	fragColor += vec4(render(o,d,iTime-0.003), 1.);
   	fragColor += vec4(render(o,d,iTime+0.003), 1.);
   	fragColor += vec4(render(o,d,iTime+0.006), 1.);
    fragColor /= 5.;
    #endif

    // finally blend in slider
    float tpm = light_time_per_m();
	vec2 uvs = (fragCoord.xy / iResolution.xy);
    if( uvs.x == clamp(uvs.x,0.06,0.09) )
    {
        float lw = 2./iResolution.x;
        float alpha = smoothstep( 0.075-lw,0.075, uvs.x ) - smoothstep( 0.075,0.075+lw, uvs.x );
        alpha *= smoothstep(0.25-lw,0.25,uvs.y) - smoothstep(0.948,0.95,uvs.y);
        float sliderHeight = 0.25 + 0.7*tpm/0.06;
        float sliderR = 0.02;
        float r = length((uvs-vec2(0.075,sliderHeight))/vec2(iResolution.y/iResolution.x,1.));
        float sliderAlpha = smoothstep(sliderR-lw*1.5,sliderR,r) - smoothstep(sliderR,sliderR+lw*1.5,r);
        alpha = .5*max(alpha, sliderAlpha );
	    fragColor.xyz = mix( fragColor.xyz, vec3(1.), alpha );
    }
}

float checker( vec3 pos, float scale )
{
    float mi = .9;
    float ma = 1.;

    pos /= scale;

    bool greater = abs(fract(pos.x)) > 0.5;

    if( abs(fract(pos.z)) > 0.5 )
        greater = !greater;
    if( abs(fract(pos.y)) > 0.5 )
        greater = !greater;

    return greater ? ma : mi;
}

/*

More info on b-splines and other curves here:
http://www.ibiblio.org/e-notes/Splines/Intro.htm

*/

#define P0  0.25
#define P1  (sin(iTime*1.5) * 0.5)
#define P2  0.21
#define P3  -0.1
#define P4  0.2
#define P5  (iMouse.z <= 0.0 ? 0.25 : iMouse.y / iResolution.y - 0.5)
#define P6  -0.25
#define P7  0.0

#define EDGE   0.005
#define SMOOTH 0.0025

float N_i_1 (in float t, in float i)
{
    // return 1 if i < t < i+1, else return 0
    return step(i, t) * step(t,i+1.0);
}

float N_i_2 (in float t, in float i)
{
    return
        N_i_1(t, i)       * (t - i) +
        N_i_1(t, i + 1.0) * (i + 2.0 - t);
}

float SplineValue(in float t)
{
    return
        P0 * N_i_2(t, 0.0) +
        P1 * N_i_2(t, 1.0) +
        P2 * N_i_2(t, 2.0) +
        P3 * N_i_2(t, 3.0) +
        P4 * N_i_2(t, 4.0) +
        P5 * N_i_2(t, 5.0) +
        P6 * N_i_2(t, 6.0) +
        P7 * N_i_2(t, 7.0);       
}

// F(x,y) = F(x) - y
float F ( in vec2 coords )
{
    // time in this curve goes from 0.0 to 9.0 but values
    // are only valid between 1.0 and 8.0
    float T = coords.x*7.0 + 1.0;    
    return SplineValue(T) - coords.y;
}

// signed distance function for F(x,y)
float SDF( in vec2 coords )
{
    float v = F(coords);
    float slope = dFdx(v) / dFdx(coords.x);
    return abs(v)/length(vec2(slope, -1.0));
}

// signed distance function for Circle, for control points
float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v/length(g); 
}

//-----------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 percent = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    percent.x *= aspectRatio;

    vec3 color = vec3(1.0,1.0,1.0);
    float dist = SDFCircle(percent, vec2(0.0 / 7.0,P0));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }

    dist = SDFCircle(percent, vec2(1.0 / 7.0,P1));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    }    

    dist = SDFCircle(percent, vec2(2.0 / 7.0,P2));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }    

    dist = SDFCircle(percent, vec2(3.0 / 7.0,P3));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }     

    dist = SDFCircle(percent, vec2(4.0 / 7.0,P4));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }   

    dist = SDFCircle(percent, vec2(5.0 / 7.0,P5));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    }      

    dist = SDFCircle(percent, vec2(6.0 / 7.0,P6));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }        

    dist = SDFCircle(percent, vec2(7.0 / 7.0,P7));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }       

    if (percent.x >= 0.0 && percent.x <= 1.0)
    {
    	dist = SDF(percent);
    	if (dist < EDGE + SMOOTH)
    	{
        	dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
        	color *= vec3(dist);
    	}
    }

	fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sep = 15.;
	float lines = iResolution.y / sep;

    vec2 oguv = fragCoord.xy / iResolution.xy;
	vec2 uv = (fragCoord.xy-(iResolution.xy*0.5)) / iResolution.yy;
    uv *= vec2(1.2);

    vec2 c = vec2(sin(iTime*1.7)*0.4, cos(iTime*1.3)*0.25);
    c = vec2(uv.x-c.x, uv.y-c.y);

    float dist = sqrt(c.x*c.x + c.y*c.y);

    if(dist < 0.5) {
       uv.y -= (c.y) * sin( (0.5-dist)/(0.5) * 3.14);
    }

    if(mod((uv.y+0.125)*lines, 1.0) < 0.25) {
        fragColor = vec4(0.0);
    }
    else {
		fragColor = vec4(oguv,0.5+0.5*sin(iTime),1.0);
    }

}

float rayStrength(vec2 raySource, vec2 rayRefDirection, vec2 coord, float seedA, float seedB, float speed)
{
	vec2 sourceToCoord = coord - raySource;
	float cosAngle = dot(normalize(sourceToCoord), rayRefDirection);

	return clamp(
		(0.45 + 0.15 * sin(cosAngle * seedA + iTime * speed)) +
		(0.3 + 0.2 * cos(-cosAngle * seedB + iTime * speed)),
		0.0, 1.0) *
		clamp((iResolution.x - length(sourceToCoord)) / iResolution.x, 0.5, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y = 1.0 - uv.y;
	vec2 coord = vec2(fragCoord.x, iResolution.y - fragCoord.y);


	// Set the parameters of the sun rays
	vec2 rayPos1 = vec2(iResolution.x * 0.7, iResolution.y * -0.4);
	vec2 rayRefDir1 = normalize(vec2(1.0, -0.116));
	float raySeedA1 = 36.2214;
	float raySeedB1 = 21.11349;
	float raySpeed1 = 1.5;

	vec2 rayPos2 = vec2(iResolution.x * 0.8, iResolution.y * -0.6);
	vec2 rayRefDir2 = normalize(vec2(1.0, 0.241));
	const float raySeedA2 = 22.39910;
	const float raySeedB2 = 18.0234;
	const float raySpeed2 = 1.1;

	// Calculate the colour of the sun rays on the current fragment
	vec4 rays1 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		rayStrength(rayPos1, rayRefDir1, coord, raySeedA1, raySeedB1, raySpeed1);

	vec4 rays2 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		rayStrength(rayPos2, rayRefDir2, coord, raySeedA2, raySeedB2, raySpeed2);

	fragColor = rays1 * 0.5 + rays2 * 0.4;

	// Attenuate brightness towards the bottom, simulating light-loss due to depth.
	// Give the whole thing a blue-green tinge as well.
	float brightness = 1.0 - (coord.y / iResolution.y);
	fragColor.x *= 0.1 + (brightness * 0.8);
	fragColor.y *= 0.3 + (brightness * 0.6);
	fragColor.z *= 0.5 + (brightness * 0.5);
}

