//main texture function
float sample_chainlink(vec2 uv, int do_dv) {

    float stepsa = floor(7.0*4.0*0.5);
    float stepsb = floor(10.0/2.0);
	float c;
    float tm = iTime*2.0;

    //tm = sin(tm)*0.5+0.5;// + sin(tm*2.0) + cos(tm*3.0);
    //tm = pow(tm, 5.0)*5.0;
    tm *= 2.0;
    uv[0] -= iTime*0.2;

    float yadd = sin(uv[0]*1.0+tm)*0.1;//*sin(uv[0]*5.0+tm)*0.1;

    uv[1] += yadd;
    vec2 pos = uv;

    float xadd = mod(floor(pos[1]*10.0), 2.0);
    c = mod(floor((pos[0])*7.0*stepsa+xadd*stepsa*0.5), stepsa)*mod(floor(pos[1]*10.0*stepsb), stepsb);
    c = max(min(c, 1.0), 0.0);

    float inside = mod(c+1.0, 2.0);

    xadd = mod(floor(-0.1+pos[1]*10.0), 2.0);
    float x =  mod(abs(-0.033+pos[0]*7.0+(1.0-xadd)*0.5), 1.0);
    float y =  mod(abs(-0.1+pos[1]*10.0)+0.5, 1.0);

    x = (x-0.5)*4.0 + 0.5;

    x = smoothstep(0.0, 1.0, x)*0.25 + x*0.75;
    y = smoothstep(0.0, 1.0, y)*0.25 + y*0.75;
    x = x-0.5;
    y = y-0.5;

    //box
    c = min2(abs(x), abs(y));
	float c2 = c;

    c = pow(smoothstep(0.0, 1.0, c), 1.0);

    uv *= 3.141*0.5;
    float thx = uv[0]*28.0+3.0, thy=uv[1]*20.0+3.1;

    float ring = abs((cos(thx)+sin(thy)));
    ring = 1.0 - pow(ring, 2.0);

    float parta = 1.0-2.0*pow(c, 0.5);
    float partb = parta*ring;

    if (do_dv == 0) {
        parta = max(parta, 0.0);
        partb = max(parta*ring, 0.0);
    }

    c = c + 0.5*pow(abs(partb), 1.5);

    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv *= 1.5;
    uv[0] *= 0.75;
    float filter1 = 0.3;

    //offset the crappy derivatives of this function to get a mirror-like effect.

#define DF 0.05 - abs(sin(7.0*uv[0])*sin(15.0*uv[1]+1.0))*0.01
    float c = sample_chainlink(uv, 1);
    float dx = (sample_chainlink(uv+vec2(DF, 0.0), 1)-c)/DF;
    float dy = (sample_chainlink(uv+vec2(0.0, DF), 1)-c)/DF;

    c = sample_chainlink(uv, 0);

    vec3 dv = normalize(vec3(dx, dy, c));

    vec3 light = normalize(vec3(-1.0, 1.5, 1.0));

    float d = abs(dot(dv, light)*0.5+0.5);
    d = pow(d, 1.0)*1.0;
    d = d*0.5 + 0.5;

    c = c*d;
    fragColor = vec4(c, c, c, 1.0);

}

#define RM_FACTOR   0.9
#define RM_ITERS     90

float plasma(vec3 r) {
	float mx = r.x + iTime / 0.130;
	mx += 20.0 * sin((r.y + mx) / 20.0 + iTime / 0.810);
	float my = r.y - iTime / 0.200;
	my += 30.0 * cos(r.x / 23.0 + iTime / 0.710);
	return r.z - (sin(mx / 7.0) * 2.25 + sin(my / 3.0) * 2.25 + 5.5);
}

float scene(vec3 r) {
	return plasma(r);
}

float raymarch(vec3 pos, vec3 dir) {
	float dist = 0.0;
	float dscene;

	for (int i = 0; i < RM_ITERS; i++) {
		dscene = scene(pos + dist * dir);
		if (abs(dscene) < 0.1)
			break;
		dist += RM_FACTOR * dscene;
	}

	return dist;
}

