    i = p.x<0.||p.x>3.? -1:
    i==5? 972980223: i==4? 690407533: i==3? 704642687: i==2? 696556137:i==1? 972881535: -1;
 	return i<0 ? -1 : i/b-2*(i/b/2);
}
