// Usign directional derivatives for lighting is much faster than computing 
// gradients/normals and doing lighting with it (if the number of lights is
// less than four). Mostly useful for volumetric effects.
//
// See 
//
//  http://iquilezles.org/www/articles/derivative/derivative.htm
//
// and also:
//
//  https://www.shadertoy.com/view/XslGRr
//  https://www.shadertoy.com/view/Xd23zh]Xd23zh
//  https://www.shadertoy.com/view/MsfGzM
//
// In the left haf of the screen, directional derivatives. On the right, the
// traditional gradient-based lighting. Move the mouse to compare.

float noise(vec3 p) {

    return 0.3*cos(sin(5.0*p.x)+cos(10.0*p.y+cos(iTime))+0.5*sin(4.0*p.z+sin(iTime)))*sin(0.5*iTime); 
}

float map( vec3 p )
{
	float d1 = p.y - 0.0 + noise(cos(0.8*p)); // ground plane
    float d2 = length(p-vec3(0.0,0.0,0.0)) - 1.0 + noise(cos(p)); // ball1
    float d3 = length(p.xz-vec2(-3.0,0.0)) - 0.5 + noise(sin(2.0*p)); // cylinder
    float d4 = length(p-vec3(1.0,1.0,1.0)) - 1.0 + noise(p); // ball2
    return min( min(d1,d2), min(d3,d4) ); // Union all of them
}

// ray casting
float intersect( in vec3 ro, in vec3 rd, const float maxdist )
{
    float res = -1.0;
    float t = 0.1;
    for( int i=0; i<128; i++ )
    {
	    float h = map(ro + t*rd);
        res = t;
        if( h<(0.0001*t) || t>maxdist ) break;
        t += h;
    }
	return res;
}

// calculate normal
vec3 calcNormal( in vec3 pos, in float eps )
{
    // so weird formula.... see the one below for more clear one
    vec2 e = vec2(1.0,-1.0)*0.5773*eps;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

vec3 calcGradient(vec3 pos) {
    // the distance field F(p) = 0 defines the isosurface
    // according to the definition of gradient DF_p = normal
	vec3 eps = vec3( 0.001, 0.0, 0.0 ); // small perturbation
	vec3 grad = vec3(
	    map(pos+eps.xyy) - map(pos-eps.xyy), // [D(x+dx,y,z)-D(x,y,z)]/dx 
	    map(pos+eps.yxy) - map(pos-eps.yxy), // [D(x,y+dy,z)-D(x,y,z)]/dy
	    map(pos+eps.yyx) - map(pos-eps.yyx) ); //[D(x,y,z+dz)-D(x,y,z)]/dz

    return grad/(2.0*eps.x);
}

vec3 calcNormal( in vec3 pos )
{   
	return normalize(calcGradient(pos));
}

vec3 render( in vec3 ro, in vec3 rd, in float doAB )
{
    vec3 col = vec3(0.0);

    const float maxdist = 32.0;
    float t = intersect( ro, rd, maxdist );

    if( t < maxdist )
    {
        float eps = 0.001;
        vec3  pos = ro + t*rd;

        vec3 lig = normalize( vec3(1.0,1.0,1.0) ); // this is a vector point from position to light source
        float dif = 0.0;

        // directional derivative
        if( doAB>0.0 )
        {
            dif = (map(pos+lig*eps) - map(pos)) / eps;
            float s = length(calcGradient(pos));
            dif = dif*(1.0/s);
        }
        // gradient based lighting
		else
        {
            vec3 nor = calcNormal( pos);
            dif = dot(nor,lig);
        }

        dif = clamp( dif, 0.0, 1.0 ); // ignore negative things which means in shadow

        col = vec3(1.0,0.8,0.3)*dif + vec3(0.1,0.15,0.3); // diffision light color + ambient, shadow is not completly black but the opposite of light

        col *= exp( -0.1*t ); // simulate light intensity decay
    }

    return pow( col, vec3(0.45) ); // gamma correction, why this formular....
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 p = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y; // uv space and preserve aspect ratio

    // camera configuration
	vec3  ro = vec3(0.0,1.0,7.0); // ray origin, camera center
	vec3  ta = vec3(0.0,1.0,0.0); // camera direction, camera aim at
    vec3  ww = normalize( ta - ro); // z direction
    vec3  uu = normalize( cross( ww,vec3(0.0,1.0,0.0 ) )); // front X up is right 
    vec3  vv = normalize( cross(uu,ww) ); // right x front = UP
    vec3  rd = normalize( p.x*uu + p.y*vv + 3.0*ww ); // ray direction take focal len into consideration

    float im = iMouse.x; if( iMouse.z<=0.001 ) im = iResolution.x/2.0; // set mouse to center when there is no clicking
    float dm = im - fragCoord.x; // for each pixel calculate its signed distance to mouse position

    vec3 col = render( ro, rd, dm ); // render scene

	col = mix( vec3(0.0), col, smoothstep( 1.0, 2.0, abs(dm) ) ); // draw splitting line

	fragColor = vec4( col, 1.0 );
}

