// atan2 en lerp:
// http://http.developer.nvidia.com/Cg/index_stdlib.html

// colors
// https://color.adobe.com/nl/Mijn-Kuler-thema-color-theme-4149936/?showPublished=true

#define PI 3.14159265359

#define twoThirdPI 3.14159265359/3.0*2.0
#define twelfthPI 3.14159265359/12.0
vec3 lightPos = vec3(0.5,0.5,0.2);

#define bars 10.0				// How many buckets to divide spectrum into
#define barSize 0.2 / bars		// Constant to avoid division in main loop
#define barGap 0.1 * barSize	// 0.1 represents gap on both sides, so a bar is
#define sampleSize 0.1			// How accurately to sample spectrum, must be a factor of 1.0

#define circleRadius 0.1;

#define c1 vec4(0.24)				// grey
#define c2 vec4(.71,.32,.36,1.0)	// red
#define c3 vec4(.91,.64, .0,1.0)	// yello
#define c4 vec4(.27,.48,.44,1.0)	// green
#define c5 vec4(.21,.40,.51,1.0)	// blue


#define colorOnCenter c1*1.5
#define colorOff (c5*4.0)
#define colorOn (c1*10.0)


float lerp(float a, float b, float t)
{
  return a + t*(b-a);
}

vec4 lerp(vec4 a, vec4 b, float t)
{
  return a + t*(b-a);
}

float atan2(float y, float x)
{
  float t0, t1, t2, t3, t4;

  t3 = abs(x);
  t1 = abs(y);
  t0 = max(t3, t1);
  t1 = min(t3, t1);
  t3 = float(1) / t0;
  t3 = t1 * t3;

  t4 = t3 * t3;
  t0 =         - float(0.013480470);
  t0 = t0 * t4 + float(0.057477314);
  t0 = t0 * t4 - float(0.121239071);
  t0 = t0 * t4 + float(0.195635925);
  t0 = t0 * t4 - float(0.332994597);
  t0 = t0 * t4 + float(0.999995630);
  t3 = t0 * t3;

  t3 = (abs(y) > abs(x)) ? float(1.570796327) - t3 : t3;
  t3 = (x < 0.0) ?  float(3.141592654) - t3 : t3;
  t3 = (y < 0.0) ? -t3 : t3;

  return t3;
}

bool drawCircle(vec2 circlePos, vec2 pixelPos, float radius)
{
    float deltaX = circlePos.x-pixelPos.x;
    float deltaY = circlePos.y-pixelPos.y;
    return(sqrt((deltaX*deltaX)+(deltaY*deltaY))<radius);
}

vec3 circleNormal(vec2 circlePos, vec2 pixelPos, float radius)
{
    float deltaX = circlePos.x-pixelPos.x;
    float deltaY = circlePos.y-pixelPos.y;
    float dist = sqrt((deltaX*deltaX)+(deltaY*deltaY));
    float dist2 = (((1.0*(radius*2.0))-dist)*(1.0/(radius*2.0)));
    vec2 dir = vec2(deltaX,deltaY);

    float normalY = (-deltaY*4.0);
    float normalX = (-deltaX*4.0);
    return vec3(normalX,normalY,dist2); 
}

vec2 circleMusicMap(vec2 circlePos, vec2 pixelPos, float radius){
	vec2 musicMap;
    // create music map red (angle center)
    float deltaX = pixelPos.x-circlePos.x;
    float deltaY = pixelPos.y-circlePos.y;
    float angleFromCenter = atan2(deltaX,deltaY);  
    musicMap.r = 1.0-(angleFromCenter+PI)/(PI*2.0);

    // create music map blue (dist center)
    float distFromCenter = sqrt((deltaY*deltaY)+(deltaX*deltaX));
    float circleFromCenter = (distFromCenter*3.0);
    circleFromCenter = circleFromCenter-radius;
    musicMap.g = circleFromCenter;   
    return musicMap;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 aspactRatio = vec2((iResolution.x/iResolution.y),1.0);
	vec2 uv = fragCoord.xy / (iResolution.xy/aspactRatio);
    vec2 center = aspactRatio*0.5;
	vec4 color = vec4(uv,0.5+0.5*sin(iTime),1.0);

    // light position
    lightPos.xy = (vec2(sin(iTime*2.0),-cos(iTime*2.0))/1.5)+center;
	if( iMouse.z > 0.0 )
	{
		lightPos = vec3((iMouse.xy / (iResolution.xy/aspactRatio)), lightPos.z);
	}

    bool filled = false;

    vec3 musicMap;
    for (float i = 0.0;i < 2.6;i+=0.5){
        for (float j = 1.0;j < 5.0;j+=1.0){
            float size = (0.01+(j*0.04));
            float fromCenter = 0.05+(0.01*j)+(size*j);
            vec2 circlePos = vec2(sin((-iTime/3.0)+(i*twoThirdPI)+(j*twelfthPI)),cos((-iTime/3.0)+(i*twoThirdPI)+(j*twelfthPI)));
            circlePos = (circlePos*fromCenter)+center;
            if(drawCircle(circlePos,uv,size)){

                // normal
                vec3 circelNormal = circleNormal(circlePos,uv,size);

                vec3 lightDir = normalize(lightPos-vec3(uv.x,uv.y,0.0));
                float lightDot = dot(lightDir,circelNormal);
                float lightPower = clamp( lightDot,0.0,1.0);
                // draw circle
                vec4 diffuse = vec4(lightPower);


				float fViewHeight = 2.0;
				vec3 vSurfacePos = vec3(uv, 0.0);

				vec3 vViewPos = vec3(0.5, 0.5, fViewHeight);
				vec3 vDirToView = normalize( vViewPos - vSurfacePos );
                vec3 vHalf = normalize( vDirToView + lightDir );
                float fNDotH = clamp( dot(circelNormal, vHalf), 0.0, 1.0);
                float specular = pow(fNDotH, 10.0) * lightDot * 0.5;

                // draw normalmaps
                //fragColor = vec4(circelNormal.xyz,0) * 0.5 + 0.5; 
                // draw music maps
                musicMap.xz = circleMusicMap(circlePos,uv,size);
                //fragColor = vec4(musicMap.x,0.0,musicMap.z,1.0);
                filled = true;

                // music visualizer

                ////old
                if(musicMap.b < 0.0){
                    fragColor = colorOnCenter*diffuse+specular;
                }else{

                    // use music map red
                    float musicChannelnput = texture( iChannel0, vec2( musicMap.r,0.0)).r;

                    // Get the starting x for this bar by rounding down
                    float barStart = floor(musicMap.r * bars) / bars;

                    // Sample spectrum in bar area, keep cumulative total
                    float intensity = 0.0;
                    for(float s = 0.0; s < barSize; s += barSize * sampleSize) {
                        intensity += texture(iChannel0, vec2(barStart + s, 0.0)).r;
                    }
                    fragColor = vec4(intensity/10.0);
                    intensity *= sampleSize*size;
                    //if(musicMap.r - barStart < barGap || musicMap.r > barStart + barSize - barGap) {
                        //intensity = 0.0;
                    //}
                    //fragColor = vec4(intensity);
                    // use music map blue
                    float onOff = musicMap.b-(intensity*3.0);
                    onOff = clamp(onOff,-0.5,0.5); 
                    onOff = floor(onOff+1.0);
                    fragColor = lerp(colorOn, (colorOff*color),onOff)*diffuse+(specular*50.0);
                    //fragColor = vec4(musicChannelnput/1.0);
                }
            }
        }
    }




    if(!filled){
       fragColor = vec4(0.20);  
    }else{
        //fragColor = vec4(musicMap.xyz,0.0);
    }
    //fragColor = vec4(musicMap.xyz,0.0);
}

