void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        float t = iTime+5.;

        // vars
        float z = 1.;

        const int n = 200; // particle count

        vec3 startColor = normalize(vec3(1,.1,0));
        vec3 endColor = normalize(vec3(1,sin(7.)*.5+.5,cos(7.)*.5+.5));

        float startRadius = .38;
        float endRadius = .42;

        float power = 1.;
        float duration = 0.8;

        vec2 
        	s = iResolution.xy,
        	v = z*(2.*gl_FragCoord.xy-s)/s.y;

        // Mouse axis y => zoom
        if(iMouse.z>0.) v *= iMouse.y/s.y * 20.;

        // Mouse axis x => duration
        if(iMouse.z>0.) duration = iMouse.x/s.x * 10.;

        vec3 col = vec3(0.);

        vec2 pm = v.yx*2.8;

        float dMax = duration;

        float mb = 0.;
        float mbRadius = 0.;
        float sum = 0.;
        for(int i=0;i<n;i++)
        {
                float d = fract(t*power+48934.4238*sin(float(i)*692.7398))*duration;
                float a = 6.28*float(i)/float(n);

                float x = d*cos(a);
                float y = d*sin(a);

                float distRatio = d/dMax;

                mbRadius = mix(startRadius, endRadius, distRatio); 

                vec2 p = v - vec2(x,y);

                //p = mod(p,pm) - 0.5*pm;

                mb = mbRadius/dot(p,p);

                sum += mb;

                col = mix(col, mix(startColor, endColor, distRatio), mb/sum);
        }

        sum /= float(n);

        sum = clamp(sum, 7.6, 1000.);

        col = normalize(col) * sum;

        col = smoothstep(vec3(2.7), vec3(2.85), col);

        fragColor.rgb = col;
}

#define time iTime*0.25
#define PI 3.14159265359

mat3 rot(vec3 ang)
{
    vec3 s = sin(ang);
    vec3 c = cos(ang);
	mat3 x = mat3(1.0,0.0,0.0,0.0,c.x,-s.x,0.0,s.x,c.x);
	mat3 y = mat3(c.y,0.0,s.y,0.0,1.0,0.0,-s.y,0.0,c.y);
	mat3 z = mat3(c.z,-s.z,0.0,s.z,c.z,0.0,0.0,0.0,1.0);
	return x*y*z;
}

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,126.7235))) * 43758.5453);
}

float worley3D(vec3 p)
{					 					 
	float r = 3.0;
    vec3 f = floor(p);
    vec3 x = fract(p);
	for(int i = -1; i<=1; i++)
	{
		for(int j = -1; j<=1; j++)
		{
			for(int k = -1; k<=1; k++)
			{
                vec3 q = vec3(float(i),float(j),float(k));
    			vec3 v = q + vec3(noise3D((q+f)*1.11), noise3D((q+f)*1.14), noise3D((q+f)*1.17)) - x;
    			float d = dot(v, v);
				r = min(r, d);
			}
		}
	}
    return sqrt(r);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 p = uv*2.0-1.0;
	p.x*=(iResolution.x/iResolution.y);
	vec3 col = vec3(0.1,0.2,0.3);
    vec3 ro = vec3(0.0,0.0,-2.0);
    vec3 rd = normalize(vec3(p,0.0)-ro);
    float mx = iMouse.x > 0.0 ? iMouse.x/iResolution.x : 0.5;
    float my = iMouse.y > 0.0 ? iMouse.y/iResolution.y : 0.5;
    vec3 ang = vec3((my-0.5)*PI, (mx-0.5)*PI, 0.0);
    ro*=rot(ang);
    rd*=rot(ang);
    vec3 rp = ro;
    float t = 1.0;
    float w;
    float ww;
    vec3 add;
    for(int i = 0; i<12; i++)
   	{       
        rp+= rd*t;
        w = worley3D(rp+vec3(0.0,0.0,time));
        ww = w*w*w*w*w*(2.0/distance(rp,ro));
        add = vec3(0.85,0.95,1.0);
        col = mix(col, add, ww);
    }

    rp = ro + rd*(12.0*(time-floor(time))+0.5);
    w = worley3D(rp+vec3(0.0,0.0,time));
    ww = w*w*w*w*w*w*(4.0/distance(rp,ro));
    add = vec3(0.12,0.35,0.5);
    col = mix(col, add , ww);

    rp = ro + rd*(8.0);
    w = worley3D(rp+vec3(0.0,0.0,time));
    ww = w*w*w*w*w*w*(2.0/distance(rp,ro));
    add = vec3(1.0,0.65,0.0);
    col = mix(col, add , ww);

    rp = ro + rd*(12.0);
    w = worley3D(rp+vec3(0.0,0.0,time));
    ww = w*w*w*w*w*w*(2.0/distance(rp,ro));
    add = vec3(1.0,0.81,0.0);
    col = mix(col, add , ww);

	fragColor = vec4(col*rot(ang*.05),1.0);
}

//noise function taken from https://www.shadertoy.com/view/XslGRr

float hash( float n ){
	return fract(sin(n)*43758.5453);
}

//this noise function was originally 3D noise, 
//but I am just setting z to 0 for the sake of simplicity here
//also cause most effects only care about 2D noise
float noise( vec2 uv ){
	vec3 x = vec3(uv, 0);

	vec3 p = floor(x);
	vec3 f = fract(x);

	f       = f*f*(3.0-2.0*f);
	float n = p.x + p.y*57.0 + 113.0*p.z;

	return mix(mix(mix( hash(n+0.0), hash(n+1.0),f.x),
				   mix( hash(n+57.0), hash(n+58.0),f.x),f.y),
			   mix(mix( hash(n+113.0), hash(n+114.0),f.x),
				   mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
}

float ring( float angle, float dist, float ANG, float THICKNESS, float POS, float SIZE ) {
    //angles between 4 and 15 are good
    //negative thickness makes it black, values around 0.02 are good

    float ZIGZAG = abs( mod( angle, ANG ) - ANG * 0.5 ) * SIZE;
    return ceil( dist - POS + ZIGZAG) - ceil( dist - (POS+THICKNESS) + ZIGZAG);   
}
float burst( float angle, float dist, float ANG ) {
    float B = abs( mod( angle, ANG ) - ANG * 0.5 );
    return B;
}
float lim( float IN, float amount ) {
	return IN * amount + (1.0 - amount);   
}
float inv( float IN ) {
 	return 1.0 - IN;   
}
float ppp( float IN ) {
 	return IN * IN * IN;   
}
float dots( float angle, float dist, float ANG, float POS ) {
    return ppp(7.5*burst( angle, dist, ANG )/ANG) * ppp(inv(ANG*1.5*distance( dist, POS )));
}

