vec4 myoverlay( vec2 fragCoord )
{ 
	float shortside = min( iResolution.x, iResolution.y );
    float longside = max( iResolution.x, iResolution.y );

	vec2 uv = fragCoord.xy / vec2( shortside, shortside );

    uv.x = uv.x - (longside/shortside - 1.0) * 0.5;

   	float dist = distance( vec2( 0.5, 0.5 ), uv );

    uv = vec2( uv.x - 0.5, uv.y - 0.5 );

    float angle = degrees( atan( uv.y / uv.x ) );

    float TIMESCALE = 0.3;
    float T = iTime * TIMESCALE * 2.0;

    float ar = noise( vec2( angle / 5.0, 1.0 ) );
    float ar2 = noise ( vec2( mod ( angle, 30.0 ) / 5.0, 1.0 ) );

    vec3 DOTS1 = max(ceil(dots( angle, dist + ar * ar2 * sin( T ) * 0.35, 10.0, 0.5) - 40.0 ), 0.0) * vec3(1.0,1.0,1.0);
    vec3 DOTS2 = max(ceil(dots( angle + 5.0, dist + ar * ar2 * sin( (T+uv.x) * 0.5 ) * 1.5 + 0.2, 10.0, 0.5) - 40.0 ), 0.0) * vec3(1.0,1.0,1.0);
    vec3 DOTS3 = max(ceil(dots( angle + 5.0, dist + ar * ar2 * cos( (T+uv.y) * 0.5 ) * 1.0 + 0.1, 5.0, 0.5) - 45.0 ), 0.0) * vec3(1.0,1.0,1.0);

    return vec4( DOTS1 + DOTS2 + DOTS3, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float shortside = min( iResolution.x, iResolution.y );
    float longside = max( iResolution.x, iResolution.y );

	vec2 uv = fragCoord.xy / vec2( shortside, shortside );

    uv.x = uv.x - (longside/shortside - 1.0) * 0.5;

   	float dist = distance( vec2( 0.5, 0.5 ), uv );
	vec3 c = myoutput(fragCoord.xy).rgb;
	if (fragCoord.x < iMouse.x)
	{
		fragColor = vec4(c, 1.0);	
	} else {

		//declare stuff
		const int mSize = 11;
		const int kSize = (mSize-1)/2;
		float kernel[mSize];
		vec3 final_colour = vec3(0.0);

		//create the 1-D kernel
		float sigma = 0.75 + dist * 5.0;
		float Z = 0.0;
		for (int j = 0; j <= kSize; ++j)
		{
			kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
		}

		//get the normalization factor (as the gaussian has been clamped)
		for (int j = 0; j < mSize; ++j)
		{
			Z += kernel[j];
		}

		//read out the texels
		for (int i=-kSize; i <= kSize; ++i)
		{
			for (int j=-kSize; j <= kSize; ++j)
			{
				final_colour += kernel[kSize+j]*kernel[kSize+i]*myoutput((fragCoord.xy+vec2(float(i),float(j)))).rgb;

			}
		}


		fragColor = vec4(final_colour/(Z*Z), 1.0);
        fragColor.rgb += myoverlay( fragCoord.xy ).rgb;
	}
}

// using the base ray-marcher of Trisomie21: https://www.shadertoy.com/view/4tfGRB#

#define T iTime
#define r(v,t) { float a = (t)*T, c=cos(a),s=sin(a); v*=mat2(c,s,-s,c); }
#define SQRT3_2  1.26

void mainImage( out vec4 f, vec2 w ) {
    vec4 p = vec4(w,0,1)/iResolution.yyxy-.5, d,c; p.x-=.4; // init ray 
    r(p.xz,.13); r(p.yz,.2); r(p.xy,.1);   // camera rotations
    d = p;                                 // ray dir = ray0-vec3(0)
    p.z += 5. *T;
    f = vec4(0);
    float closest = 999.0; vec4 u_c=f;

    for (float i=1.; i>0.; i-=.01)  
    {
        vec4 u = floor(p/8.), t = mod(p, 8.)-4., ta; // objects id + local frame
        // r(t.xy,u.x); r(t.xz,u.y); r(t.yz,1.);     // objects rotations
        u = sin(78.*(u+u.yzxw));                     // randomize ids
        // t -= u;                                   // jitter positions
        c = p/p*1.2;



        float x1,x2,x=1e9;
        for (float j=2.3; j>1.; j-= .3) {
            r(t.xy,u.x); r(t.xz,u.y); r(t.yz,u.z);
            ta = abs(t);
            x1 = length(t.xyz) -j*SQRT3_2;       // inside carving sphere
            x2 = max(ta.x,max(ta.y,ta.z)) -j;    // cube
            x2 = max(-x1,x2);                    // cube-sphere
            x1 = length(t.xyz) -j*SQRT3_2-.1;    // outside carving sphere
            x2 = max(x1,x2);                     // shape inter sphere
            x = min(x,x2);                       // union with the others
        }

        x1 = length(t.xyz) -.6;                  // central spheres
        if (x1 < closest) { closest = x1; u_c = u; } 
        x = min(x1,x);
        if (x==x1)  c  = u*3.; 
        //   else   c += u*3.*pow(abs((x-x1)),.2)*(.5+.5*sin(.5*T));  // thanks squid !

        // f = i*i*c;      // color texture + black fog 

        if(x<.01) // hit !
            { f = i*i*c; break;  }  // color texture + black fog 
        p -= d*x;           // march ray
     }
     if (cos(.25*T)>0.) f += u_c * exp(-closest)*(.5+.5*cos(.5*T)); // thanks kuvkar ! 
}

float radius = 100.8;

vec3 trans(vec3 p)
{
  return mod(p, 8.0)-4.0;
}

float lengthN(vec3 v, float n)
{
  vec3 tmp = pow(abs(v), vec3(n));
  return pow(tmp.x+tmp.y+tmp.z, 1.0/n);
}

float distanceFunction(vec3 pos)
{
  return length(trans(pos)) - 1.0;
}


vec3 getNormal(vec3 p) {
    const float d = 0.0001;
    return
        normalize
        (
            vec3
            (
                distanceFunction(p + vec3(d, 0., 0.)) - distanceFunction(p + vec3(-d, 0., 0.)),
                distanceFunction(p + vec3(0., d, 0.)) - distanceFunction(p + vec3(0., -d, 0.)),
                distanceFunction(p + vec3(0., 0., d)) - distanceFunction(p + vec3(0., 0., -d))
            )
         );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;    
    //vec3 camPos = vec3(cos(iTime * 0.01) * radius, 0.0, sin(iTime * 0.01) * radius);
    //vec3 camPos = vec3(0., 0., smoothstep(uv.x, uv.y, cos(iTime * 10.1)));
	vec3 camPos = vec3(10., 10., smoothstep(0.01, uv.x, sin(iTime * 1.1)));

    //vec3 camDir = vec3(0.0, 0.0, -1.0);
    vec3 camDir = vec3(cos(iTime * 0.1) * 30., sin(iTime * 0.1) * 10., abs(sin(iTime * 0.1) * 10.));
    //vec3 camUp = vec3(0.0, 1.0, 0.0);
    vec3 camUp = vec3(0.0, 1.0, 0.0);
    //vec3 camUp = vec3( pow(cos(iTime * 0.01) , 112.0), cos(iTime) * 10., sin(iTime) * 10.);
    vec3 camSide = cross(camDir, camUp);
    float focus = 1.8 + dot(uv.x, uv.y) * dot(uv.x, uv.y);

    vec3 rayDir = normalize(camSide * uv.x + camUp * uv.y + camDir * focus);

    float t = 0.0, d;
    vec3 posOnRay = camPos;

    for(int i = 0; i < 50; ++i) {
        d = distanceFunction(posOnRay);
        t += d;
        posOnRay = camPos + t * rayDir;
    }

    vec3 normal = getNormal(posOnRay);
    if(abs(d) < 0.01) {
        fragColor = vec4(normal, 1.0);
    } else {
        fragColor = vec4(0.0);
    }
 }

#define LIMIT 800

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float cam(float f) {
    float tt = mod(f, 25.0) * 4.0;
	float go = smoothstep(10.0, 45.0, tt) - smoothstep(60.0, 95.0, tt);
    return go * 100000.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float t = iTime;
    float x = floor(fragCoord.x) + floor(cam(t));
    float y = floor(fragCoord.y);
    //float wtf = 2.0 * floor(mod(t*10.0, x*tan(t/y))) + 1.0;
    //float wtf = 2.0 * floor(mod(t*2.0, 10.0)) + 1.0;
    float wtf = 1.0;
    float c = 0.0;
    //float maxx = 0.0;
    float bgOff = 1.0;
    //x = x * x;
    for (int k = 0; k < LIMIT; k++) {
        c++;
        if (x <= 1.0) {
            c = 0.0;
            //maxx = 0.0;
            bgOff = 0.0;
        	break;   
        }
        if (c >= y / (0.0025 * iResolution.y)) {
        	break;   
        }
        if (mod(x,  2.0) == 0.0) {
            x = x / 2.0;
        } else {
            x = x * 3.0 + wtf;
            if (x <= wtf * 4.0) {
            	x = 1.0;   
            }
        }
        //maxx = max(maxx, x);
    }
    float bgOn = (1.0 - bgOff);
    float bg1 = sin(t)*bgOn*0.1;
    float bg2 = sin(t + (3.14159) / 2.0)*bgOn*0.1;
    fragColor = vec4(hsv2rgb(vec3(mod(x/10.0, 360.0) / 360.0, 1.0, 0.5 * bgOff)), 1.0) + vec4(bg1, bg2, (0.1-bg1-bg2)*bgOn, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = fragCoord.xy / iResolution.xy;
