float _max( float a, float b )
{
	return a < b ? b : a;   
}
float _pow2( float x )
{
	return x*x;   
}

float _dot( vec3 a, vec3 b )
{
 	return a.x*b.x + a.y*b.y + a.z*b.z;   
}

vec3 _normalize( vec3 a )
{
	return a / sqrt( _dot(a,a) ); 
}


//Implicit sphere equation for inside/outside test
bool insideCircle( vec2 coord, vec2 center, float radius )
{
    float phi = _pow2(coord.x - center.x) + _pow2(coord.y - center.y) - _pow2(radius);
    return (phi < 0.0) ? true : false;
}

//Sphere Z-coord returned by solving sphere equation
float findSphereZ( vec2 coord, vec2 center, float radius ) 
{
    //Assume sphere center is at z = 0
	return sqrt( _pow2(radius) - _pow2(coord.x - center.x) - _pow2(coord.y - center.y) );   
}

//Sphere normal finder
vec3 getSphereNormal( vec3 coord, vec2 center ) 
{
    //Assume sphere center is at z = 0 - bad practice to do this though
    vec3 normVec = vec3( coord.x - center.x, coord.y - center.y, coord.z );
    return _normalize(normVec);
}

vec2 getCenter( float loopTime, float radius )
{
    vec2 p0 = vec2( iResolution.x / 2.0, radius );
    vec2 p1 = vec2( radius, iResolution.y / 2.0 );
    vec2 p2 = vec2( iResolution.x / 2.0, iResolution.y - radius );
    vec2 p3 = vec2( iResolution.x - radius, iResolution.y / 2.0 );

    float rem = mod(iTime,loopTime);
    float frac = rem / loopTime;

    //Scripted motion - compiler wouldn't let me embed ternary operators
    vec2 center = (frac < 0.25 ) ? (p0 + frac*4.0*(p1-p0)) : vec2(0.0);
    center = (frac > 0.25 && frac < 0.5 ) ? (p1 + (frac-0.25)*4.0*(p2-p1)) : center;
    center = (frac > 0.5 && frac < 0.75 ) ? (p2 + (frac-0.5)*4.0*(p3-p2)) : center;
    center = (frac > 0.75 && frac <= 1.0 ) ? (p3 + (frac-0.75)*4.0*(p0-p3)) : center;

 	return center;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float radius = iResolution.x/5.0;
    vec2 center = getCenter( 10.0, radius);

    //Camera position
    vec3 cam = vec3( iResolution.x/2.0,iResolution.y/2.0, -radius*2.0 );

    //Light source vector
    vec3 L1Pos = vec3( iResolution.x/2.0,iResolution.y/2.0, -radius*3.0 );
    vec3 L1Color = vec3(0.5, 0.25, 0.5);

    vec3 L2Pos = vec3( iResolution.x,iResolution.y, -radius  );
    vec3 L2Color = vec3(0.0, 0.5, 1.0);

    //Draw sphere if coord inside 2-D circle projection
    if( insideCircle( fragCoord, center, radius ) )
    {        

        float z = findSphereZ( fragCoord, center, radius );
    	vec3 surfaceCoord = vec3( fragCoord.x, fragCoord.y, -z );
        vec3 N = getSphereNormal( surfaceCoord, center );

        vec3 L1 = _normalize( L1Pos - surfaceCoord );

        vec3 diffuseColor = L1Color * _dot( N, L1 );                  

        vec3 V = _normalize( cam - surfaceCoord );

        vec3 L2 = _normalize( L2Pos - surfaceCoord );

        vec3 R2 = 2.0 * _dot( N, L2 ) * N - L2;

        vec3 specularColor =L2Color * pow(_max(0.0,_dot(R2,V)),8.0);

        fragColor = vec4( diffuseColor + specularColor, 1.0 );
    }
    //Draw background colour otherwise
    else fragColor = vec4(.45,.65,0.2,1.0);

}

// "Guilin" by dr2 - 2015
