void mainImage( out vec4 f, in vec2 g )
{
	float 
        z = 4.,
        t = iTime,
        t0 = sin(t)*.5+.8,
        t1 = sin(t*.2)*.5+.8,
        r = 0.,
        k = 11.*.02
        ;

    vec2 
        s = iResolution.xy,
        u = vec2(t1, -.8*.5/t1),
        d = s-s,
        v = z * (2.*g-s)/s.y * mat2(0,1,-1,0)
        ;

   	vec3 
        c = d.xxx,
        a = c
        ;

    //vec4 
    //    m = iMouse;

   	//if (m.z >0.) u = m.xy/s; 

    for(float i=0.;i<1.;i+=.02) //1./50.
    {
        if (i > k ) break;

        if (dot(d,d) > 1e12) 
        {       
        	r = i/k;

            a.y = fract(r);

            // based iq palette formula  => https://www.shadertoy.com/view/ll2GD3
            c += ( t0 + a*cos( 6.*(t0*t0+a) ) )*r;

            break;
        }

        d = v + vec2(d.x * d.x - d.y * d.y, d.x * d.y) * u;
    }
    f.rgb = c;
}

const float scale = 5.; 

#define PI 3.14159

// --- noise functions from https://www.shadertoy.com/view/XslGRr
// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

float hash( float n ) {
    return fract(sin(n)*43758.5453);
}

// --- End of: Created by inigo quilez --------------------

// more 2D noise
vec2 hash12( float n ) {
    return fract(sin(n+vec2(1.,12.345))*43758.5453);
}
float hash21( vec2 n ) {
    return hash(n.x+10.*n.y);
}
vec2 hash22( vec2 n ) {
    return hash12(n.x+10.*n.y);
}
float cell;   // id of closest cell
vec2  center; // center of closest cell
float dist;   // diss to closest cell

vec3 worley( vec2 p ) {
    vec3 d = vec3(1e15);
    vec2 ip = floor(p);
    for (float i=-2.; i<3.; i++)
   	 	for (float j=-2.; j<3.; j++) {
                vec2 p0 = ip+vec2(i,j);
            	float a0 = hash21(p0), a=5.*a0*iTime+2.*PI*a0; vec2 dp=vec2(cos(a),sin(a)); 
                vec2  c = hash22(p0)*.5+.5*dp+p0-p;
                float d0 = dot(c,c);
                if      (d0<d.x) { d.yz=d.xy; d.x=d0; cell=hash21(p0); center=c+p;}
                else if (d0<d.y) { d.z =d.y ; d.y=d0; }
                else if (d0<d.z) {            d.z=d0; }  
            }
	dist = d.x;
    return sqrt(d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 2.*(fragCoord.xy / iResolution.y -vec2(.9,.5));


	vec3 w = scale*worley(scale*p);
 	float d21 = w.y-w.x, d32=w.z-w.y, d31=w.z-w.x;
    vec3 col = 1.-clamp(vec3(d21,d32,d31),0.,1.); col = vec3((1.-col.b)*col.rg,col.b);
    //col = 1.-smoothstep(0.,.5,col);
    float seed = smoothstep(0.003,.0, dist); col = seed+(1.-seed)*col;

   fragColor = vec4(col,1.);
}

// by Nikos Papadopoulos, 4rknova / 2015
