#define ANIMATED
#define SHOW_CONTROL_POINTS
//#define SHOW_SEGMENT_POINTS
//#define MOUSE_ENABLED 
//#define AA 1.

#define STEPS  15.
#define STROKE .8

#define EPS    .01

#define BG   vec3(0.0745, 0.0862, 0.1058)
#define COL0 vec3(.2, .35, .55)
#define COL1 vec3(.9, .43, .34)
#define COL3 vec3(1.)

vec2 interpolate(vec2 a, vec2 b, vec2 c, vec2 d, float p)
{
    // CatmullRoms are cardinals with a tension of 0.5
	vec2 P = -a + (3. * (b - c)) + d;
    vec2 Q = (2. * a) - (5. * b) + (4. * c) - d;
    vec2 R = c - a;
    vec2 S = 2. * b;

	float p2 = p * p;
    float p3 = p * p2;

    return .5 * ((P * p3) + (Q * p2) + (R * p) + S);
}

float df_circ(in vec2 p, in vec2 c, in float r)
{
    return abs(r - length(p - c));
}

float df_line(in vec2 p, in vec2 a, in vec2 b)
{
    vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0., 1.);	
	return length(pa - ba * h);
}

float sharpen(in float d, in float w)
{
    float e = 1. / min(iResolution.y , iResolution.x);
    return 1. - smoothstep(-e, e, d - w);
}

float ip_control(vec2 uv, vec2 a, vec2 b, vec2 c, vec2 d)
{    
    float cp = 0.;

#ifdef SHOW_CONTROL_POINTS    
    float c0 = sharpen(df_circ(uv, a, .02), EPS * .75);
    float c1 = sharpen(df_circ(uv, b, .02), EPS * .75);
    float c2 = sharpen(df_circ(uv, c, .02), EPS * .75);
    float c3 = sharpen(df_circ(uv, d, .02), EPS * .75);

    float l0 = sharpen(df_line(uv, a, b), EPS * .6);
    float l1 = sharpen(df_line(uv, b, c), EPS * .6);
    float l2 = sharpen(df_line(uv, c, d), EPS * .6);

    cp = max(max(max(c0, c1),max(c2, c3)),
	         max(max(l0, l1),l2));
#endif

    return cp;
}

float ip_point(vec2 uv, vec2 a, vec2 b, vec2 c, vec2 d)
{
    vec2 p = interpolate(a, b, c, d, mod(iTime * 2., 10.) / 10.);
    return sharpen(df_circ(uv, p, .025), EPS * 1.);
}

float ip_curve(vec2 uv, vec2 a, vec2 b, vec2 c, vec2 d)
{ 
    float e = 0.;
    for (float i = 0.; i < STEPS; ++i)
    {
        vec2  p0 = interpolate(a, b, c, d, (i   ) / STEPS);
        vec2  p1 = interpolate(a, b, c, d, (i+1.) / STEPS);
#ifdef SHOW_SEGMENT_POINTS        
        float m = sharpen(df_circ(uv, p0, .01), EPS * .5);
        float n = sharpen(df_circ(uv, p1, .01), EPS * .5);
        e = max(e, max(m, n));
#endif
        float l = sharpen(df_line(uv, p0, p1), EPS * STROKE);
        e = max(e, l);
    }

    return e;
}

vec3 scene(in vec2 uv, in vec2 a, in vec2 b, in vec2 c, in vec2 d)
{
    float d0 = ip_control(uv, a, b, c, d);
    float point = 0.;

#ifdef ANIMATED
    point = ip_point(uv, a, b, c, d);
#endif

    float d1 = ip_curve(uv, a, b, c, d);    
    float rs = max(d0, d1);

    return (point < .5)
        ? rs * (d0 > d1 ? COL0 : COL1)
        : point * COL3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy * 2. - 1.);
    uv.x *= iResolution.x / iResolution.y;
    vec3 col = BG;

    vec2 a = vec2(-.75,-.8);
    vec2 b = vec2(-.50, .0);
    vec2 c = vec2( .50, .0);
    vec2 d = vec2( .75, .8);

#ifdef MOUSE_ENABLED        
    a = (iMouse.xy / iResolution.xy * 2. - 1.)
           * vec2(iResolution.x / iResolution.y, 1.);
#endif

#ifdef AA
    // Antialiasing via supersampling
    float e = 1. / min(iResolution.y , iResolution.x);    
    for (float i = -AA; i < AA; ++i) {
        for (float j = -AA; j < AA; ++j) {
    		col += scene(uv + vec2(i, j) * (e/AA), a, b, c, d) / (4.*AA*AA);
        }
    }
#else
    col += scene(uv, a, b, c, d);
#endif /* AA */

	fragColor = vec4(col, 1);
}

// Created by Pol Jeremias - poljere/2015
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define SOUND_MULTIPLIER 1.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);
    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    // Calculate polar coordinates
    float r = length(uv);
    float a = atan(uv.y, uv.x);

    // Draw the lines
    const float it = 5.0;
    float c = 0.0;
    for( float i = 0.0 ; i < it ; i += 1.0 )
    {
        float i01 = i / it;
        float rnd = texture( iChannel0, vec2(i01)).x;
        float react = SOUND_MULTIPLIER * texture( iChannel1, vec2(i01, 0.0) ).x;    

        float c1 = (uv.x + 1.1 + react) * 0.004 * abs( 1.0 / sin( (uv.y +0.25) +
                                                         sin(uv.x * 4.0 * rnd + rnd * 7.0 + iTime * 0.75) *
                                                                 (0.01 + 0.15*react)) );
        c = clamp(c + c1, 0.0, 1.0);
    }

    float s = 0.0;
    const float it2 = 20.0;
    for( float i = 0.0 ; i < it2 ; i += 1.0 )
    {
        float i01 = i / it2;       
        float react = SOUND_MULTIPLIER * texture( iChannel1, vec2(i01, 0.0) ).x;  
        vec2 rnd = texture( iChannel0, vec2(i01)).xy;
        vec2 rnd2 = rnd - 0.5;

        rnd2 = vec2(0.85*sin(rnd2.x * 200.0 + rnd2.y * iTime * 0.1), 
                    -0.1 - 0.15 * sin(rnd2.x * rnd2.x * 200.0 + iTime  * rnd2.x * 0.25));

        float r1 = 1.0 - length(uv - rnd2);
        float rad = ( 1.0 - clamp(0.03 * rnd.y + react * 0.05, 0.0, 1.0) );

        r1 = smoothstep(rad, rad + 0.015, r1);
        s += r1;
    }


    // Calculate the final color mixing lines and backgrounds
    vec3 bg = mix( vec3(0.93, 0.71, 0.62), vec3(0.9, 0.44, 0.44), r);
    bg = mix(bg, vec3(0.9, 0.91, 0.62), c);
    bg = mix(bg, vec3(0.9, 0.91, 0.82), s);

    fragColor = vec4(bg, 1.0);
}

/*

More info on b-splines and other curves here:
http://www.ibiblio.org/e-notes/Splines/Intro.htm

*/

#define A  0.25
#define B  (sin(iTime*1.5) * 0.5)
#define C  (iMouse.z <= 0.0 ? 0.25 : iMouse.y / iResolution.y - 0.5)
#define D  -0.1

#define EDGE   0.005
#define SMOOTH 0.0025

float N_i_1 (in float t, in float i)
{
    // return 1 if i < t < i+1, else return 0
    return step(i, t) * step(t,i+1.0);
}

float N_i_2 (in float t, in float i)
{
    return
        N_i_1(t, i)       * (t - i) +
        N_i_1(t, i + 1.0) * (i + 2.0 - t);
}

float SplineValue(in float t)
{
    return
        A * N_i_2(t, 0.0) +
        B * N_i_2(t, 1.0) +
        C * N_i_2(t, 2.0) +
        D * N_i_2(t, 3.0);
}

// F(x,y) = F(x) - y
float F ( in vec2 coords )
{
    // time in this curve goes from 0.0 to 5.0 but values
    // are only valid between 1.0 and 4.0
    float T = coords.x*3.0 + 1.0;    
    return SplineValue(T) - coords.y;
}

// signed distance function for F(x,y)
float SDF( in vec2 coords )
{
    float v = F(coords);
    float slope = dFdx(v) / dFdx(coords.x);
    return abs(v)/length(vec2(slope, -1.0));
}

// signed distance function for Circle, for control points
float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v/length(g); 
}

