void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 percent = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    percent.x *= aspectRatio;

    vec3 color = vec3(1.0,1.0,1.0);
    float dist = SDFCircle(percent, vec2(0.0,A));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    }

    dist = SDFCircle(percent, vec2(0.33,B));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    }    

    dist = SDFCircle(percent, vec2(0.66,C));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,1.0,0.0),vec3(1.0,1.0,1.0),dist);
    }    

    dist = SDFCircle(percent, vec2(1.0,D));
	if (dist < EDGE + SMOOTH)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0),dist);
    }      

    if (percent.x >= 0.0 && percent.x <= 1.0)
    {
    	dist = SDF(percent);
    	if (dist < EDGE + SMOOTH)
    	{
        	dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
        	color *= vec3(dist);
    	}
    }

	fragColor = vec4(color,1.0);
}

// Removing soft shadows improves FPS by a factor ~2.5, but it's less pretty
#define SHADOWS

void sphereFold(inout vec3 z, inout float dz)
{
	float r2 = dot(z,z);
	if (r2 < 0.5)
    { 
		float temp = 2.0;
		z *= temp;
		dz*= temp;
	}
    else if (r2 < 1.0)
    { 
		float temp = 1.0 / r2;
		z *= temp;
		dz*= temp;
	}
}

void boxFold(inout vec3 z, inout float dz)
{
	z = clamp(z, -1.0, 1.0) * 2.0 - z;
}

float mandelbox(vec3 z)
{
    float scale = 2.0;
	vec3 offset = z;
	float dr = 1.0;
	for (int n = 0; n < 10; n++)
    {
		boxFold(z,dr);
		sphereFold(z,dr);
        z = scale * z + offset;
        dr = dr * abs(scale) + 1.0;
	}
	float r = length(z);
	return r / abs(dr);
}

float plane(vec3 pos)
{
	return length(max(abs(pos)-vec3(12.0,0.5,12.0),0.0));
}

float scene(vec3 pos)
{
    return min(mandelbox(pos), plane(pos-vec3(0.0,-6.5,0.0)));
}

float raymarcher( in vec3 ro, in vec3 rd )
{
	const float maxd = 50.0;
	const float precis = 0.01;
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<100; i++ )
    {
        if( h<precis||t>maxd ) break;
	    h = scene( ro+rd*t );
        t += h * 1.0;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 normal( in vec3 pos )
{
    const float eps = 0.005;

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*scene( pos + v1*eps ) + 
					  v2*scene( pos + v2*eps ) + 
					  v3*scene( pos + v3*eps ) + 
					  v4*scene( pos + v4*eps ) );
}

float softray( in vec3 ro, in vec3 rd , in float hn)
{
    float res = 1.0;
    float t = 0.0005;
	float h = 1.0;
    for( int i=0; i<40; i++ )
    {
        h = scene(ro + rd*t);
        res = min( res, hn*h/t );
		t += clamp( h, 0.02, 2.0 );
    }
    return clamp(res,0.0,1.0);
}

float ambocc( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = scene( aopos );
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 light( in vec3 lightdir, in vec3 lightcol, in vec3 tex, in vec3 norm, in vec3 camdir )
{    
    float cosa = pow(0.5 + 0.5*dot(norm, -lightdir),2.0);
    float cosr = max(dot(-camdir, reflect(lightdir, norm)), -0.0);

    float diffuse = cosa;
    float phong = pow(cosr, 8.0);

    return lightcol * (tex * diffuse + phong);
}

vec3 background( in vec3 rd )
{
	return vec3(1.0+2.0*rd.y);
}

vec3 material( in vec3 pos , in vec3 camdir )
{    
	vec3 norm = normal(pos);

    vec3 d1 = -normalize(vec3(5.0,10.0,-20.0));
    vec3 d2 = -normalize(vec3(-5,10.0,20.0));
    vec3 d3 = -normalize(vec3(20,5.0,-5.0));
    vec3 d4 = -normalize(vec3(-20.0,5.0,5.0));

    vec3 tex = vec3(0.2);
    if (pos.y > -5.95) tex = vec3(0.32,0.28,0.0);

    #ifdef SHADOWS
    float sha = 0.7 * softray(pos, -d1, 32.0) + 0.3 * softray(pos, -d4, 16.0);
    #else
    float sha = 1.0;
    #endif
    float ao = ambocc(pos, norm);

    vec3 l1 = light(d1, vec3(1.0,0.9,0.8), tex, norm, camdir);
    vec3 l2 = light(d2, vec3(0.8,0.7,0.6), tex, norm, camdir);
    vec3 l3 = light(d3, vec3(0.3,0.3,0.4), tex, norm, camdir);
    vec3 l4 = light(d4, vec3(0.5,0.5,0.5), tex, norm, camdir);

    #ifdef SHADOWS
    return 0.2 * ao + 0.8 * (l1+l2+l3+l4)*sha;
    #else
    return 0.5 * ao + 0.5 * (l1+l2+l3+l4)*sha;
    #endif
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

vec3 rayrender(vec3 pos, vec3 dir)
{
   vec3 col = vec3(0.0);

   float dist = raymarcher(pos, dir);

    if (dist==-1.0) col = background(dir);
    else
    {
    	vec3 inters = pos + dist * dir;
    	col = material(inters, dir);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    vec2 xy = (fragCoord.xy - iResolution.xy/2.0) / max(iResolution.xy.x, iResolution.xy.y);

    vec3 campos = vec3(35.0*cos(t/5.0),10.0,35.0*sin(t/5.0));
    vec3 camtar = vec3(0.0,0.0,0.0);

    mat3 camMat = calcLookAtMatrix( campos, camtar, 0.0 );
	vec3 camdir = normalize( camMat * vec3(xy,0.9) );

    vec3 col = rayrender(campos, camdir);

    #ifdef SHADOWS
    col = pow(col, vec3(1.0/2.2));
    #else
    col = pow(col, vec3(1.0));
    #endif

	fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;
    float aspect_ratio = iResolution.y / iResolution.x;

    vec2 center = vec2(0.5, 0.3*aspect_ratio + 0.2* abs(cos(iTime)));
    float radius = 0.2*aspect_ratio;
    float val = (p.x - center.x) * (p.x - center.x) + (p.y*aspect_ratio - center.y) * (p.y*aspect_ratio - center.y);
    if (val < radius*radius)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1);
    }
    else if (p.y > 0.1 && p.y < 0.11) 
    {
        fragColor = vec4(0.5, 0.5, 0.5, 1);
    }
    else
    {
        fragColor = vec4(1.0, 1.0, 1.0, 1);
    }
}

vec3 rayVector(in vec3 position, in vec3 target, in vec2 fragCoord)
{
    vec3 eye = normalize(target - position);
    vec3 up = vec3(0., 1., 0.);
    vec3 right = cross(eye, up);
    up = cross(eye,right);

    mat3 cameraMatrix = mat3(right.x, up.x, eye.x,
                			 right.y, up.y, eye.y,
                			 right.z, up.z, eye.z);

    vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);
    uv.x = uv.x * iResolution.x/iResolution.y;
    float focalDistance = 0.5;
    return normalize(vec3(uv.x,uv.y,focalDistance)) * cameraMatrix;
}

vec4 shade(in vec3 pos, in vec3 normal, in vec3 cameraLoc, in vec3 lightPos, in vec4 lightColor, in vec4 surface)
{
    vec3 light = normalize(lightPos - pos);
    float dotlight = dot(normal,light);

    vec3 camera = normalize(cameraLoc - pos);
    vec3 cameraReflected = normalize(reflect(camera,normal));
    float spec = 0.0;
    if (dot(cameraReflected,light) < 0.0)
        spec = min(1.0,pow(dot(cameraReflected,light),2.0));
    return (surface
        * vec4(0.2+dotlight) * lightColor
        + vec4(0.5*spec)) * 10.0/length(lightPos - pos); // Sphere color
}

    const int maxSteps = 60;
    const int reflectionSteps = 10;

vec4 reflection(in vec3 ro, in vec3 rd, in vec3 cameraLoc, in vec3 shapeLoc, in vec3 light,  in vec3 light2,  in vec3 light3)
{
    vec4 color = vec4(0.0); // Sky color

    float t = 0.0;
    for(int i = 0; i < reflectionSteps; ++i)
    {
        vec3 p = ro + rd * t;

        vec3 pm = mod(p,6.0) - shapeLoc;

        float d = length(mod(p,6.0) - shapeLoc) - 1.8; 
        if(d < 0.1)
        {
            vec3 normal = normalize(pm);
            color = (shade(
                p, normal, cameraLoc, light, 
                vec4(1.0,0.0,0.0,0.0), 
                texture( iChannel0, vec2(atan(pm.x,pm.z),pm.y)))
                + shade(
                p, normal, cameraLoc, light2, 
                vec4(0.0,1.0,0.0,0.0), 
                texture( iChannel0, vec2(atan(pm.x,pm.z),pm.y)))
                + shade(
                p, normal, cameraLoc, light3, 
                vec4(0.0,0.0,1.0,0.0), 
                texture( iChannel0, vec2(atan(pm.x,pm.z),pm.y)))) * .333;
                ;
            break;
        }

        t += d;
    }
    return color;
}

vec4 march(in vec3 ro, in vec3 rd, in vec3 cameraLoc, in vec3 shapeLoc, in vec3 light,  in vec3 light2,  in vec3 light3)
{
    vec4 color = vec4(0.0); // Sky color

    float t = 0.0;
    for(int i = 0; i < maxSteps; ++i)
    {
        vec3 p = ro + rd * t;

        vec3 pm = mod(p,6.0) - shapeLoc;

        float d = length(mod(p,6.0) - shapeLoc) - 1.8; 
        if(d < 0.01)
        {
            vec3 normal = normalize(pm);

            vec3 cameraReflected = normalize(reflect(rd, normal));

            color = (shade(
                p, normal, cameraLoc, light, 
                vec4(1.0,0.0,0.0,0.0), 
                texture( iChannel0, vec2(atan(pm.x,pm.z),pm.y)))
                + shade(
                p, normal, cameraLoc, light2, 
                vec4(0.0,1.0,0.0,0.0), 
                texture( iChannel0, vec2(atan(pm.x,pm.z),pm.y)))
                + shade(
                p, normal, cameraLoc, light3, 
                vec4(0.0,0.0,1.0,0.0), 
                texture( iChannel0, vec2(atan(pm.x,pm.z),pm.y)))) * .333
            + 0.5*reflection(p + normal*.1, cameraReflected, p, shapeLoc, light, light2, light3);
                ;
            break;
        }

        t += d;
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 shapeLoc = vec3(3.0,3.0,3.0);
    vec3 cameraLoc = vec3(3. + 4.0 * sin(iTime), 3.0 + 4.0 * sin(0.4*iTime) , 3.+4.0 * cos(iTime));
    vec3 cameraTarget = shapeLoc;
    vec3 light = vec3(3. + 4.0 * sin(iTime*2.), 3.0 + 8.0 * sin(0.4*iTime) , 3.+4.0 * cos(2.*iTime));
    vec3 light2 = vec3(3. + 4.0 * sin(iTime*3.), 3.0 + 4.0 * sin(0.2*iTime) , 3.+8.0 * cos(3.*iTime));
    vec3 light3 = vec3(3. + 8.0 * sin(iTime*4.), 3.0 + 4.0 * sin(0.1*iTime) , 3.+4.0 * cos(4.*iTime));

    vec3 ro = cameraLoc;
    vec3 rd = rayVector(cameraLoc, cameraTarget, fragCoord);


    fragColor = march(ro, rd, cameraLoc, shapeLoc, light, light2, light3);
}

const float pi = 3.14159;

mat3 xrot(float t)
{
    return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrot(float t)
{
    return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrot(float t)
{
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}

vec2 map(vec3 p)
{
    float t = p.z;
    p = fract(p) * 2.0 - 1.0;
    vec3 q = p;
    float k = 1.0;
    float fd = 1000.0;
    float mt = 0.0;
    const int n = 8;
    for (int i = 0; i < n; ++i) {  
    	float d = length(q) - 0.1;
        if (d < fd) {
            mt = float(i);
            fd = d;
        }
        fd = min(fd, d);
        q += -sign(q) * (length(q)-0.15);
    }
    mt /= float(n-1);
    float tr = 1.0 + 0.4 * sin(t*3.0);
    float ca = length(p.xy) - tr;
    fd = max(fd, -ca);
    return vec2(fd, mt);
}

float trace(vec3 o, vec3 r)
{
	float t = 0.0;
    for (int i = 0; i < 32; ++i) {
        vec3 p = o + r * t;
        float d = map(p).x;
        t += d * 0.25;
    }
    return t;
}

vec3 normal(vec3 p)
{
	vec3 o = vec3(0.01, 0.0, 0.0);
    return normalize(vec3(map(p+o.xyy).x - map(p-o.xyy).x,
                          map(p+o.yxy).x - map(p-o.yxy).x,
                          map(p+o.yyx).x - map(p-o.yyx).x));
}

vec3 col(float x)
{
	vec3 ka = vec3(1.0, 1.0, 0.0) * 0.5;
    vec3 kb = vec3(1.0, 0.5, 0.0) * 0.5;
    vec3 kc = vec3(0.0, 0.0, 1.0) * 0.5;
    vec3 ma = mix(ka, kb, x);
    vec3 mb = mix(kb, kc, x);
    return mix(ma, mb, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    mat3 xfm = yrot(iTime) * xrot(iTime*0.5) * zrot(iTime*0.25);

    vec3 r = normalize(vec3(uv, 1.0 - dot(uv,uv)*0.333));
 	r *= xfm;

    vec3 o = vec3(0.5, 0.5, 0.0);
    o.z += iTime * 0.125;

    float t = trace(o, r);
    vec3 w = o + r * t;
    vec3 sn = normal(w);
    vec2 fd = map(w);

    float prod = max(dot(sn, -r), 0.0);

    float fog = 1.0 / (1.0 + t * t + fd.x * 100.0);

    float flmb = 0.0;
    float fspec = 0.0;
    float fls = 0.0;

    for (int j = -1; j <= 1; j+=2) {

        vec3 lpos = o + vec3(0.0,0.0,1.0) * float(j);

        float lt = 0.0;
        for (int i = 0; i < 16; ++i) {
            lt += (length(o + r * lt - lpos) - 0.1) * 0.5;
        }

        float lm = 1.0;
        if (t < lt) {
            lm = 1.0 / (1.0 + lm * lm * 0.1);
        }

        float ls = 1.0 / (1.0 + lt * lt * 0.001);

        vec3 ld = lpos - w;
        float la = length(ld);
        ld /= la;
        float lmb = max(dot(ld, sn), 0.0);
        vec3 refl = reflect(ld, sn);
        float spec = max(dot(refl, r), 0.0);
        spec = clamp(pow(1.0+spec, 4.0), 0.0, 1.0);
        spec = mix(spec, 0.0, fd.y);
        float atten = 1.0 / (1.0 + la * la * 0.01);

        flmb += lmb * atten;
        fspec += spec * atten;
        fls += ls * lm;
    }

    vec3 diff = col(fd.y);

    diff = mix(diff, vec3(1.0, 1.0, 1.0), abs(sn.z));

    vec3 fc = diff * (flmb + fspec) * fog + fls;

	fragColor = vec4(fc, 1.0);
}

