mat3 rotX(float a)
{
	float c=cos(a);
	float s=sin(a);
	return mat3(1.0,0.0,0.0,0.0,c,-s,0.0,s,c);
}

mat3 rotY(float a)
{
	float c=cos(a);
	float s=sin(a);
	return mat3(c,0.0,s,0.0,1.0,0.0,-s,0.0,c);
}

mat3 rotZ(float a)
{
	float c=cos(a);
	float s=sin(a);
	return mat3(c,-s,0.0,s,c,0.0,0.0,0.0,1.0);
}

mat3 rot(vec3 z,float a)
{
	float c=cos(a);
	float s=sin(a);
	float ic=1.0-c;
	return mat3(
		ic*z.x*z.x+c,ic*z.x*z.y-z.z*s,ic*z.z*z.x+z.y*s,
		ic*z.x*z.y+z.z*s,ic*z.y*z.y+c,ic*z.y*z.z-z.x*s,
		ic*z.z*z.x-z.y*s,ic*z.y*z.z+z.x*s,ic*z.z*z.z+c);
}

float dist(vec3 p)
{
    float d = 100.0;
    vec3 tp; /*temp point*/
    for(int i = 0; i < 8; i++)
    {
        tp = p*rotX(iTime/float(i+1));
        tp *=rotZ(2.0*iTime/float(i+1));
    	d = min(d,length(vec2(length(tp.xz)-0.28*float(i)-0.25, tp.y))-0.13);
    }
    return d;
}

vec3 normal(vec3 p, float d)
{
    vec3 s=vec3(0.1,0.0,0.0);
    return normalize(vec3(
        dist(p+s.xyy-d),
        dist(p+s.yxy-d),
        dist(p+s.yyx-d)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /*screen coordinates (sc)*/
	vec2 sc = vec2(fragCoord.x-0.5*iResolution.x,fragCoord.y-0.5*iResolution.y);
    sc /= iResolution.xy;
    sc.x *= iResolution.x/iResolution.y;

    /*calculate vectors for raymarching*/
    vec3 upVec = vec3(0.0,-1.0,0.0);/*up vector is on y-axis*/
    vec3 lookPos = vec3(0.0,0.0,0.0); /*eye looking at origin*/
    vec3 eyePos = vec3(0.0,0.0,-5.0); /*eye offset on z-axis*/

    /*animate camera*/
    eyePos *= rotX(sin(0.1*iTime));
    eyePos *= rotY(sin(0.2*iTime));

    vec3 rayVec = normalize(lookPos - eyePos); /*direction of ray*/

    /*calculate a vector pointing directly to the left of the eye*/
    vec3 leftVec = normalize(cross(upVec,rayVec));
    /*calculate the up for the eye*/
    vec3 eyeUpVec = normalize(cross(rayVec,leftVec));

    rayVec *= rot(eyeUpVec,sc.x*0.8);
    rayVec *= rot(leftVec,sc.y*0.8);

    /*march ray*/
    float d;
    float marchLen;
    vec3 rayPos = eyePos;
    vec4 color = vec4(1.0,1.0,1.0,1.0);
    bool hit = false;
    vec3 n;
    for(int i = 0; i < 100; i++)
    {
        d = dist(rayPos);
        if(hit == false)
        {
            if(d<0.0)
            {
                hit=true;
                n = normal(rayPos,d);
                color=vec4(abs(dot(rayVec,n)));
            }
            marchLen = max(0.001,abs(d));
            rayPos += rayVec*marchLen;
        }
    }
	fragColor = color;
}

