	vec3 bcol=vec3(1.0);
	//march

	float t=DDE(ro,rd).y*rndStart(pxx),d,od=1.0;
    bool hit = false;
	for(int i=0;i<240;i++){
		vec2 v=DDE(ro+rd*t,rd);
		d=v.x;
		float px=pxl*(1.0+t);
		if(d<px){
            hit = true;
            break;
		}
		od=d;
		t+=v.y;//d;
		if(t>150.0)break;
	}
    return hit ? vec4(compute_color(ro, rd, t), 1.) : 
