// ray - sphere intersection
vec2 iSphere(vec3 ro, vec3 rd, vec4 sph)
{
    vec3 tmp = ro - sph.xyz;

    float b = dot(rd, tmp);
    float c = dot(tmp, tmp) - sph.w * sph.w;

    float disc = b * b - c;

    if(disc < 0.0) return vec2(-M_MAX, -M_MAX);

    float disc_sqrt = sqrt(disc);

    float t0 = -b - disc_sqrt;
    float t1 = -b + disc_sqrt;

    return vec2(t0, t1);
}

