// Henyey-Greenstein phase function
float phase(float nu, float g)
{
	return (3.0 * (1.0 - g * g) * (1.0 + nu * nu)) / (2.0 * (2.0 + g * g) * pow(1.0 + g * g - 2.0 * g * nu, 1.5));
}

///////////////////////////////////////
// density integral calculation from p0 to p1 
// for mie and rayleight
vec2 densityOverPath(vec3 p0, vec3 p1, vec2 prescaler)
{
    float l = length(p1 - p0);
    vec3  v = (p1 - p0) / l;

    l /= NUM_DENSITY_SAMPLES;

    vec2 density = vec2(0.0);
    float t = 0.0;

	for(int i = 0; i < INT_NUM_DENSITY_SAMPLES; i++)
    {
        vec3 sp = p0 + v * (t + 0.5 * l);
        vec2 h = vec2(length(sp) - earthRadius);
        density += exp(-h / prescaler);

        t += l;
    }

    return l * density;
}

///////////////////////////////////////
// inscatter integral calculation
vec4 inscatter(vec3 cam, vec3 v, vec3 sun)
{    
    vec4 atmoSphere 	= vec4(earthCenter, atmoRadius);
    vec4 earthSphere 	= vec4(earthCenter, earthRadius);

	vec2 t0 = iSphere(cam, v, atmoSphere);
    vec2 t1 = iSphere(cam, v, earthSphere);

    bool bNoPlanetIntersection = t1.x < 0.0 && t1.y < 0.0;

    float farPoint = bNoPlanetIntersection ? t0.y : t1.x;
    float nearPoint = t0.x > 0.0 ? t0.x : 0.0;

    float l = (farPoint - nearPoint) / NUM_VIEW_SAMPLES;
	cam += nearPoint * v;  

    float t = 0.0;

    vec3 rayleight = vec3(0.0);
	vec3 mie = vec3(0.0);

    vec2 prescalers = vec2(heightScaleRayleight, heightScaleMie);

    vec2 densityPointToCam = vec2(0.0);

    for(int i = 0; i < INT_NUM_VIEW_SAMPLES; i++)
    {
        vec3 sp = cam + v * (t + 0.5 * l);
        float tc = iSphere(sp, sun, vec4(earthCenter, atmoRadius)).y;

        vec3 pc = sp + tc * sun;

        vec2 densitySPCam = densityOverPath(sp, cam, prescalers);
        vec2 densities = densityOverPath(sp, pc, prescalers) + densitySPCam;

        vec2 h = vec2(length(sp) - earthRadius);
        vec2 expRM = exp(-h / prescalers);

        rayleight 	+= expRM.x * exp( -M_4PIbetaR * densities.x );
		mie 		+= expRM.y * exp( -M_4PIbetaM * densities.y );

		densityPointToCam += densitySPCam;

        t += l;
    }

	rayleight *= l;
    mie *= l;

    vec3 extinction = exp( - (M_4PIbetaR * densityPointToCam.x + M_4PIbetaM * densityPointToCam.y));

    float nu = dot(sun, -v);

    vec3 inscatter_ = sunIntensity * (betaM * mie * phase(nu, g) + betaR * phase(nu, 0.0) * rayleight);
    return vec4(inscatter_, extinction.r * float(bNoPlanetIntersection));
}

///////////////////////////////////////
// rotation around axis Y
vec3 rotate_y(vec3 v, float angle)
{
	vec3 vo = v; float cosa = cos(angle); float sina = sin(angle);
	v.x = cosa*vo.x - sina*vo.z;
	v.z = sina*vo.x + cosa*vo.z;
	return v;
}

///////////////////////////////////////
// noise from iq
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0) * p.z) + f.xy;
	vec2 rg = texture( iChannel0, (uv + 0.5)/256.0, -100.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 sc = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    sc.x *= iResolution.x / iResolution.y;

    vec3 mouse = 4.0 * vec3(2.0 * iMouse.xy / iResolution.xy - 1.0,0.0);

    vec3 ro = vec3(0.0);

    bool key_m = texture(iChannel1, vec2(KEY_M, 0.75)).x > 0.0;
   	ro = key_m ?	2.0 * vec3(earthRadius * sin(mouse.x), 0.0, earthRadius * cos(mouse.x)): 
    				vec3(0.0, earthRadius + 0.1, 1000.0 * abs(cos(iTime / 10.0)));

    vec3 rd = normalize(rotate_y(vec3(sc, 1.2), M_PI - mouse.x));

    vec3 sun = normalize(vec3(1.0, 1.0, 1.0));

    vec4 col = inscatter(ro, rd, sun);

    vec3 sunPos = sun * distanceToSun;

    vec4 star = vec4(sunPos, sunRadius);
    vec2 t0 = iSphere(ro, rd, star);

    if(t0.x > 0.0)
    {
    	col.xyz += vec3(1.0,1.0,1.0) * col.a;
    }

    vec3 stars = vec3(noise(rd * iResolution.y * 0.75));
    stars += vec3(noise(rd * iResolution.y * 0.5));
    stars += vec3(noise(rd * iResolution.y * 0.25));
    stars += vec3(noise(rd * iResolution.y * 0.1));
    stars = clamp(stars, 0.0, 1.0);
    stars = (vec3(1.0) - stars);

    col.xyz = col.xyz + stars * col.a;
    col.xyz = pow(col.xyz, vec3(1.0 / 2.2));

    fragColor = col;
}

// --- noise functions from https://www.shadertoy.com/view/XslGRr
// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

const mat3 m = mat3( 0.00,  0.80,  0.60,
           		    -0.80,  0.36, -0.48,
             		-0.60, -0.48,  0.64 );

float hash( float n ) {
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x ) { // in [0,1]
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.-2.*f);

    float n = p.x + p.y*57. + 113.*p.z;

    float res = mix(mix(mix( hash(n+  0.), hash(n+  1.),f.x),
                        mix( hash(n+ 57.), hash(n+ 58.),f.x),f.y),
                    mix(mix( hash(n+113.), hash(n+114.),f.x),
                        mix( hash(n+170.), hash(n+171.),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p ) { // in [0,1]
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );
    return f;
}
// --- End of: Created by inigo quilez --------------------

vec2 sfbm2( vec3 p ) {
    return 2.*vec2(fbm(p),fbm(p-327.67))-1.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 mouse = iMouse.xy / iResolution.xy;
    if (iMouse.z<0.) 
         mouse = vec2(.9,.5)*vec2(cos(1.*t)+.5*sin(2.2*t),sin(1.1*t)+.5*cos(1.9*t))/1.5;   
    else 
         mouse = 2.*mouse-1.;
	vec2 uv = 2.*(fragCoord.xy / iResolution.y-vec2(.9,.5));
    float a = .5*t, c=cos(a), s=sin(a); uv *= mat2(c,-s,s,c);

    vec4 col=vec4(0.);
    vec3 paint = vec3(.3,.9,.7);

    int i=0; for(float z=0.; z<1.; z+= 1./30.) {
        vec2 duv = vec2(.8,.5)*sfbm2(vec3(1.2*uv,3.*z+t)) - 3.*z*mouse;
    	float d = abs(length(uv+duv)-1.2*(1.-z)),
              a = smoothstep(.2,.19,d); 
        d = a-.5*smoothstep(.18,.17,d)+.5*smoothstep(.02,.01,d);
        col += (1.-col.a)*vec4(d*paint*exp(-3.*z),a);
        if (col.a>=.9) break;
        // nemo ;-) if (i++==5) { d=smoothstep(.1,.09,length(vec2(1.,2.)*(uv+2.*z*mouse))); col += (1.-col.a)*d*vec4(1.,.5,0.,1.);}
    }
	fragColor = col;
}

//
//
// Spider by cedric voisin 2014
//
//

#define PI 3.14
#define maxH 1.5

struct leg {
	vec3 pHip, pKnee, pAnkle, pFoot;
	vec2 rp; // (cosp, sinp)
};
leg legs[8];

// perlin

float r(vec2 c){
	return fract(sin(dot(c ,vec2(12.9898,78.233))) * 43758.5453);
}

float pn (vec2 p){
	p/=2.;
    vec2 i = floor(p), w = fract(p), j = vec2(1.,0.);
    w = w*w*(3.-2.*w);
    return mix(mix(r(i), r(i+j), w.x), mix(r(i+j.yx), r(i+1.), w.x), w.y);
}

float an (vec2 p){
	const int n=7;
	float m = 0., f = 1.;
	for ( int i=0; i<n; i++ ){ m += pn(f*p)/f; f*=2.; }
	return m/(2.-pow(.5,float(n)));
}

// primitive (from iq's website)
float deC(vec3 p, vec3 a, vec3 b, float r){
	vec3 pa = p - a;
	vec3 ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

// leg
float deLeg(vec3 p){
	float sil = 1000.;
	for (int i=0;i<8;i++){
    	sil = min(sil, deC(p, legs[i].pHip, legs[i].pKnee, .03));
    	sil = min(sil, deC(p, legs[i].pKnee, legs[i].pAnkle, .02));
    	sil = min(sil, deC(p, legs[i].pAnkle, legs[i].pFoot, .01));
	}
	return sil;
}

vec3 norLeg(vec3 p){
	vec3 dp = vec3(.0001,0.,0.);
	vec3 n=normalize(vec3(deLeg(p+dp.xyy),deLeg(p+dp.yxy),deLeg(p+dp.yyx))); 
	return n;
}

float field(vec2 f){return maxH*pn(f);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// global
	vec2 xy = -2.5 + 5.*fragCoord.xy / iResolution.xy;
	xy.y *= iResolution.y/iResolution.x;

	// time	
	float camTime = .03*iTime;
	float spiTime = (iTime+cos(.5*iTime)+cos(.4*iTime));

	// spider main
	vec3 pSpi, pAbdo;
	float rBody, rAbdo, rf;
	rBody = .2;
	pSpi = vec3(spiTime,0.,0.);;
	pSpi.z = .2+rBody+field(pSpi.xy);

	// sun
	vec3 pSun = pSpi+vec3(100.*sin(.2*camTime),100.*cos(.2*camTime),100.);

	// cam
	float xCam, yCam, zCam;
	vec3 pCam, nCam, uCam, rCam, pEye;
	xCam = pSpi.x + 5. * sin(6.*camTime);
	yCam = pSpi.y - 5. * cos(6.*camTime);
	zCam = max(7. + 2.*cos(camTime-5.), .2+field(vec2(xCam, yCam)) );
	//zCam = 4.;

	pEye = vec3(xCam, yCam, zCam);
	nCam = normalize(pSpi-pEye);
	rCam = normalize(vec3(nCam.y,-nCam.x,0.));
	uCam = cross(rCam,nCam);
	pCam = pEye + 5.*nCam;

	// spider parts
	// abdomen
	rAbdo = 1.7*rBody;
	vec3 oldPos = vec3(spiTime-.05,0.,0.);
	oldPos.z = .22+rBody+field(oldPos.xy);
	pAbdo = pSpi-(rBody+rAbdo)*normalize(pSpi - oldPos);

	// directions
	vec3 spiF, spiL, spiU;
	spiF = normalize(pSpi - pAbdo);
	spiL = normalize(vec3(-spiF.y,spiF.x,0.));
	spiU = cross(spiF, spiL);

	// eyes co(-pi/32), sin(-pi/32), sin(pi/5)
	vec3 pOcelli[2];
	pOcelli[0] = pSpi + .7*rBody*spiF + rBody*.1*spiL + rBody*.8*spiU;
	pOcelli[1] = pSpi + .7*rBody*spiF - rBody*.1*spiL + rBody*.8*spiU;

	//legs a=hip->knee, b=knee->ankle, c=ankle->foot
	rf = rBody + 1.35; // 1.35 = leg span
	float a=.9, b=1., f=.35;

	float rcosp, rsinp, cosa, dt, tf, tc, to, tPct, c;
	vec2 pf;
	vec3 base, up;

	//defined manually to get them out of the loop and avoid cos/sin calls
	legs[0].rp =  vec2(-.588,-.809); // vec2(cos(-PI/5.-3.*PI/6.),sin(-PI/5.-3.*PI/6.));
	legs[1].rp =  vec2(-.105,-.995); // vec2(cos(-PI/5.-2.*PI/6.),sin(-PI/5.-2.*PI/6.));
	legs[2].rp =  vec2(.407,-.914); // vec2(cos(-PI/5.-PI/6.),sin(-PI/5.-PI/6.));
	legs[3].rp =  vec2(.809, -.588); // vec2(cos(-PI/5.),sin(-PI/5.));
	legs[4].rp =  vec2(.809,.588); // vec2(cos(PI/5.),sin(PI/5.));
	legs[5].rp =  vec2(.407,.914); // vec2(cos(PI/5.+PI/6.),sin(PI/5.+PI/6.));
	legs[6].rp =  vec2(-.105,.995); // vec2(cos(PI/5.+2.*PI/6.),sin(PI/5.+2.*PI/6.));
	legs[7].rp =  vec2(-.588,.809); // vec2(cos(PI/5.+3.*PI/6.),sin(PI/5.+3.*PI/6.));

	for (int i=0;i<8;i++){

		// hip	
		legs[i].pHip = pSpi + rBody*legs[i].rp.x*spiF + rBody*legs[i].rp.y*spiL;

		// foot
		dt = mod(float (i),2.)*.5;
		tf = spiTime+dt+.3; // .3 = how much foot precedes body (tuning)
		tc = fract(tf);
		to = floor(tf);
		rcosp = rf*legs[i].rp.x;
		rsinp = rf*legs[i].rp.y;

		// cycle: .67s on the ground, .33s to catch up
		if (tc < .67){
			pf = vec2(to-dt+rcosp,rsinp);
			legs[i].pFoot = vec3(pf, field(pf));
		} else {
			tPct = (tc-.67)/.33;
			pf = vec2(to-dt+tPct*tc+rcosp,rsinp);
			legs[i].pFoot = vec3(pf, field(pf.xy)+2.5*tPct*(1.-tPct)); // parabola 2.5~step height
		}

		// ankle: cos(foot angle) ~ distance from the hip
		base = legs[i].pHip-legs[i].pFoot;
		up = normalize(cross(vec3(base.y,-base.x,0.),base));
		cosa = cos( 2.8*(1.-length(base)/(a+b+f)) ); // amax+base*(amin-amax)/(a+b+f), amin = 0, amax=2.8
		legs[i].pAnkle = legs[i].pFoot + cosa*f*normalize(base) + sqrt(1.-cosa*cosa)*f*up; 

		// knee	(triangle)
		base = legs[i].pAnkle - legs[i].pHip;
		up = normalize(cross(vec3(base.y,-base.x,0.),base));
		c = length(base);
		cosa = (a*a+c*c-b*b)/(2.*a*c);
		legs[i].pKnee = legs[i].pHip + cosa*a*normalize(base) + sqrt(1.-cosa*cosa)*a*up;
	}

	// virtual screen
	vec3 pStart = pCam+xy.x*rCam+xy.y*uCam;
	vec3 dir = normalize(pStart-pEye);

	// ray march
	int idI = 0; //Background 0, Terrain 1, Body 2, Abdo 3, Leg 4, eyes 5
	float eps = .001, fov = 30., de = fov, si = 10.*fov, shad = 1., s = 0., h = 1.;
	vec3 npi = -dir, pi = pEye, dp = vec3(.0001,0.,0.);
	float dpx, dpy;

	for (int i=0; i<150; i++){

		// body
		de = min(de, clamp(length(pi-pSpi)-rBody,eps,fov));
		if (de == eps) {si = s; idI = 2; break;}
		// abdomen
		de = min(de, clamp(length(pi-pAbdo)-rAbdo,eps,fov));
		if (de == eps) {si = s; idI = 3; break;}
		// eyes
		de = min(de, clamp(length(pi-pOcelli[0])-.1*rBody,eps,fov));
		if (de == eps) {si = s; idI = 5; break;}
		de = min(de, clamp(length(pi-pOcelli[1])-.1*rBody,eps,fov));
		if (de == eps) {si = s; idI = 5; break;}
		// legs	
		de = min(de, clamp(deLeg(pi),eps,fov));
		if (de == eps) {si = s; idI = 4; break;}
		// terrain
		h = pi.z - field(pi.xy);
		if (h<.01) {si = s; idI = 1; break;}

		s += min(de,.5*h);
		if (s > fov) break;
		pi = pEye + dir*s;

	}	

	// common colors
	vec3 col, co, li;
	vec3 colSky = vec3(.8,.7,.6);
	vec3 colSun = vec3 (1.,1.,.7);

	// illuminations
	if (idI == 0){ // background

		//base
		co = vec3(0.,0.,0.);
		float vGrad=clamp(1.5-fragCoord.y / iResolution.y,0.,1.);
		co += vGrad*colSky;

		// lightning
		li = vec3(0.,0.,0.);
		float sunGrad = 1.+dot(normalize(pSun-pEye), normalize(pi-pEye) );
		li += (1.+sunGrad)*colSun;

		col = co*li;
	}

	if (idI == 1){ // terrain
		dpx = maxH * (an(pi.xy+dp.xy)-an(pi.xy-dp.xy)) / (2.*dp.x);							  
		dpy = maxH * (an(pi.xy+dp.yx)-an(pi.xy-dp.yx)) / (2.*dp.x);
		npi = normalize( vec3(-dpx, -dpy, 1.) );	
		pi = pEye + dir*si;

		// base
		co = vec3(0.,0.,0.);
		vec3 colField = vec3(.62,.6,.6);
		co += colField;

		// occlusion
		float iOcc = npi.z;

		// spider occlusion
		float sOcc = smoothstep(0., .8, length(pi-.5*pSpi-.5*pAbdo)/rf );

		// lightning
		// spider shadows
		float sShad = 1., tShad = 1.;
		dir = normalize(pSun-pi);
		de = fov;
		vec3 ps = pi;
		s = 0.;
		for (int i=0; i<50; i++){
			tShad = 1.;
			// body
			de = min(de, clamp(length(ps-pSpi)-rBody,eps,fov));
			de = min(de, clamp(length(ps-pAbdo)-rAbdo,eps,fov));
			de = min(de, clamp(deLeg(ps),eps,fov));

			s += de;
			if (ps.z > pSpi.z+5.) break;
			ps = pi + dir*s;
			tShad = min(tShad, 60.*de/s);
			sShad *= tShad;
		}	

		float iDif = dot(npi,normalize(pSun-pi));

		li = vec3(0.,0.,0.);
		li += iDif*colSun;
		li *= sShad;
		li *= sOcc;
		li += .8*iOcc*colSky;

		col = co*li;
	}

	if (idI == 2){ // body
		npi = normalize(pi-pSpi);

		// base
		co = vec3(0.,0.,0.);
		vec3 colBase = vec3 (.9,.6,.2);
		co += colBase;

		// dark below
		co = mix(.4*colBase, co,smoothstep(-.4,1.,dot(npi,spiU)) );

		// occlusion
		float iOcc = .5 + .5*dot(npi,spiF);

		// lightning
		float iAmb = .5+.5*npi.z;
		float iDif = .5+.5*dot(npi,normalize(pSun-pi));

		li = vec3(0.,0.,0.);
		li += iDif*colSun;
		li += .2*iAmb*colSky;
		li *= iOcc;

		col = 2.*co*li;
	}

	if (idI == 3){ // abdomen
		vec3 piInA = vec3(dot(pi-pAbdo,spiF), dot(pi-pAbdo,spiL), dot(pi-pAbdo,spiU));
		npi = normalize(pi-pAbdo);

		// base
		co = vec3(0.,0.,0.);
		vec3 colBase = vec3 (.9,.5,.1);
		co += colBase;

		// decoration (black)
		float theta = acos(piInA.x/length(piInA));
		float phi = .2+abs(atan(piInA.y, piInA.z)); // abs for symmetry
		co *= 1.05-smoothstep(.5,.5,an(4.*vec2(theta, phi)));
		co = mix(.9*colBase, co,smoothstep(-.4,1.,dot(npi,spiU)) );

		// occlusion
		float iOcc = .5-.5*dot(npi,spiF);

		// lightning
		float iAmb = .5+.5*npi.z;
		float iDif = dot(npi,normalize(pSun-pi));

		li = vec3(0.,0.,0.);
		li += .07*iDif*colSun;
		li += .2*iAmb*colSky;
		li *= iOcc;

		col = 12.*co*li;
	}

	if (idI == 4){ // legs
		npi = norLeg(pi);

		// base
		co = vec3(0.,0.,0.);
		vec3 colBase = vec3 (.6,.4,.1);
		co += colBase;

		// black articulations
		float iArt = 2.*rf;
		for (int i=0;i<8;i++){
			iArt = min(iArt, length(pi-legs[i].pKnee));
			iArt = min(iArt, length(pi-legs[i].pAnkle));
		}
		co *= 10.*iArt*iArt*iArt;

		// occlusion
		float iOcc = length(pi-pSpi)/rf;	

		// lightning
		float iAmb = .5+.5*npi.z;
		float iDif = dot(npi,normalize(pSun-pi));

		li = vec3(0.,0.,0.);
		li += iDif*colSun;
		li += iAmb*colSky;
		li *= 2.*iOcc;

		col = co*li;
	}

	if (idI == 5){ // eyes
		col = vec3(0.,0.,0.);
	}

	// fog
	vec3 colFog = vec3(.5,.6,.7);
	float cFog = 1.-smoothstep(.3*fov, fov, length(dir*si))+smoothstep(fov, 1.1*fov,length(dir*si));
    col = mix(colFog, col, cFog );

	col = clamp(col,0.,1.);
	fragColor = vec4(col,1.);
}

#define time iTime

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,126.7378))) * 43758.5453)*2.0-1.0;
}

float linear3D(vec3 p)
{
	vec3 p0 = floor(p);
	vec3 p1x = vec3(p0.x+1.0, p0.y, p0.z);
	vec3 p1y = vec3(p0.x, p0.y+1.0, p0.z);
	vec3 p1z = vec3(p0.x, p0.y, p0.z+1.0);
	vec3 p1xy = vec3(p0.x+1.0, p0.y+1.0, p0.z);
	vec3 p1xz = vec3(p0.x+1.0, p0.y, p0.z+1.0);
	vec3 p1yz = vec3(p0.x, p0.y+1.0, p0.z+1.0);
	vec3 p1xyz = p0+1.0;

	float r0 = noise3D(p0);
	float r1x = noise3D(p1x);
	float r1y = noise3D(p1y);
	float r1z = noise3D(p1z);
	float r1xy = noise3D(p1xy);
	float r1xz = noise3D(p1xz);
	float r1yz = noise3D(p1yz);
	float r1xyz = noise3D(p1xyz);

	float a = mix(r0, r1x, p.x-p0.x);
	float b = mix(r1y, r1xy, p.x-p0.x);
	float ab = mix(a, b, p.y-p0.y);
	float c = mix(r1z, r1xz, p.x-p0.x);
	float d = mix(r1yz, r1xyz, p.x-p0.x);
	float cd = mix(c, d, p.y-p0.y);


	float res = mix(ab, cd, p.z-p0.z);

	return res;
}

float fbm(vec3 p)
{
	float f = 0.5000*linear3D(p*1.0); 
		  f+= 0.2500*linear3D(p*2.01); 
		  f+= 0.1250*linear3D(p*4.02); 
		  f+= 0.0625*linear3D(p*8.03);
		  f/= 0.9375;
	return f;
}

