    mat2 rot = mat2(cos(ang),-sin(ang),sin(ang),cos(ang));
	uv*=16.0*(sin(time*0.1)+1.5)*rot;

	float f = fbm(vec3(uv,time)+fbm(vec3(uv,time)+fbm(vec3(uv,time))))*0.5+0.5;

    vec3 col, col2;
    col = vec3(fbm(vec3(uv*f*0.3,time*0.75)));
    col2 = col;

	col *= vec3((sin(time*0.2)*0.5+1.5),1.0,0.6);
    col +=  vec3(0.1,0.7,0.8)*f;

    col2 *= vec3(0.9,1.0,(sin(time*0.2)*0.5+1.5));
    col2 += vec3(0.8,0.5,0.1)*f;

	col = mix(col, col2, smoothstep(-50.0,50.0,uv.x));

    col *= mix(0.5,sin(time*0.5)*0.25+1.0,length(col));
	fragColor = vec4(col,1.0);
}

// Procedural Houndstooth shader
// 	tutorial of one way to combine multiple procedural shapes without using if() branches
// 	to create a complex pattern.
//
// by @christinacoffin
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// for application as a procedural texture on 3d obj+environment,
//	you'd need to supersample+filter the results of Houndstooth() function.
// a good article on that can be found here:
//	http://iquilezles.org/www/articles/filtering/filtering.htm

float Houndstooth( vec2 uv_A )
{
    // generate working vars for 2x2 checker shape
    float sub_A = step(uv_A.x, 0.5);
    float sub_B = step(1.0-uv_A.y, 0.5);
    float sub_C = max(sub_A,sub_B);// L shape black
    float sub_D = min(sub_A,sub_B);// L shape black (inverted)
    float sub_E = 1.0-sub_C;// corner

    float stripe_A =  step( (uv_A.x) + (uv_A.y),  0.25 ); 
    float stripe_B =  step( (uv_A.x) + (uv_A.y),  0.5 ); 
    float stripe_C =  step( (uv_A.x) + (uv_A.y),  0.75 ); 

    float stripe_F =  step( (uv_A.x-0.5) + (uv_A.y-0.5),  0.25 );
    float stripe_G =  step( (uv_A.x-0.5) + (uv_A.y-0.5),  0.5 );
    float stripe_H =  step( (uv_A.x-0.5) + (uv_A.y-0.5),  0.75 );    

	// build diagonal parts as 3 bands and generate that before mixing in the square quadrants.

    // subtracting the results of a stepfunction with a lower threshold value from a higher one
    //	creates a square wave function that we use to create the diagonal bands    

    // a good reference to visualize step+pulse functions and mixing them can be found here:
    //	http://realtimecollisiondetection.net/blog/?p=95    

    // 1st band
    float lum = (stripe_B  - stripe_A);
    // larger inner diagonal
    lum +=	(stripe_F  - stripe_C);
    // 3rd band
    lum +=	(stripe_H  - stripe_G);

    // put the black+white squares in the diagonal quadrants 'on top' to finish the pattern
    lum += sub_E;// top diag corner white  
    lum -= sub_D;// upper left corner black    

    return clamp(lum , 0.0, 1.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = iTime;// animated rotation over time.

	vec2 uv;
    vec2 vignetteUV;
    uv = fragCoord.xy / iResolution.xy;
    vignetteUV = uv;// copy UV for vignette code

    vec4 animFragColor;
    animFragColor = vec4(fract(uv),0.5+0.5*sin(iTime),1.0);

    uv.xy += vec2( -.5, -.5);// offset uv so it rotates in the center of screen
    uv.x /= iResolution.y/iResolution.x;// aspect correct
    uv *= 6.0;// tiling factor of pattern

	// rotate UV space
    vec2 rotCoord;
    rotCoord.x = uv.x * cos(angle) + uv.y * sin(angle);
    rotCoord.y = uv.x * - sin(angle) + uv.y * cos(angle);    

    vec2 uv_A = rotCoord;// copy rotated UV
    uv_A = uv_A - floor(uv_A);// turn the tiled UV coords into 0-1 space for the procedural texture func

    fragColor.rgb = vec3( Houndstooth( uv_A ) );// 'sample' the procedural texture

    fragColor.rgb*= animFragColor.rgb;// mul with screen gradient wash color to colorize it 

	//-----------------------------------------------------
	// Vignette
	fragColor.rgb *= 0.15 + 0.85*pow( abs(32.0*vignetteUV.x*vignetteUV.y*(1.0-vignetteUV.x)*(1.0-vignetteUV.y)), 0.85 );    

}

float dis(vec2 a, vec2 b) {
    float dx = a.x - b.x;
    float dy = a.y - b.y;
	return sqrt(dx*dx + dy*dy);
}

float u(float a, float b) {
    return max(a, b);
}

float circle(vec2 uv, vec2 p, float r) {
	return -1.0 * sign(length(uv - p)  - r);
}

float box( vec2 uv, vec2 p, vec2 s )
{
  vec2 res = abs(uv - p) - s;
  return -1.0 * sign(max(res.x,res.y));
}

vec2 rot(vec2 uv, float a) {
    uv -= vec2(0.5, 0.5);
	float s = sin(a);
    float c = cos(a);
    return vec2(uv.x*c + uv.y*s, uv.x*s + uv.y*c) + vec2(0.5, 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime;
    float hit = u(circle(uv, vec2(0.5 + sin(t*0.75), 0.5 + sin(t)), 0.3),
                  circle(uv, vec2(0.1 + 0.5*sin(t*0.3), 0.1 + 0.6*cos(t*0.4)), 0.1));
    hit = u(hit, box(rot(uv, t), vec2(0.5, 0.5), vec2(0.1,0.7)));
    vec3 col = vec3(1,0,0);

    float c = max(sin(t*1.0 + uv.x * 20.0), sin(t*1.0 + uv.y * 20.0));
    hit = max(0.0, hit);
    c += hit * (0.5 * c + 0.5);
    vec3 wCol = vec3(c,c,c);


    wCol.x = max(wCol.x, 0.0);
    wCol.y = max(wCol.y, 0.0);
    wCol.z = max(wCol.z, 0.0);

    col.x = max(col.x, 0.0);
    col.y = max(col.y, 0.0);
    col.z = max(col.z, 0.0);


	fragColor = vec4(/*col * hit + wCol  * (1.0 - hit)*/wCol, 1.0);


    vec2 p = vec2(0.5 + sin(iTime*0.1),0.5+ sin(iTime*0.1));
    vec2 p2 = vec2(0.3+ cos(iTime*0.2),0.7+ sin(iTime*0.15));
    vec2 p3 = vec2(1.0+ sin(iTime*0.06),1.0+ sin(iTime*0.07));
	fragColor += (1.0-hit) * 0.1 * vec4(sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     cos(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     sin(dis(uv, p) * 100.0 - 1.0*iTime), 

                     sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     sin(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     cos(dis(uv, p) * 100.0 - 1.0*iTime), 

                     sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     sin(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     sin(dis(uv, p) * 100.0 - 1.0*iTime)
                     ,1.0);

    //fragColor += (sign(sin(t*2.0) - 0.9) + 1.0) * 0.5 * 0.5 * vec4(-fragColor.x, 1, -fragColor.z,1.0);

}

