vec4 worley( vec3 p ) {
    vec4 d = vec4(1e15);
    vec3 ip = floor(p);
    for (float i=-1.; i<2.; i++)
   	 	for (float j=-1.; j<2.; j++)
            for (float k=-1.; k<2.; k++) {
                vec3 p0 = ip+vec3(i,j,k),
                      c = hash33(p0)+p0-p;
                float d0 = dot(c,c);
                if      (d0<d.x) { d.yzw=d.xyz; d.x=d0; }
                else if (d0<d.y) { d.zw =d.yz ; d.y=d0; }
                else if (d0<d.z) { d.w  =d.z  ; d.z=d0; }
                else if (d0<d.w) {              d.w=d0; }   
            }
    return sqrt(d);
}


