    grad=.8, scale = 7., thresh=.7+.2*(cos(.5*time)+.36*cos(.5*3.*time))/1.36;
    vec4 w=scale*worley(scale*p-vec3(0.,0.,3.*time)); 
    float v=1.-1./(1./(w.z-w.x)+1./(w.a-w.x)); // formula (c) Fabrice NEYRET - BSD3:mention author.
#  if SPIKES    
    v *= 1.-.9*(2.*fbm(10.*scale*p)-1.);
#  endif
#endif

    return (true)? smoothstep(thresh-grad/2.,thresh+grad/2.,v*d) : v*d;
}

// Cheap computation of normals+Lambert using directional derivative (see https://www.shadertoy.com/view/Xl23Wy )
// still, we need an estimate of slope amplitude to avoid artifacts (see grad+scale).
float shadedNormal( vec3 p, float v ) {
    float epsL = 0.01;
#if 1// centered directional derivative
    float dx = (tweaknoise(p+epsL*lightDir,false)-tweaknoise(p-epsL*lightDir,false))/(2.*epsL);
#else // cheap directional derivative
    float dx = (tweaknoise(p+epsL*lightDir,false)-v)/epsL;
#endif
    return clamp(-dx*grad/scale/v, 0.,1.); // Lambert shading

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // if (max(fragCoord.x,fragCoord.y)/iResolution.y<.05) 
    // { fragColor = vec4(ambient,1.); return; }
    time = iTime*1.;
    vec2 mouse=iMouse.xy/iResolution.xy;
    if (mouse.x+mouse.y==0.) mouse.xy=vec2(0.4,.5);

    //camera
    float theta = (mouse.x*2. - 1.)*PI;
    float phi = (mouse.y - .5)*PI;
#if 1 // camera shake 
    float t=3.*time,B=.07; theta += B*cos(t); phi += B*sin(t);
#endif
    vec3 cameraPos =vec3(sin(theta)*cos(phi),sin(phi),cos(theta)*cos(phi));   
    vec3 cameraTarget = vec3(0.);
    vec3 ww = normalize( cameraPos - cameraTarget );
    vec3 uu = normalize(cross( vec3(0.,1.,0.), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec2 q = 2.*(fragCoord.xy / iResolution.y -vec2(.9,.5));
    { float  t=.5*time, c=cos(t),s=sin(t); q *= mat2(c,-s,s,c); }
    vec3 rayDir = normalize( q.x*uu + q.y*vv -1.5*ww );

    // ray-trace volume
    vec3 col=vec3(0.);
 	float transp=1., epsC=.01/2.;
    float l = .5;
    vec3 p=cameraPos+l*rayDir, p_=p;

    for (int i=0; i<200; i++) { 
        float Aloc = tweaknoise(p,true); // density field
        if (Aloc>0.01) {

#if FOG 
	      	float fog = pow(skyTrsp,length(p_-p)); p_=p;
            col += transp*skyColor*(1.-fog);
    	    transp *= fog; 
            if (transp<.001) break;
#endif            

            //{ vec3 pp=smoothstep(0.,0.2,mod(p*scale,1.)); if (pp.x*pp.y*pp.z<.02) {col+=vec3(1.); break;}}
#if SHADED          
            vec3 c = ambient+diffuse*shadedNormal(p,Aloc);
#else
            vec3 c = vec3(0.,.05,0.);
#endif            
 	        col += transp*c*Aloc;
            //if (c.r>1.) { fragColor = vec4(0.,0.,1.,1.); return; }
            col = clamp(col,0.,1.); // anomaly :-(
    	    transp *= 1.-Aloc;
	        if (transp<.001) break;
        }

        p += epsC*rayDir;
    }

   fragColor = vec4(col+ transp*skyColor, 1.);
}

float plane(vec3 pos)
{
	return length(max(abs(pos)-vec3(4.0,0.05,4.0),0.0));
}

float julia(vec3 pos)
{
    float t = iTime / 3.0;

	vec4 c = 0.5*vec4(cos(t),cos(t*1.1),cos(t*2.3),cos(t*3.1));
    vec4 z = vec4( pos, 0.0 );
	vec4 nz;

	float md2 = 1.0;
	float mz2 = dot(z,z);

	for(int i=0;i<10;i++)
	{
		md2*=4.0*mz2;
	    nz.x=z.x*z.x-dot(z.yzw,z.yzw);
		nz.yzw=2.0*z.x*z.yzw;
		z=nz+c;

		mz2 = dot(z,z);
		if(mz2>4.0)
        {
			break;
        }
	}

	return 0.25*sqrt(mz2/md2)*log(mz2);
}

float scene(vec3 pos)
{
    return min(julia(pos-vec3(0.0,1.5,0.0)), plane(pos));
}

float raymarcher( in vec3 ro, in vec3 rd )
{
	const float maxd = 15.0;
	const float precis = 0.0001;
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<100; i++ )
    {
        if( h<precis||t>maxd ) break;
	    h = scene( ro+rd*t );
        t += h * 1.0;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 normal( in vec3 pos )
{
    const float eps = 0.005;

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*scene( pos + v1*eps ) + 
					  v2*scene( pos + v2*eps ) + 
					  v3*scene( pos + v3*eps ) + 
					  v4*scene( pos + v4*eps ) );
}

float softray( in vec3 ro, in vec3 rd , in float hn)
{
    float res = 1.0;
    float t = 0.0005;
	float h = 1.0;
    for( int i=0; i<40; i++ )
    {
        h = scene(ro + rd*t);
        res = min( res, hn*h/t );
		t += clamp( h, 0.02, 2.0 );
    }
    return clamp(res,0.0,1.0);
}

float ambocc( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = scene( aopos );
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 light( in vec3 lightdir, in vec3 lightcol, in vec3 tex, in vec3 norm, in vec3 camdir )
{    
    float cosa = pow(0.5 + 0.5*dot(norm, -lightdir),2.0);
    float cosr = max(dot(-camdir, reflect(lightdir, norm)), -0.0);

    float diffuse = cosa;
    float phong = pow(cosr, 128.0);

    return lightcol * (tex * diffuse + phong);
}

vec3 background( in vec3 rd )
{
	return vec3(1.0+2.0*rd.y);
}

vec3 material( in vec3 pos , in vec3 camdir )
{    
	vec3 norm = normal(pos);

    vec3 d1 = -normalize(vec3(5.0,10.0,-20.0));
    vec3 d2 = -normalize(vec3(-5,10.0,20.0));
    vec3 d3 = -normalize(vec3(20,5.0,-5.0));
    vec3 d4 = -normalize(vec3(-20.0,5.0,5.0));

    vec3 tex = vec3(1.0);
    float sha = 1.0;
    float ao = 1.0;

    if (pos.y < 0.2)
    {
        tex = vec3(0.2);
    	sha = 0.3 + 0.7 * softray(pos, -d1,4.0) * (0.7+softray(pos, norm,2.0));
    }
    else
    {
        tex = vec3(0.3,0.1,0.2);
    	sha = 0.5 + 0.5 * softray(pos, norm, 2.0);
        ao = ambocc(pos, norm);
    }

    vec3 l1 = light(d1, vec3(1.5,1.4,1.2), tex, norm, camdir);
    vec3 l2 = light(d2, vec3(1.2,1.1,0.9), tex, norm, camdir);
    vec3 l3 = light(d3, vec3(0.6,0.7,0.9), tex, norm, camdir);
    vec3 l4 = light(d4, vec3(0.2,0.2,0.2), tex, norm, camdir);

    vec3 amb = vec3(0.05);

    return amb*ao+(l1+l2+l3+l4)*sha;
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

vec3 rayrender(vec3 pos, vec3 dir)
{
   vec3 col = vec3(0.0);

   float dist = raymarcher(pos, dir);

    if (dist==-1.0) col = background(dir);
    else
    {
    	vec3 inters = pos + dist * dir;
    	col = material(inters, dir);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    vec2 xy = (fragCoord.xy - iResolution.xy/2.0) / max(iResolution.xy.x, iResolution.xy.y);

    vec3 campos = vec3(5.0*cos(t/5.0),3.0,5.0*sin(t/5.0));
    vec3 camtar = vec3(0.0,1.5,0.0);

    mat3 camMat = calcLookAtMatrix( campos, camtar, 0.0 );
	vec3 camdir = normalize( camMat * vec3(xy,0.9) );

    vec3 col = rayrender(campos, camdir);

    col = pow(col, vec3(1.0/2.2));

	fragColor = vec4(col,1.0);
}

// using the base ray-marcher of Trisomie21: https://www.shadertoy.com/view/4tfGRB#

#define T iTime
#define r(v,t) { float a = t*T, c=cos(a),s=sin(a); v*=mat2(c,s,-s,c); }

void mainImage( out vec4 f, vec2 w ) {
    vec4 p = vec4(w,0,1)/iResolution.yyxy-.5, d; p.x-=.4; // init ray 
    r(p.xz,.13); r(p.yz,.2); r(p.xy,.1);      // camera rotations
    d = p*.5;                                 // ray dir = ray0-vec3(0)
    p.z += 5.*T;

    for (float i=1.; i>0.; i-=.01)  
    {
        vec4 u = floor(p/8.), t = mod(p, 8.)-4.; // objects id + local frame
        r(t.xy,u.x); r(t.xz,u.y); r(t.yz,1.);    // objects rotations
        u = sin(78.*(u+u.yzxw));                 // randomize ids
        t -= u;                                  // jitter positions (also cause artifacts..)
        u= .5+.5*u;

        vec4 ta = abs(t);     
        float x = max(ta.x,max(ta.y,ta.z)) -2.;      // bricks

        vec4 w,v, w1,v1;
        w = floor(t-.5)+u, v =  mod(t-4.5,1.)-.5,
        w1 = sin(78.*(w+w.yzxw)), v1 = v-w1/4.;
        x = max(-length(v1.xyz) +.4, x);             // small holes
        w = floor(t/2.-.1)+u, v =  mod(t-4.2,2.)-1., 
        w1 = sin(13.*(w+w.yzxw)), v1 = v-w1/3.;
        x = max(-length(v1.xyz) +.8, x);             // medium holes
        w = floor(t/3.)+u, v =  mod(t-4.,3.)-1.5, 
        w1 = sin(57.*(w+w.yzxw)), v1 = v-w1/2.;
        x = max(-length(v1.xyz) +1.3, x);            // big holes


        u=vec4(.7,.6,.2,1)*1.2;
        f = (.2+i)*u; 

        if(x<.01) break;    // hit !
        p -= d*x;           // march ray
     }
}

