mat3 getRotYMat(float a){return mat3(cos(a),0.,sin(a),0.,1.,0.,-sin(a),0.,cos(a));}

float map(in vec3 p, in vec3 q, inout vec3 r, inout float m)
{
	float d = 0.;
    for (int j = 0; j < 3 ; j++)
    	r=max(r.zyx*=r*=r*=r=mod(q*m+1.,2.)-1.,r.yzx),
        d=max(d,(0.12 -length(r))/m),
        m*=1.1;
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 s = iResolution.xy;
    float t = iTime*.2, c,d,m,f=0.;
    vec3 p=vec3((2.*fragCoord.xy-s)/s.x,1.),r=p-p,q=r;
    p*=getRotYMat(-t);
    q.zx += 10.+vec2(sin(t),cos(t))*3.;
    for (float i=1.; i>0.; i-=.001) 
    {
    	c=d=0.,m=1.;
        f+=0.01;
        d = map(p,q,r,m);
        q+=p*d;
        c = i;
        if(d<1e-5) break;
   	}

    vec3 eps = vec3( 0.001, 0., 0. );
    vec3 nor = normalize(vec3(
    	map(p,q+eps.xyy,r,m) - map(p,q-eps.xyy,r,m),
        map(p,q+eps.yxy,r,m) - map(p,q-eps.yxy,r,m),
        map(p,q+eps.yyx,r,m) - map(p,q-eps.yyx,r,m) ));

    float k = dot(r,r+.15);
    vec3 col= vec3(1.,k,k/c)-vec3(0.7 ,0.8, 0);
    fragColor.rgb = col/f;
}

//drawing a menger in taxicab space can be done by rotating around
//the perimeter and recursively rotating around a perimeter 1/3 the size every
//8th of a turn similar to how a sierpinski can be drawn with 1/2 scale and 
//a 3rd of a turn. You can't do both in euclidean space... s'all I'm say'n.

float sqrt2=sqrt(2.0);
float tx_cos(float a){return abs(mod(a,4.0*sqrt2)-2.0*sqrt2)/sqrt2-1.0;}
float tx_sin(float a){return tx_cos(a-sqrt2);}
vec2 tx_cossin(float a){return vec2(tx_cos(a),tx_sin(a));}
float tx_length(vec2 p){return abs(p.x)+abs(p.y);}

float InOrOut(vec2 p){
    float sn=0.5*sin(iTime);
	p.x-=(1.0-sn*2.0)*0.25;
	float b=1.0,a=0.0;
	for(int n=0;n<32;n++){
		float r=tx_length(p+tx_cossin(a)*b);
		if(r<b/(1.5+sn)){
			if(b<0.03)return 0.0; //in menger
			p+=tx_cossin(a)*b;a=0.0;b=b/(2.5+sn);
		}else {
			if(a>4.0*sqrt2)return 1.0; //not in the menger
			a+=mix(4.0/3.0,0.5,0.5+sn)*sqrt2;
		}
	}
	return 0.5;//maybies
}

