    mat3 ca = setCamera( ro, ta, 3.14159/2.0 );

    // ray direction
	vec3 rd = ca * normalize(vec3(p.xy,2.5));

    // render
    vec3 col = render( ro, rd );

    fragColor=vec4( col, 1.0 );
}

