float dot2( in vec3 v ) { return dot(v,v); }
float triangle( vec3 p, vec3 a, vec3 b, vec3 c)
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

mat3 md()
{
    return mat3(1.,0.,0.,0.,1.,0.,0.,0.,1.);
}


mat3 rotx(float a)
{
	float c = cos(a);
    float s = sin(a);
    return mat3(1.,0.,0.,0.,c,-s,0.,s,c);
}

mat3 roty(float a)
{
	float c = cos(a);
    float s = sin(a);
    return mat3(c,0.,s,0.,1.,0.,-s,0.,c);
}

mat3 rotz(float a)
{
	float c = cos(a);
    float s = sin(a);
    return mat3(c,-s,0.,s,c,0.,0.,0.,1.);
}

vec3 rot(in vec3 p, vec3 a, vec3 c)
{
    p -= c;
    return c + p * md() * rotx(a.x) * roty(a.y) * rotz(a.z); 
}

float scene(in vec3 p)
{       
    float s1 = sin(T);
    float s2 = .2 + sin(T - .2);

    vec3 nipPosL = vec3(-1.9,s1,0);
    vec3 titPosL = vec3(-1.5,.1 + s2,-2.15);
    vec3 mlPos = vec3(-1.6,s2,-1.5);
    vec3 nipPosR = vec3(1.9,s1,0);
    vec3 titPosR = vec3(1.5,.1 + s2,-2.15);
    vec3 mrPos = vec3(1.6,s2,-1.5);
    vec3 a = vec3(radians((-50. + R.y / 2.) / 16.), 0.,0);

    if (iMouse.z > 0.)
        a = radians(vec3((iMouse.y - R.y / 2.) / 16.,-(iMouse.x - R.x / 2.) / 16.,0));
    titPosL = rot(titPosL, a, nipPosL);
    mlPos = rot(mlPos, a,nipPosL);
    titPosR = rot(titPosR, a, nipPosR);
    mrPos = rot(mrPos, a, nipPosR);

    float nipL = sphere(p, vec4(nipPosL,2));
    float nipR = sphere(p, vec4(nipPosR,2));
    float titL = sphere(p, vec4(titPosL,.02));
    float titR = sphere(p, vec4(titPosR,.02));
    float mL = sphere(p, vec4(mlPos,.6));
    float mR = sphere(p, vec4(mrPos,.6));
    float b = iTime * 50.;
    return min(smin(nipL, smin(mL, titL, 15.), 20.), smin(nipR, smin(mR, titR, 15.), 20.));
}

RAY trace(float maxd)
{
    RAY r = RAY(ro, 2., .001);
    for (int i = 0; i < 128; ++i)
    {
        if (abs(r.d) < .001 || r.l > maxd)
            break;
        r.l += r.d;
        r.p = ro + rd * r.l;
        r.d = scene(r.p);
    }
    return r;
}

void init(in vec2 uv)
{
    ro = vec3(0,.6,-4);
    rd = normalize(vec3(uv, 1));
    light = vec3(0,4,-8);
	dir = normalize(vec3(0,0,-1));
}

void mainImage(out vec4 C, in vec2 U)
{
    C.rgb = vec3(0);
	vec2 uv = 2. * U.xy / R.xy - 1.;
    uv.x *= R.x / R.y;
    init(uv);
    RAY r = trace(100.);
    if (r.l < 100.)
    {
        vec3 c = mix(vec3(1.,.83,.73), vec3(.98,.7,.6), vec3(-r.p.z));
        vec3 e = vec3(.00001, 0, 0);
        vec3 n = vec3(r.d) - vec3(scene(r.p - e.xyy), scene(r.p - e.yxy), scene(r.p - e.yyx));
        float b = dot(normalize(n), normalize(light - r.p));
        C.rgb = (b * c + 0.02 * pow(b, 42.)) * (1. - r.l * .01);
    }
}

const float PI = 3.14159;

const float SNAKE_NW  = 0.10;
const float SNAKE_DW = 0.106;

const vec4 VCOL = vec4(1.0, 0.2, 0.0, 1.0);
const vec4 WCOL = vec4(0.8, 0.4, 1.0, 1.0);
const vec4 XCOL = vec4(0.2, 0.4, 0.8, 1.0);
const vec4 YCOL = vec4(0.2, 0.8, 0.2, 1.0);
const vec4 BCOL = vec4(0.0, 0.0, 0.0, 1.0);

const vec4 BACK = vec4(1.0, 0.8, 0.4, 1.0);

const float SNAKE_STEP = 0.2;


float rand(const float timeShift, const float index, const float timeStep)
{
    float f = floor((iTime + timeShift) / timeStep);

    vec2 v = vec2(mod(f + index, iResolution.y),
                  mod(f - index, iResolution.x));

    return fract(sin(dot(v.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float continuousRand(const float timeShift, const float index, const float timeStep)
{
    float c00 = 0.5 *       rand(timeShift           , index, timeStep);
    float c10 = 0.5 *       rand(timeShift + timeStep, index, timeStep);
    float c01 = 2.0 * PI  * rand(timeShift           , index + 0.5, timeStep);
    float c11 = 2.0 * PI  * rand(timeShift + timeStep, index + 0.5, timeStep);

    float t = 0.5 + 0.5 * sin(PI * (fract((iTime + timeShift) / timeStep) - 0.5));

    return (1.0 - t) * c00 * sin((iTime + timeShift) / timeStep + c01) +
                  t  * c10 * sin((iTime + timeShift) / timeStep + c11);

}

vec4 addSnakeColor(const vec4 scol, const vec4 fragColor, const float timeShift, const vec2 p)
{
    vec2 v = 2.0 * vec2(continuousRand(timeShift * SNAKE_STEP, 0.0, 1.0) + 0.5,
                        continuousRand(timeShift * SNAKE_STEP, 1.0, 1.0) + 0.5);

    float l = length(p - v);
    float x = step(l, SNAKE_NW);
    float y = step(l, SNAKE_DW) - x;

	return (scol - fragColor) * x +
           (BCOL - fragColor) * y;
}

vec4 antialiasing(const vec4 scol, const vec4 fragColor, const float timeShift, const vec2 p)
{
    vec2 DX = vec2(1.0 / iResolution.x, 0.0);
    vec2 DY = vec2(0.0, 1.0 / iResolution.y);

	return 0.25 * addSnakeColor(scol, fragColor, timeShift, 2.0 * p) +
           0.25 * addSnakeColor(scol, fragColor, timeShift, 2.0 * p + DX) +
           0.25 * addSnakeColor(scol, fragColor, timeShift, 2.0 * p + DY) +
           0.25 * addSnakeColor(scol, fragColor, timeShift, 2.0 * p + DX + DY);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = vec2(fragCoord.x / iResolution.x,
                  fragCoord.y / iResolution.y);

    fragColor = BACK;

    fragColor += antialiasing(VCOL, fragColor, 0.0, p);
    fragColor += antialiasing(WCOL, fragColor, 1.0, p);
    fragColor += antialiasing(XCOL, fragColor, 2.0, p);
    fragColor += antialiasing(YCOL, fragColor, 3.0, p);
}

#define scale 25.

float superellipse(vec2 uv,vec2 o,float r,float n)
{
    float res=pow(abs((uv.x-o.x)/r),n)+pow(abs((uv.y-o.y)/r),n);
    return res<=1.?sqrt(1.-res):.0;
}

vec3 putPixel(vec2 uv)
{
    return superellipse(fract(uv),vec2(.5),.5,3.5)*vec3(.1,.9,.07);
}

void mainImage(out vec4 C,in vec2 U)
{
