}

#define polar(l,a) (l*vec2(cos(a),sin(a)))

float tau = atan(1.0)*8.0;

float timeScale = 1.0;

float distLine(vec2 p0,vec2 p1,vec2 uv)
{
	vec2 dir = normalize(p1 - p0);
	uv = (uv - p0) * mat2(dir.x, dir.y,-dir.y, dir.x);
	return distance(uv, clamp(uv, vec2(0), vec2(distance(p0, p1), 0)));   
}

//Spirograph function (change these numbers to get different patterns)
vec2 spirograph(float t)
{
    return polar(0.30, t * 1.0) 
         + polar(0.08, t *-4.0)
         + polar(0.06, t *-8.0)
         + polar(0.05, t * 16.0)
         + polar(0.02, t * 24.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float frameTime = (1.0 / 60.0) * timeScale;

    vec2 aspect = iResolution.xy / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y - aspect/2.0;

    float lineRad = 1.0 / iResolution.y;

    float curTime = iTime * timeScale;
    float lastTime = curTime - frameTime;

    float dist = distLine(spirograph(curTime), spirograph(lastTime), uv);

    vec3 col = vec3(0.0);

    //Click to reset
    if(iMouse.w > 0.0)
    {
		col = vec3(0.0);
    }
    else
    {
        if(dist < lineRad)
        { 
            col = vec3(1.0);
        }
        else
        {
            discard;
        }        
    }

	fragColor = vec4(col,1.0);
}

/*

	Bumped Sinusoidal Warp
	----------------------

	Sinusoidal planar deformation, or the 2D sine warp effect to people 
	like me. The effect has been around for years, and there are
	countless examples on the net. IQ's "Sculpture III" is basically a 
	much more sophisticated, spherical variation.

    This particular version was modified from Fabrice's "Plop 2," which in 
	turn was a simplified version of Fantomas's "Plop." I simply reduced 
	the frequency and iteration count in order to make it less busy.

	I also threw in a texture, added point-lit bump mapping, speckles... 
	and that's pretty much it. As for why a metallic surface would be 
	defying	the laws of physics and moving like this is anyone's guess. :)

	By the way, I have a 3D version, similar to this, that I'll put up at 
	a later date.



	Related examples:

    Fantomas - Plop
    https://www.shadertoy.com/view/ltSSDV

    Fabrice - Plop 2
    https://www.shadertoy.com/view/MlSSDV

	IQ - Sculpture III (loosely related)
	https://www.shadertoy.com/view/XtjSDK

	Shane - Lit Sine Warp (far less code)
	https://www.shadertoy.com/view/Ml2XDV

*/


// Warp function. Variations have been around for years. This is
// almost the same as Fabrice's version:
// Fabrice - Plop 2
// https://www.shadertoy.com/view/MlSSDV
vec2 W(vec2 p){

    p = (p + 3.)*4.;

    float t = iTime/2.;

    // Layered, sinusoidal feedback, with time component.
    for (int i=0; i<3; i++){
        p += cos(p.yx*3. + vec2(t, 1.57))/3.;
        p += sin(p.yx + t + vec2(1.57, 0))/2.;
        p *= 1.3;
    }

    // A bit of jitter to counter the high frequency sections.
    p +=  fract(sin(p+vec2(13, 7))*5e5)*.03 - .015;

    return mod(p, 2.) - 1.; // Range: [vec2(-1), vec2(1)]

}

// Bump mapping function. Put whatever you want here. In this case, 
// we're returning the length of the sinusoidal warp function.
float bumpFunc(vec2 p){ 

	return length(W(p))*.7071; // Range: [0, 1]

}

/*
// Standard ray-plane intersection.
vec3 rayPlane(vec3 p, vec3 o, vec3 n, vec3 rd) {

    float dn = dot(rd, n);

    float s = 1e8;

    if (abs(dn) > 0.0001) {
        s = dot(p-o, n) / dn;
        s += float(s < 0.0) * 1e8;
    }

    return o + s*rd;
}
*/

vec3 smoothFract(vec3 x){ x = fract(x); return min(x, x*(1.-x)*12.); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;


    // PLANE ROTATION
    //
    // Rotating the canvas back and forth. I don't feel it adds value, in this case,
    // but feel free to uncomment it.
    //float th = sin(iTime*0.1)*sin(iTime*0.12)*2.;
    //float cs = cos(th), si = sin(th);
    //uv *= mat2(cs, -si, si, cs);


    // VECTOR SETUP - surface postion, ray origin, unit direction vector, and light postion.
    //
    // Setup: I find 2D bump mapping more intuitive to pretend I'm raytracing, then lighting a bump mapped plane 
    // situated at the origin. Others may disagree. :)  
    vec3 sp = vec3(uv, 0); // Surface posion. Hit point, if you prefer. Essentially, a screen at the origin.
    vec3 rd = normalize(vec3(uv, 1)); // Unit direction vector. From the origin to the screen plane.
    vec3 lp = vec3(cos(iTime)*.5, sin(iTime)*.2, -1); // Light position - Back from the screen.
	vec3 sn = vec3(0, 0, -1); // Plane normal. Z pointing toward the viewer.


/*
	// I deliberately left this block in to show that the above is a simplified version
	// of a raytraced plane. The "rayPlane" equation is commented out above.
	vec3 rd = normalize(vec3(uv, 1));
	vec3 ro = vec3(0, 0, -1);

	// Plane normal.
	vec3 sn = normalize(vec3(cos(iTime)*.25, sin(iTime)*.25, -1));
    //vec3 sn = normalize(vec3(0, 0, -1));

	vec3 sp = rayPlane(vec3(0), ro, sn, rd);
    vec3 lp = vec3(cos(iTime)*.5, sin(iTime)*.25, -1); 
*/    


    // BUMP MAPPING - PERTURBING THE NORMAL
    //
    // Setting up the bump mapping variables. Normally, you'd amalgamate a lot of the following,
    // and roll it into a single function, but I wanted to show the workings.
    //
    // f - Function value
    // fx - Change in "f" in in the X-direction.
    // fy - Change in "f" in in the Y-direction.
    vec2 eps = vec2(4./iResolution.y, 0);

    float f = bumpFunc(sp.xy); // Sample value multiplied by the amplitude.
    float fx = bumpFunc(sp.xy - eps.xy); // Same for the nearby sample in the X-direction.
    float fy = bumpFunc(sp.xy - eps.yx); // Same for the nearby sample in the Y-direction.

 	// Controls how much the bump is accentuated.
	const float bumpFactor = 0.05;

    // Using the above to determine the dx and dy function gradients.
    fx = (fx - f)/eps.x; // Change in X
    fy = (fy - f)/eps.x; // Change in Y.
    // Using the gradient vector, "vec3(fx, fy, 0)," to perturb the XY plane normal ",vec3(0, 0, -1)."
    // By the way, there's a redundant step I'm skipping in this particular case, on account of the 
    // normal only having a Z-component. Normally, though, you'd need the commented stuff below.
    //vec3 grad = vec3(fx, fy, 0);
    //grad -= sn*dot(sn, grad);
    //sn = normalize( sn + grad*bumpFactor ); 
    sn = normalize(sn + vec3(fx, fy, 0)*bumpFactor);           


    // LIGHTING
    //
	// Determine the light direction vector, calculate its distance, then normalize it.
	vec3 ld = lp - sp;
	float lDist = max(length(ld), .0001);
	ld /= lDist;

    // Light attenuation.    
    float atten = 1./(1. + lDist*lDist*.15);
	//float atten = min(1./(lDist*lDist*1.), 1.);

    // Using the bump function, "f," to darken the crevices. Completely optional, but I
    // find it gives extra depth.
    atten *= f*.9 + .1; // Or... f*f*.7 + .3; //  pow(f, .75); // etc.



	// Diffuse value.
	float diff = max(dot(sn, ld), 0.);  
    // Enhancing the diffuse value a bit. Made up.
    diff = pow(diff, 4.)*.66 + pow(diff, 8.)*.34; 
    // Specular highlighting.
    float spec = pow(max(dot( reflect(-ld, sn), -rd), 0.), 12.); 


    // TEXTURE COLOR
    //
	// Combining the surface postion with a fraction of the warped surface position to index 
    // into the texture. The result is a slightly warped texture, as a opposed to a completely 
    // warped one. By the way, the warp function is called above in the "bumpFunc" function,
    // so it's kind of wasteful doing it again here, but the function is kind of cheap, and
    // it's more readable this way.
    vec3 texCol = texture(iChannel0, sp.xy + W(sp.xy)/8.).xyz; 
    texCol *= texCol; // Rough sRGB to linear conversion... That's a whole other conversation. :)
    // A bit of color processing.
    texCol = smoothstep(.05, .75, pow(texCol, vec3(.75, .8, .85)));    

    // Textureless. Simple and elegant... so it clearly didn't come from me. Thanks Fabrice. :)
    //vec3 texCol = smoothFract( W(sp.xy).xyy )*.1 + .2;



    // FINAL COLOR
    // Using the values above to produce the final color.   
    vec3 col = (texCol*(diff*vec3(1, .97, .92)*2. + .5) + vec3(1, .6, .2)*spec*2.)*atten;

    // Faux environment mapping: I added this in at a later date out of sheer boredome, and  
    // because I like shiny stuff. You can comment it out if it's not to your liking. :)
    float ref = max(dot(reflect(rd, sn), vec3(1)), 0.);
    col += col*pow(ref, 4.)*vec3(.25, .5, 1)*3.;


    // Perform some statistically unlikely (but close enough) 2.0 gamma correction. :) 
	fragColor = vec4(sqrt(clamp(col, 0., 1.)), 1);
}

#define PI 3.14159265359
#define PI2 6.28318530718
#define SPIRALS 7.
#define LEV 5
//#define CELLROTATION

vec2 rotate2D(vec2 p, float t)
{
    mat2 m = mat2( cos(t), sin(t), -sin(t), cos(t) );
    return m * p;
}

vec4 phyllotaxis( vec2 uv, float offset )
{

    // initiate
    float i_s = 1.;
    float r_s = 1.;
    float t_s = 0.;
    float occ = 1.;
    float dsp = 0.5;
    vec3 n = vec3(0., 0., 1.);

    for( int i=1; i<LEV; i++ )
    {
      float zoom = i == 1 ? offset : 0.;

      // Log-Polar coordinates from UVs generated in previous iteration
      float r = length(uv);
      float lr = log(r);
      float theta = atan( uv.x, uv.y);

      // Logarithmic spiral coordinates
      vec2 spiral = vec2( theta - lr, theta + lr - zoom)/PI;

      // Phyllotaxis florets - main pattern
      // Log-polar fractions back to cartesian cells
      uv = fract( spiral * SPIRALS ) -  0.5;

      // Align new theta's using parent theta. Not very accurate as there is some distortion.
      // Also had to offset with a mysterious constant of 0.36 (golden fraction?)
      #ifdef CELLROTATION
        // Experimental part. Flatten the spiral coordinates to cells
        // and use uniform value of an entire cell to offset theta
        float cellr = floor(spiral.x * SPIRALS) - floor(spiral.y * SPIRALS);
        float cellt = floor(spiral.x * SPIRALS ) + floor(spiral.y * SPIRALS );
        vec2 uvcell = vec2( cellr, cellt / (SPIRALS / 1.55) );
        //uv = rotate2D( uv, -(theta+0.72)  ); // + 2x golden fraction?
        uv = rotate2D( uv,  -(uvcell.y) );
        t_s = theta;
      #else
        // cheap offset with golden(?) constant.
        // thetas are aligned but slightly distorted
        t_s = theta + t_s + 0.36;
      #endif

      // smooth cone tips
      float taper = smoothstep(0.0, 0.2, r) * (1. - smoothstep(0.5, 0.8, r));

      // build and layer the normals and multiply with floret radius
      n += mix( vec3(0., 0., 1.0), vec3( sin(t_s), cos(t_s), 0. ),  pow(taper, 0.5)) * r_s;        

      // comp occlusion.
      occ *= 1.-pow(r, 2.);

      // displacement is not used in this demo
      // dsp += (1.-r) * i_s * r_s;

      // store iteration multiplier for displacements
      i_s = 1. / float(i);

      // combine and store floret radius. next iteration we use it to
      // multiply displacements and normals
      r_s *= sqrt(r);
    }    

   return vec4( normalize(n), occ ); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

	vec2 m = iMouse.xy / iResolution.xy -.5;
	m.x*= iResolution.x/ iResolution.y;
	m *= 20.0;

    // VARIABLES
    float t = fract(iTime * .05) * PI;
    vec3 sp = vec3(uv - 0.4, 0.);
    vec3 lp = iMouse.z < .5 ? vec3(sin(t*5.)*10.,cos(t*8.)*10., -1.5) : vec3(m, -2.);
    vec3 ld = normalize(lp - sp);
    vec3 ro = vec3(0, 0, -0.5);
    vec3 rd = normalize(ro-sp);

    // THE PATTERN
    vec4 brocc = phyllotaxis(sp.xy, t);
    vec3 n = vec3( brocc.xy, -brocc.z);
    float occ = brocc.w;

    // COLORS
    vec3 base = vec3(0.38, 0.52, 0.26);
    vec3 diff = vec3(0.6, 0.6, 0.5);
    vec3 spec = diff;
    vec3 back = vec3(0.1, 0.01, 1.5);
    vec3 ambi = vec3(0.25, 0.44, 0.23);

    // SHADE
	diff *= max(dot(n, ld), 0.);
    back *= max(dot(n, vec3(0.4, -0.4, 0.2)), 0.);
    spec *= pow(max(dot( reflect(-ld, n), rd), 0.), 7.); 
    ambi *= occ;

    // COMP
    vec3 col = base * ambi;
    col += base * diff;
    col += spec * 0.2;
    col += base * back;

    // POST
    col *= pow(20.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y),0.5) +  0.1;
    col = sqrt(col);

	fragColor = vec4( col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv *= 7.+sin(iTime*.1)*4.;
    uv += vec2(sin(iTime*.1),cos(iTime*.044))*3.;

    for (int i = 0; i < 3; i++) {
        float ang = abs(atan(uv.y,uv.x))/4.+
            cos((abs(uv.x)+abs(uv.y))/2.);
        uv *= mat2(sin(ang), cos(ang), -cos(ang), sin(ang));

        vec2 dir = -normalize(abs(mod(uv*(1.+sin(iTime*.1)*.5),.5)-.25));
        uv = uv.yx + dir * .2 * sin(atan(uv.y,uv.x)+iTime*.14);
        uv = abs(uv);
    }

    float dst = max(0., 1.-(length(max(abs(sin(uv))-.1,0.))));
    fragColor = vec4(cos(dst*16.+iTime)*.5+.5,
                     dst,
                    dst,1.);
}

#define PI 3.14159265359
#define maxDist 1000000.0

//global variables and constants
#define lineWidth 0.003
#define amount 48.0
float delta  = 2.0 * PI / amount;
float rotationSpeed(){
    return 2.0 * iTime;
}
vec2 centre1 = vec2(-0.4, 0.1),
	centre2 = vec2(0.4, -0.1);

//basic functions
float toAngle(vec2 ray){
	return atan(ray.y, ray.x);
}
vec2 toCartesian(float angle){
	return vec2(cos(angle), sin(angle));
}
float getWidth(vec2 rayDir, vec2 rightRay){
    //normalizes the ray, if you were wondering, and compares it to rightRay
    return length(rayDir - rightRay * dot(rayDir, rightRay) / dot(rightRay, rightRay));
}
float modify(float arg){
    //fixing and making some quadrangles white
    if(arg > amount / 2.0) arg = amount - arg;

    return mod(arg, 2.0) == 0.0 ? 0.0 : arg + 1.0;
}

vec2 castRayToEdge(vec2 rayOrg, vec2 rayDir){
    //casting ray to edge of screen
    float dist_topBot, dist_leftRight;
    //topBot
    if(rayDir.y != 0.0) 
        dist_topBot = (sign(rayDir.y) - rayOrg.y) / rayDir.y;
    else dist_topBot = maxDist;
    //rightLeft    
    if(rayDir.x != 0.0)
        dist_leftRight = (sign(rayDir.x) * iResolution.x / iResolution.y - rayOrg.x) / rayDir.x;
    else dist_leftRight = maxDist;

    return rayOrg + min(dist_topBot, dist_leftRight) * rayDir;
}

float floorToGlobal(float alpha){
	return floor((rotationSpeed() - alpha) / delta);
}

float alphaToNextRay(float alpha){
    return mod(rotationSpeed() - alpha, delta);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -1.0  + 2.0 * fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y; float color = 1.0;

    //vars for calculation:
    float angle1 = toAngle(castRayToEdge(centre1, uv - centre1)), 
        angle2 = toAngle(castRayToEdge(centre2, uv - centre2));

    //coloring the lines
    if(getWidth(uv - centre1, castRayToEdge(vec2(0.0), toCartesian(angle1 + alphaToNextRay(angle1))) - centre1) < lineWidth) color = 0.0;
    if(getWidth(uv - centre2, castRayToEdge(vec2(0.0), toCartesian(angle2 + alphaToNextRay(angle2))) - centre2) < lineWidth) color = 0.0;

    //coloring the squares
    color -= 1.5 * modify(abs(floorToGlobal(angle1) - floorToGlobal(angle2))) / amount;
    fragColor = vec4(color);
}

