float doRing( vec3 p ){

    vec3 pos = p;


    float lor = sign( pos.x );


    pos.x = abs( pos.x );

    //pos.x = mod( pos.x , 1.5 );
    float degree = atan( pos.y , pos.z );

    float ogD = degree;

    degree += iTime;// * (1. + lor * .2);
    float l = length( pos.yz );



    degree = mod( degree - 3.14159  / 8. , 3.14159  / 4. );


    pos.y = l * sin( degree );
    pos.z = l * cos( degree );


    return sdSphere( pos - vec3( 2.4 ,  1. , 2.39 )  * .2  , .04 *( 1. + abs(ogD) ));

}

