vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
     return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v)
  { 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //   x0 = x0 - 0.0 + 0.0 * C.xxx;
  //   x1 = x0 - i1  + 1.0 * C.xxx;
  //   x2 = x0 - i2  + 2.0 * C.xxx;
  //   x3 = x0 - 1.0 + 3.0 * C.xxx;
  vec3 x1 = x0 - i1 + C.xxx;
  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
  i = mod289(i); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
  }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 div = vec2(10, 10);
	vec2 uv = fragCoord.xy / iResolution.xy * div.xy;
	vec3 v = vec3(
        uv.x + sin(iTime) * 0.2, 
        uv.y + cos(iTime) * 0.2, 
        iTime / 10.0);



    float noise = snoise(v);
	uv = fragCoord.xy / iResolution.xy * div.xy;
    vec3 v2 = vec3(uv.x , uv.y, iTime / 5.0);
    noise = sin(noise * 3.14 * (sin(iTime) + snoise(v2) * 2.0) * 0.75);

    float darkenFactor = 0.2;
    float darkenValue = darkenFactor;

    div = vec2(5, 5);
    uv = fragCoord.xy / iResolution.xy * div.xy;
    vec3 v3 = vec3(uv.x, uv.y, iTime / 2.0);
    darkenValue = darkenValue * snoise(v3);

    vec3 v4 = vec3(uv.x * 1000.0, uv.y * 1000.0, iTime);  

    float b = snoise(v4) * 0.1;

    fragColor = vec4(1.0 - darkenValue + (noise * (darkenValue + 0.2)) - b,
                     noise - b,
                     b,
                     1.0);



}

float pi = 3.1415;






void mainImage( out vec4 color, in vec2 pixCoord )



{
	float zoom = (iResolution.x / 10.0) * (1.0 + 0.2 * cos(2.0 * iTime));
    float cameraX = 5.0   + 4.0 * cos(iTime);
    float cameraY = 2.0  + 4.0 * sin(iTime * 1.1);
    float angle = 20.0*(pi/180.0)+iTime;




    float xBeforeRot = (pixCoord.x / zoom) - cameraX;
    float yBeforeRot = (pixCoord.y / zoom) - cameraY;
    float x = xBeforeRot * cos (angle) + yBeforeRot*sin(angle);
    float y = -xBeforeRot * sin(angle) + yBeforeRot*cos(angle);





    float hauteurMoyenne = 0.0;
    float amplitude = cos(iTime);
    float phase = 4.0 + 5.0 * cos(iTime);
    float periode = 3.0;
    float fx = hauteurMoyenne + cos(x * 2.0 * pi / periode + phase) * amplitude;

    if (y < fx) {
        color = vec4(cos(iTime)*0.5, sin(iTime)*0.3, cos(iTime), 1.0);
        return;
    }

    float fx2 = 1.0 + cos(2.5 * (x + iTime)) *sin(iTime);

    if (y < fx2) {
        color = vec4(0.6+sin(iTime), cos(iTime), 1.0, 1.0);
        return;
    }


    color = vec4(0.0, 0.0, 0.0, 1.0);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float map(vec3 p)
{
   	p.z += iTime * 6.0;
    p.y += sin(iTime + p.z * 0.25) * 5.0;
    vec3 q = fract(p) * 2.0 - 1.0;
    float result = (mix(length(q) - sin(2.0 * iTime + p.x), sdBox(q, vec3(0.25)), sin(iTime  * 2.0) * 0.5 + 0.5));
    result = sin(result);
    return result;
}

float trace(vec3 o, vec3 r)
{
    float t = 0.0;
    for (int i = 0; i < 32; ++i)
    {
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 2.0));
    float the = iTime * 0.25;
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    vec3 o = vec3(0.0, iTime, iTime);

    float st = (sin(iTime) + 1.5) * 0.4;

    float t = trace(o, r * st);

    float fog = 1.0 / (1.0 + t * t * 0.1);

    vec3 fc = vec3(fog * 2.0);

    //vec3 tint = vec3(st - 0.5,st,st + 0.5);
    float st2 = sin(iTime) * 0.5 + 0.5;
    float ct2 = cos(iTime) * 0.5 + 0.5;
    vec3 tint = vec3(st2, ct2, -st2);
	fragColor = vec4(fc * tint, 1.0);
}

float Cell(vec2 c) {
	vec2 uv = fract(c);c -= uv;
	return (1.-length(uv*2.-1.)) * step(fract(sin(c.x+c.y*1e2)*1e3), .04);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 p = fragCoord.xy / iResolution.xy -.5;
	float a = fract(atan(p.x, p.y) / 6.2832);
	float d = length(p);

	vec2 coord = vec2(pow(d, .04), a)*256.;
	vec2 delta = vec2(1. + iTime*20., 1.);

	float c = Cell(coord-=delta);
	c += Cell(coord-=delta);

	fragColor = vec4(c*d*3.);
}

