}



float ao_1_2( in vec3 pos, in vec3 nor, vec2 beats )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12 * float( i ) / 4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = doModel( aopos, beats ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
