const float zNear = 1.0;
const float zFar = 400.0;

const float epsilon = 0.01;

const vec3 sphereCenter = vec3(0.5, 0.5, 100.0);
const float sphereRadius = 25.0;


vec3 computeColor(in vec3 m, in vec3 ray, in vec3 normal, in vec3 light) {
    vec3 baseColor = vec3(1.0, 0.0, 0.0);

    vec3 diffuseColor = vec3(0.5, 0.5, 0.5);
    vec3 specularColor = vec3(0.3, 0.3, 0.3);
    vec3 ambientColor = vec3(0.2, 0.2, 0.2);

    vec3 lightRay = normalize(m - light);

    float diffuseFactor = dot(normal, lightRay);

    vec3 ambient = baseColor * ambientColor;
    vec3 diffuse = vec3(0.0, 0.0, 0.0);
    vec3 specular = vec3(0.0, 0.0, 0.0);

    if (diffuseFactor > 0.0) {
        diffuse = baseColor * diffuseColor * diffuseFactor;

        float shininess = 20.0;
        float specularFactor = dot(ray, reflect(lightRay, normal));
        if (specularFactor > 0.0) {
       		specular = specularColor * pow(max(0.0, specularFactor), shininess);
        }
    }

	return ambient + diffuse + specular;
}

float map(in vec3 m, out vec3 normal) {
    normal = normalize(m - sphereCenter);
 	return length(sphereCenter - m) - sphereRadius;
}


bool raymarch(in vec3 origin, in vec3 dir, out vec3 normal, out vec3 hit) {
	float totalDist = 0.0;
    vec3 m = vec3(origin);

    for (int i = 0; i < 50; i++) {
        float dist = map(m, normal);

        totalDist += dist;

        if (dist < epsilon) {
            hit = origin *  dir * totalDist;
        	return true;   
        }

        if (totalDist > zFar) {
            return false;
        }


        m += dir * dist;
    }
    return false;
}

