}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

Voxel map( in vec3 p) {

    Voxel voxel = Voxel(1., vec4(.12, .1, 0., 1.));

    vec2 gridPosition = floor(p.xz + .5);

    float height = rand(gridPosition);

    vec3 positionOffset = vec3(0.0, 0.0, 2.0);
    vec3 q = p;
    q.xz = mod(p.xz + positionOffset.xz + 1.0, 2.0) - 1.0;

    q.y += height;

    Voxel candle = Voxel(sdCapsule(q, vec3(0., 1., 0.), vec3(0., -1., 0.), .1), vec4(1.) * (q.y - height + .3));
    voxel = minVoxel(voxel, candle);

    Voxel plane = Voxel(p.y + 1.0, vec4(0.));                                    
	voxel = smin(voxel, plane, .5);

    Voxel wick = Voxel(sdCappedCylinder(q + vec3(0., -1.2, 0.), vec2(.01, .2)), vec4(1.));
	voxel = minVoxel(voxel, wick);

    return voxel;
}

Voxel mapCandles( in vec3 p) {

    Voxel voxel = Voxel(1., vec4(0.));

    vec2 gridPosition = floor(p.xz + .5);

    float height = rand(gridPosition);

    vec3 positionOffset = vec3(0.0, 0.0, 2.0);
    vec3 q = p;

    q.xz = mod(p.xz + positionOffset.xz + 1.0, 2.0) - 1.0;
    q.y += height;

    vec3 flamePosition = q + vec3(0., -1.5, 0.);
    Voxel flame = Voxel(sdEllipsoid(flamePosition, vec3(.1, .4, .1)), vec4(1., .5, 0., 1.) + (.2 - flamePosition.x));
    vec2 noiseOffset = flamePosition.xy;
    noiseOffset.y = noiseOffset.y - iTime + gridPosition.x + gridPosition.y;

    Voxel flameHalo = flame;

    flame.dist += noise(noiseOffset * 10.) * .10;
    voxel = minVoxel(voxel, flame);

    return voxel;
}

vec3 calcNormal ( vec3 p ) {
  vec2 e = vec2(.0001, 0.0);
  return normalize(vec3(
    map(p + e.xyy).dist - map(p - e.xyy).dist,
    map(p + e.yxy).dist - map(p - e.yxy).dist,
    map(p + e.yyx).dist - map(p - e.yyx).dist
  ));
}

vec3 calcNormalCandles ( vec3 p ) {
  vec2 e = vec2(.0001, 0.0);
  return normalize(vec3(
    mapCandles(p + e.xyy).dist - mapCandles(p - e.xyy).dist,
    mapCandles(p + e.yxy).dist - mapCandles(p - e.yxy).dist,
    mapCandles(p + e.yyx).dist - mapCandles(p - e.yyx).dist
  ));
}

Voxel rayMarch(vec3 rayOrigin, vec3 rayDirection)
{
  Voxel voxel = Voxel(0., vec4(0.0));

  float rayMarchingStep = 0.001;
  float dist = .1;

  for(int i = 0; i < 64; i++) {
    if (rayMarchingStep < 0.001 || dist > 100.) break;
    voxel = map(rayOrigin + rayDirection * dist);
    rayMarchingStep = voxel.dist;
    dist += rayMarchingStep;
  }

  vec3 normal = calcNormal(rayOrigin + rayDirection * dist);
  voxel.color *= 1.7 + dot(normal, normalize(vec3(1., 1., 1.)));

  return voxel;
}

Voxel rayMarchCandles(vec3 rayOrigin, vec3 rayDirection)
{
  Voxel voxel = Voxel(0., vec4(0.0));

  float rayMarchingStep = 0.001;
  float dist = .1;

  for(int i = 0; i < 64; i++) {
    if (rayMarchingStep < 0.001 || dist > 100.) break;
    voxel = mapCandles(rayOrigin + rayDirection * dist);
    rayMarchingStep = voxel.dist;
    dist += rayMarchingStep;
  }

  vec3 normal = calcNormalCandles(rayOrigin + rayDirection * dist);
  voxel.color *= 2. + dot(normal, normalize(vec3(1., 1., 1.)));

  return voxel;
}

