#define MAX_ITER 50
#define MAX_BOUNCES 4
#define EPS .001
#define STEP .4
#define Scale 2.
#define Offset .3
#define DIST iMouse.y*.1
#define FRACT_ITER 9
#define X_EPS .001
#define Y_EPS X_EPS
vec3 mat;

float seed = 0.0;
float rand() { return fract(sin(seed++)*43758.5453123); }

float ground(vec3 p, float height)
{
    return p.y - height;//+.2*sin(p.z*3.);
}

float sphere(vec3 p, vec3 center, float radius)
{
    return length(p-center) - radius;
}

float column(vec3 p, vec3 center, float height, float radius)
{
    return length((p-center).xz)-radius;
}

float fbm(vec2 p)
{
    float c = cos(.45);
    float s = sin(.45);

    p.x *= .2;

    mat2 m = mat2(c, -s, s, c);
    p+=vec2(1.5);

    float a = 1.5;
    float f = .132;
    float l = 7.5;
    float g = .635;
    float x = .0;
    for (int i = 0; i < 7; i++)
    {
   		x += a*texture(iChannel0, p*f).r;
        p *= m * 2.;
        a *= g;
        f *= l;
    }

    return x;
}

float de(in vec3 p)
{
    float s1 = sphere(p, vec3(0., sin(iTime), .0), .5);
    float orbit_radius = 1.3;
    float s2_angle = iTime*3.;
    float s2 = sphere(p, vec3(orbit_radius*cos(s2_angle), 
                              1., orbit_radius*sin(s2_angle)), .5);
    float f = ground(p, -.7);
    float c = column(mod(p, 8.)-4., vec3(2.), 5., .5);

    vec3 scol = vec3(1., .6, .3);

    vec2 st = sin(3.14159*p.xz/1.);
    float tile=step(st.x*st.y,0.);
    vec3 fcol = .5*vec3((tile+1.)* fbm(p.xz));

    vec3 ccol = vec3(1., 0., 0.);

    float d = min(s1, f);
    d = min(d, s2);
    d = min(d, c);
    float factor = step(f, s1);
    mat = scol*(1.-factor) + fcol*factor;
    factor = step(s2, d);
    mat = mat*(1.-factor) + ccol*factor;
    factor = step(c, d);
    mat = mat*(1.-factor) + ccol*factor;
    return d;

}

vec3 normal(vec3 p)
{
    float d = de(p);

    float x = de(vec3(p.x+EPS, p.y, p.z)) - d;
    float y = de(vec3(p.x, p.y+EPS, p.z)) - d;
    float z = de(vec3(p.x, p.y, p.z+EPS)) - d;

    return normalize(vec3(x, y, z));
}

vec3 light(vec3 norm)
{
    vec3 light_dir = normalize(vec3(-1.));

    return vec3(.15)+vec3(1.)*mat*clamp(dot(light_dir, -norm), .0, 1.);
}

float ao(vec3 p, vec3 norm)
{
    return .3+clamp(de(p+norm*.2), 0., 1.);   
}


void trace(vec3 ro, vec3 rd, inout vec3 col)
{
    float tot_d = 0.;
    float d = 0.;

    for (int j = 0; j < MAX_BOUNCES; j++)
    {
        for (int i = 0; i < MAX_ITER; i++)
        {
            vec3 pt = ro+rd*d;

            float s = de(pt);
            d += STEP*s;

            if (s < EPS)
            {
                tot_d += d;
                vec3 norm = normal(pt);
                col += light(norm) * ao(pt, norm) * exp(-tot_d*tot_d*.01);
                rd = reflect(rd, -norm);
                ro = pt + rd *EPS;
                d = 0.;
                break;
            }
        }


    }

}

vec3 color(vec2 uv)
{
    vec3 ro = vec3(0., 0., 1.5+DIST);
    vec3 rd = normalize(vec3(uv, -1.));

    vec3 col = vec3(0.);

    trace(ro, rd, col);

    return col;
}

