float hash(in float n)
{
    return fract(sin(n)*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = iTime;

    //"squarified" coordinates 

	vec2 xy = ( 2.* fragCoord -iResolution.xy ) / iResolution.y ;

    //rotating light 
    vec3 lightDir = vec3( sin( time ), 1., cos( time * .5 ) );

    const float count = 200.;

    vec3 pp = vec3(0.);
    float length = 1e10;
    for( float i = 0.; i < count; i+=1. )
    {
        //random cell
        float an = sin( time * PI * .00001 ) - hash( i ) * PI * 2.;
        float ra = sqrt( hash( an ) );

    	vec2 p = vec2( lightDir.x + cos( an ) * ra, lightDir.z + sin( an ) * ra );

        //finds the closest cell center from XY coords
        float di = distance( xy, p );
        length = min( length, di );
        if( length == di )
        {
            pp.xy = p;
            pp.z = i / count * ( xy.y*xy.x );
        }
    }

    //shimmy shake
    fragColor = vec4( pp + vec3( 1.) * ( 1.- max( 0.0, dot( pp, lightDir)) ), 1. );

}

const float PI   = 3.141519;
const float dots = 12.0;

// Conver HSV/HSB color to RGB
//convert HSV to RGB
vec3 hsv2rgb(vec3 c){
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float time = iTime/5.0;
  vec2 uv = fragCoord.xy / iResolution.xy;
  uv -= 0.5;
  vec3 color = vec3(0.0);

  // Create 12 single dots
  for(float i=0.0; i < dots; i++) {
    float radius = 0.2;
    vec2 dotPosition = vec2(radius * cos(2.0 * PI * i/dots + time/2.0),
                            radius * sin(2.0 * PI * i/dots + time));
    // dotRadius and trignometric function to create wafting effect
    float dotRadius = 0.02 * (sin(time * PI/2.0 * i) + 2.0);
    vec3 dotColor = hsv2rgb(vec3((i + time*10.)/dots,1.,1.0));
    // Add dots to main color output
    color += dotRadius/length(uv - dotPosition) * dotColor;
  }

  fragColor = vec4(color, 1.0);
}

const float BLADE       = 5.0;
const float BLADE_RADIUS  = 0.5;
const float SCAN_SPEED  = 4.5;
const float SCAN_LINE   = 1.0;


const float TAU         = 6.2831853;
const vec2 CENTER       = vec2(0.5,0.5);
const vec4 BLACK        = vec4(0.0,0.0,0.0,1.0);
const vec4 BLUE         = vec4(0.0,0.0,1.0,1.0);

bool bladeBorder(vec2 uv, float r, inout vec4 fragColor)
{
    float cur_radius = length(uv)/BLADE_RADIUS;
    if (cur_radius < r)
    {
        if (length(cur_radius - r) < 0.04)
        {
            fragColor = vec4(0.2,0.6,.4,1.0);
        }
        return true;
    }
    return false;
}

bool bladeColor(vec2 uv, float r, inout vec4 fragColor)
{
    float cur_radius = length(uv)/BLADE_RADIUS;
    if (cur_radius < r)
    {
        if (length(cur_radius - r) < 0.04)
        {
            fragColor = vec4(0.2,0.6,.4,1.0);
        }
        else
        {
			fragColor = vec4(1.0,0.0,0.0,1.0);
        }
        return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(0.0,0.0);
    vec2 center =  iResolution.xy/2.0;

    float m = min(iResolution.x, iResolution.y);
    uv = (fragCoord-center)/m;

    float time = iTime/4.0;    

    float t = atan(uv.y, uv.x);

    float dt1 = uv.y;    
    float dt2 = mod(time, TAU*BLADE);

    float t1 = (t + dt1) * BLADE;
    float t2 = (t + dt2) * BLADE;

    float dl = mod(t1 - t2, TAU);
    float dl2 = mod(t1 - t2, 2.0 * TAU);

    if (length(dl)*iResolution.y < 2.0)
    {
        fragColor = BLUE;
    }
    else
    {       
        float r1 = cos(t1);
        float r2 = cos(t2);

        if (dl >= dl2)
        {

            if (!bladeColor(uv, r1, fragColor))
            if (!bladeBorder(uv, r2, fragColor))
                fragColor = BLACK;
        }
        else
        {

            if (!bladeColor(uv, r2, fragColor))
            if (!bladeBorder(uv, r1, fragColor))
                fragColor = BLACK;
        }
    }
}

