// PairOfPants-function visualization - written 2015 by Jakob Thomsen
// (intended to be used to improve SineTree https://www.shadertoy.com/view/MljXDV)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define pi 3.1415926

float sfract(float val, float scale)
{
    return (2.0 * fract(0.5 + 0.5 * val / scale) - 1.0) * scale;
}

float mirror(float v)
{
    return abs(2.0 * fract(v * 0.5) - 1.0);
}

float fn(vec3 v)
{
    v.xy *= 1.75;
    v.z = v.z / 2.0 - 0.5;
    //v.x *= 1.75;
    //v.z = mirror(v.z) - 1.0;
    // https://en.wikipedia.org/wiki/Pair_of_pants_(mathematics)
    // implicit surface formula from http://tube.geogebra.org/m/437815
    // (NOTE: original formula without abs doesn't run on all systems - thanks again, Fabrice!)
    //return (1.0+v.z)*(pow(abs(v.x-1.0),2.0)+v.y*v.y-0.5)*(pow(abs(v.x+1.0),2.0)+v.y*v.y-0.5)-v.z*(v.x*v.x+v.y*v.y-0.5);
    // Compactified formula by FabriceNeyret2
    float d = dot(v.xy,v.xy);
    return (1.+v.z) * (d-2.*v.x+.5) * (d+2.*v.x+.5) - v.z*(d-.5);
}

vec3 nrm(vec3 v)
{
    vec3 n;
    float d = 0.01;
    n.x = fn(v + vec3(d, 0.0, 0.0)) - fn(v + vec3(-d, 0.0, 0.0));
    n.y = fn(v + vec3(0.0, d, 0.0)) - fn(v + vec3(0.0, -d, 0.0));
    n.z = fn(v + vec3(0.0, 0.0, d)) - fn(v + vec3(0.0, 0.0, -d));
    return normalize(n);
}

float comb(float v, float s)
{
    return pow(0.5 + 0.5 * cos(v * 2.0 * pi), s);
}

vec4 tex(vec3 v)
{
    float d = abs(v.z * 20. + sfract(-iTime, 4.) * 5.); 
    vec4 c = vec4(0.5 + 0.5 * nrm(v), 1.0);
    return exp(-d*d) + c * abs(nrm(v).x);
}

vec3 camera(vec2 uv, float depth)
{
    float phi = iTime*.1,  C=cos(phi), S=sin(phi);

    vec3 v = vec3(uv, depth);

    v *= mat3( 0, 1,-1,
              -1,-1,-1, 
               1,-1,-1 );

    v.zy *=  mat2 (C,S,-S,C); // could be mixed above

    return v;
}

void mainImage( out vec4 o,  vec2 uv )
{
    float t = iTime * 0.1;
    vec2 R  = iResolution.xy;
        uv = ( 2. * uv - R)  / R.y;

    vec3 w = vec3(0), v;

    for(float layer = 0.; layer < 1.; layer += 1./256.) 
        v = camera(uv, 2. * layer - 1.),
        abs(v.x) < 1. && abs(v.y) < 1. && abs(v.z) < 1. && abs(fn(v)) < .05 ?  w = v : w;

    // o = tex(w);
    o = all(equal(w, vec3(0))) ? vec4(0.0) : tex(w); // avoids flashing background
}

