const float PI = 3.14159265358979323846;
const vec4 laubergine = vec4 (float (0x77) / 255., float (0x21) / 255., float (0x6F) / 255., 1.);
const vec4 wgrey = vec4 (float (0xAE) / 255., float (0xA7) / 255., float (0x9F) / 255., 1.);
const vec4 uorange = vec4 (float (0xdd) / 255., float  (0x48) / 255., float  (0x14) / 255., 1.);
const int MAX_STEPS = 64;
const float epsilon = 0.0001;

mat4 trans (vec3 t)
{
    mat4 mat = mat4 (vec4 (1., .0, .0, .0),
                     vec4 (.0, 1., .0, .0),
                     vec4 (.0, .0, 1., .0),
                     vec4 (t.x, t.y, t.z, 1.));
    return mat;
}

vec3 opTransf (vec3 p, mat4 m)
{
    return vec4 (m * vec4 (p, 1.)).xyz;
}

mat4 rotY (in float angle)
{
    float rad = radians (angle);
    float c = cos (rad);
    float s = sin (rad);

    mat4 mat = mat4 (vec4 (  c, 0.0,  -s, 0.0),
                     vec4 (0.0, 1.0, 0.0, 0.0),
                     vec4 (  s, 0.0,   c, 0.0),
                     vec4 (0.0, 0.0, 0.0, 1.0));

    return mat;
}

mat4 rotZ (in float angle)
{
    float rad = radians (angle);
    float c = cos (rad);
    float s = sin (rad);

    mat4 mat = mat4 (vec4 (  c,   s, 0.0, 0.0),
                     vec4 ( -s,   c, 0.0, 0.0),
                     vec4 (0.0, 0.0, 1.0, 0.0),
                     vec4 (0.0, 0.0, 0.0, 1.0));

    return mat;
}

float opSubtract (float d1, float d2)
{
    return max (-d1, d2);
}

float box (vec3 p, vec3 b, float r)
{
    return length (max (abs (p) - b + vec3 (r), .0)) - r;
}

float cylinder (vec3 p, vec2 h)
{
  vec2 d = abs (vec2 (length (p.xy), p.z)) - h;
  return min (max (d.x, d.y), .0) + length (max (d, .0));
}

float wavyfloor (vec3 p, vec4 n)
{
    return dot ((p).xyz, n.xyz) + n.w - .1 * sin (4. * p.x) * cos (4. * p.z);
}

float logo (vec3 np)
{
    float d1 = .0;
    float d2 = .0;
    const float angle = 120.;
    const float offset = 60.;

    vec3 p = opTransf (np, rotZ (90.));
    const float a = 1.;          // outter circle
    const float b = 164. / 284.; // cutout circle
    const float c = 110. / 284.; // inner circle
    const float d = 10.  / 284.; // beam
    const float e = 53.  / 284.; // head-circle
    const float f = 38.  / 284.; // head-circle cutout
    const float g = 192. / 284.; // head-circle offset

    // big outter and smaller inner "circle"
    mat4 m1 = trans (vec3 (.0, .0, .0));
    d1 = cylinder (opTransf (p, m1), vec2 (a, .1));
    mat4 m2 = trans (vec3 (.0, .0, .0));
    d2 = cylinder (opTransf (p, m2), vec2 (b, .4));
    d1 = opSubtract (d2, d1);
    d2 = cylinder (opTransf (p, m1), vec2 (c, .1));
    d1 = min (d1, d2);

    // three "beams"
    m1 = trans (vec3 (.0, .2, .0));
    d2 = box (opTransf (p, m1), vec3 (d, .4, .1), .0);
    d1 = min (d1, d2);

    m1 = trans (vec3 (.0, .2, .0)) * rotZ (angle);
    d2 = box (opTransf (p, m1), vec3 (d, .4, .1), .0);
    d1 = min (d1, d2);

    m1 = trans (vec3 (.0, .2, .0)) * rotZ (2. * angle);
    d2 = box (opTransf (p, m1), vec3 (d, .4, .1), .0);
    d1 = min (d1, d2);

    // three small "circles"
    m1 = trans (vec3 (.0, g, .0)) * rotZ (offset);
    d2 = cylinder (opTransf (p, m1), vec2 (e, .1));
    d1 = min (d1, d2);

    m1 = trans (vec3 (.0, g, .0)) * rotZ (offset + angle);
    d2 = cylinder (opTransf (p, m1), vec2 (e, .1));
    d1 = min (d1, d2);

    m1 = trans (vec3 (.0, g, .0)) * rotZ (offset + 2. * angle);
    d2 = cylinder (opTransf (p, m1), vec2 (e, .1));
    d1 = min (d1, d2);

    // three small "circle"-cutouts
    m1 = trans (vec3 (.0, g, .0)) * rotZ (offset);
    d2 = cylinder (opTransf (p, m1), vec2 (f, .2));
    d1 = opSubtract (d2, d1);

    m1 = trans (vec3 (.0, g, .0)) * rotZ (offset + angle);
    d2 = cylinder (opTransf (p, m1), vec2 (f, .2));
    d1 = opSubtract (d2, d1);

    m1 = trans (vec3 (.0, g, .0)) * rotZ (offset + 2. * angle);
    d2 = cylinder (opTransf (p, m1), vec2 (f, .2));
    d1 = opSubtract (d2, d1);

    return d1;
}

float map (vec3 p, inout int matindex)
{
    float ground = .0;
    float ulogo = .0;
    float t = iTime;

    ground = wavyfloor (p, normalize (vec4 (.0, -.5, .0, 1.)));
    mat4 m = rotY (40. * t);
    ulogo = logo (opTransf (p, m));

    float d = min (ground, ulogo);
    if (d == ground) {
        matindex = 1;
    } else if (d == ulogo) {
        matindex = 2;
    }

    return d;
}

vec3 normal (vec3 p)
{
    vec3 e = vec3 (.0001, .0, .0);
    int matindex;
    float d = map (p, matindex);

    vec3 n = vec3 (map (p + e.xyy, matindex) - d,
                   map (p + e.yxy, matindex) - d,
                   map (p + e.yyx, matindex) - d);

    return normalize (n);
}

float shadow (vec3 ro, vec3 rd, float tmin, float k)
{
    float s = 1.;
    float d = .0;
    float t = tmin;
    int index = 0;
    for (int i = 0; i < 32; i++) {
        d = map (ro + rd * t, index);
        if (d < epsilon) {
            return .0;
        }
        s = min (s, k * d / t);
        t += d;
    }
    return s;
}

vec4 shade (vec3 p, vec4 color)
{
    float t = 1.5 * iTime;
    vec3 lPos0 = vec3 (3. * cos (t),       -4., 3. * sin (t));
    vec3 lPos1 = vec3 (4. * cos (-t * .2), -3., 4. * sin (-t));

    vec4 lCol = vec4 (.95, .95, .9, 1.);
    vec3 n = normal (p);
    vec3 lDir0 = normalize (lPos0 - p);
    vec3 lDir1 = normalize (lPos1 - p);
    vec4 lInt0 = lCol * dot (n, lDir0);
    vec4 lInt1 = lCol * dot (n, lDir1);
    vec4 col = shadow (p, normalize (lPos0 - p), 0.001, 64.) * lInt0 * color;
    col += shadow (p, normalize (lPos1 - p), 0.001, 64.) * lInt1 * color;

    return col;
}

mat3 camera (vec3 eye, vec3 aim, float r)
{
    vec3 cw = normalize (aim - eye);
    vec3 cp = vec3 (sin (r), cos (r), .0);
    vec3 cu = normalize (cross (cw, cp));
    vec3 cv = normalize (cross (cu, cw));
    return mat3 (cu, cv, cw);
}

float march (vec3 ro, vec3 rd, float pixelSize, inout int index)
{
    bool forceHit = false;
    float infinity = 10000.0;
    float t_min = .0001;
    float t_max = 50.0;
    float t = t_min;
    float candidate = t_min;
    float candidate_error = infinity;
    float w = 1.4;
    float lastd = .0;
    float stepSize = .0;
    float sign = map (ro, index) < .0 ? -1. : 1.;

    for (int i = 0; i < MAX_STEPS; i++)
	{
        float signedd = sign * map (ro + rd * t, index);
        float d = abs (signedd);
        bool fail = w > 1. && (d + lastd) < stepSize;

        if (fail) {
            stepSize -= w * stepSize;
            w = 1.;
        } else {
            stepSize = signedd * w;
        }

		lastd = d;

        float error = d / t;
        if (!fail && error < candidate_error) {
            candidate_error = error;
            candidate = t;
        }

        if (!fail && error < pixelSize || t > t_max) {
        	break;
		}

        t += stepSize;

	}

    if ((t > t_max || candidate_error > pixelSize) && !forceHit) {
        return infinity;
    }

	return candidate;
}

