#define DUST_MOTE_COUNT 10
#define WIDTH 1.0

float audio_freq( in sampler2D channel, in float f) { return texture( channel, vec2(f, 0.25) ).x; }
float audio_ampl( in sampler2D channel, in float t) { return texture( channel, vec2(t, 0.75) ).x; }

vec3 dust_mote(vec3 color, vec2 pos, vec2 center, float radius, float alpha, float focus){
    vec2 disp = pos - center;
    float dist = dot(disp,disp);
    vec3 mote = (color+0.005)*alpha* vec3(smoothstep(radius * (1.0+focus), radius, dist));
    return (1.0-mote)*color + mote;
}

float rnd(float s) { return sin(2923.138674*s); }

vec3 dust_motes(vec3 color, vec2 pos, const int number, float t) {
    vec3 new_color = color;
    for(int i = 0; i < DUST_MOTE_COUNT; i++) {
        float fudge = rnd(float(i));
        float cycle = fract(t+fudge);
    	float fade = 2.0 * cycle * (1.0 - cycle);
        vec2 center = vec2(fudge+sin(t*fudge+fudge), 1.1-cycle*2.0+rnd(fudge));
        new_color = dust_mote(new_color, pos, center, 0.01+0.007*fudge, 0.5*fade, 0.6 + 0.4*fudge); 
    }
    return new_color;
}

vec3 B2_spline(vec3 x) { // returns 3 B-spline functions of degree 2
    vec3 t = 3.0 * x;
    vec3 b0 = step(0.0, t)     * step(0.0, 1.0-t);
	vec3 b1 = step(0.0, t-1.0) * step(0.0, 2.0-t);
	vec3 b2 = step(0.0, t-2.0) * step(0.0, 3.0-t);
	return 0.5 * (
    	b0 * pow(t, vec3(2.0)) +
    	b1 * (-2.0*pow(t, vec3(2.0)) + 6.0*t - 3.0) + 
    	b2 * pow(3.0-t,vec3(2.0))
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y += 0.02 * sin(3.0*uv.x+iTime / 2.0);
    vec2 centered = (uv - 0.5)/WIDTH;
    float intro = smoothstep(0.0, 30.0, iTime);
    centered.y -=  intro - 1.0;
    float sample0 = audio_ampl(iChannel3, 0.02);
    centered /= 1.0 + 0.03*(sample0-0.5);
    centered.x *= 0.97 + 0.01*(1.0 + cos(iTime));
    float mirrored = abs(centered.x);
    centered.x *= iResolution.x / iResolution.y;

    float dist2 = dot(centered, centered);
    float clamped_dist = smoothstep(0.0, 1.0, dist2);
    float arclength    = abs(atan(centered.y, centered.x) / radians(360.0))+0.01;
    float shine_shift = 0.15-centered.y;

    float sample1 = audio_freq(iChannel3, mirrored + 0.01);
    float sample2 = audio_ampl(iChannel3, clamped_dist);
    float sample3 = audio_ampl(iChannel3, arclength);
    float sample4 = audio_freq(iChannel3, 0.01+.05*mirrored/(shine_shift));

    // Color variation functions
    float t = iTime / 100.0;
    float polychrome = (1.0 + sin(t*10.0))/2.0; // 0 -> uniform color, 1 -> full spectrum
    vec3 spline_args = fract(vec3(polychrome*uv.x-t) + vec3(0.0, -1.0/3.0, -2.0/3.0));
    vec3 spline = B2_spline(spline_args);

    float f = abs(centered.y);
    vec3 base_color  = vec3(1.0, 1.0, 1.0) - f*spline;
    vec3 flame_color = pow(base_color, vec3(3.0));
    vec3 disc_color  = 0.20 * base_color;
    vec3 wave_color  = 0.10 * base_color;
    vec3 flash_color = 0.05 * base_color;

    float disp_dist = smoothstep(-0.2, -0.1, sample3-dist2);
    disp_dist *= (1.0 - disp_dist);

    vec3 color = vec3(0.0);

    float shine = (sample4)*smoothstep(1.5, 0.0, shine_shift)*smoothstep(0.05, 0.3, shine_shift);
    shine = pow(shine, 5.0);

    // spline debug
    // vec3 s = smoothstep(-0.01, 0.01, spline-uv.y); color += (1.0-s) * s;

    float v = abs(centered.y);
    color += flame_color * smoothstep(v, v*8.0, sample1);
    color += disc_color  * smoothstep(0.5, 1.0, sample2) * (1.0 - clamped_dist);
    color += flash_color * smoothstep(0.5, 1.0, sample3) * clamped_dist;
    color += wave_color  * disp_dist;
    color = dust_motes(color, centered+sample0*0.03-0.06, 10, t*10.0);
    color += intro * intro*flame_color * shine;
    color = pow(color, vec3(0.4545));
	fragColor = vec4(color, 1.0);
}

vec2 sun_position_shift = vec2( 1.0,-0.5);

vec4 SkyColor = vec4(0.3, 0.3, 0.5, 1.0);

float ground_height = -0.55;

float Brightness = 0.2; 

//more steps you take more accurate will be the results
// less steps color gets fadded
const int maxSteps = 10;

float sphereRadius = 0.15;

//min considerable distance to break marching 
float epsilon = 0.01;


//returns distance of point p from sphere of given radius 
float distSphere(vec3 p, float radius)
{
    //if point is outside the radius it will return +ve
    // else it will return -ve value
    return length(p) - radius;
}

//Standard ray march implementation
float raymarch(vec3 rayOrigin, vec3 rayDirection)
{
    float t = 0.0;
    for(int i = 0; i < maxSteps; ++i)
    {
        //pick a point p from origin along rayDirection
        //at distance step t. (addition of 2 vectors)
        vec3 p = rayOrigin + rayDirection * t; 

        //calculate distance of p from sphere
        float d = distSphere(p, sphereRadius);


        //increment the step value by distance
        t += d;

        //if we are close enough to target pixel. break
        if(d < epsilon)
        {
            break;
        }
    }
    return t;
}

//Function returns the sky shade besed on uv.y 
vec4 DrawSky(vec2 uv)
{
    vec4 color = vec4(0.0, 0.0, 0.3, 1.0);
    // above 0 is sky below is ground
    if(uv.y > ground_height)
    {
        //adding gradient by adding uv.y value.
        color = SkyColor + (-uv.y * Brightness); //multiply by brightness

        //if brightness is high, change in y will be more giving bright color
    }

    return color;
}

//Function responsible for road color
float road( vec3 p )
{ 
    // draw black for x in range of [-2,2] else draw gray
    float road =  abs(p.x) < 2.0 ? 0.1 : 0.25;

    // alternator for strip 1 if z is divisible by 2 else 0
    float i = mod(floor(p.z),2.0) < 1.0 ? 1.0 : 0.0;

    //strin is narrower than road 
    float strips =  (abs(p.x) < 0.1) ? 0.25*i : 0.0;

    return road + strips;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //map uv in 0-1 range
	vec2 uv = fragCoord.xy / iResolution.xy;
    //shift uv to -1 to 1 range
    uv = uv * 2.0 - 1.0;
    //correct aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    //screen goes from 0 to 1 in x and y
    // eye (camera) is at center
    vec3 eye   = vec3(0, 0, -1);

    //ray vector starts from origin in direction of uv 
    //as uv covers all the pixels on screen thus ray
    //goes in all direction in +ve z axis. 
    vec3 ray = normalize(vec3(uv + sun_position_shift,1.0));

    //origin of ray should be eye position
    vec3 rayOrigin = normalize(eye);


    float t = raymarch(rayOrigin, ray);

    vec4 sky = vec4(1.0/t) + DrawSky(uv);

    //magic numbers 1: 0.2 here define the width of distance road
    //magic numbers 2: 1.8 here define the number of strips on road
	vec3 dir = normalize( vec3(uv.x, uv.y+0.2, 1.8));

    // dividing x and z by y - this make the road narrow as we go up in y 
    // also adding global time in z to make strips move
    vec3 path = vec3( road(dir.xzz/dir.y + vec3(0.0, 0.0, -iTime*3.0)));

    //mixing two images
    vec3 color = mix(path, sky.xyz, exp(-max(-uv.y*20.0 - 5.0,0.0)) );

    fragColor = vec4(color,1.0);
}

