// biases x to be closer to 0 or 1
// can act like a parameterized smoothstep
// https://www.desmos.com/calculator/c4w7ktzhhk
// if b is near 1.0 then numbers a little closer to 1.0 are returned
// if b is near 0.0 then numbers a little closer to 0.0 are returned
// if b is near 0.5 then values near x are returned
float bias(float x, float b) {
    b = -log2(1.0 - b);
    return 1.0 - pow(1.0 - pow(x, 1./b), b);
}


float PI = 3.1415926;
#define t iTime
#define bias_number 0.4*sin(iTime) + 0.5

float formula(float x) {
    return bias(x, bias_number);
}
bool isClose(float a, float b) { return abs(a-b) < 0.005; }
void plot(out vec4 fragColor, in vec2 uv )
{
    float px = 2.0 / iResolution.x;
    float py = 2.0 / iResolution.y;


    vec4 color = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 blue  = vec4(0.1, 0.1, 0.9, 1.0);
    if (isClose(uv.x, 0.5)) color = blue;
    if (isClose(uv.x, 1.5)) color = blue;
    if (isClose(uv.y, 0.5)) color = blue;
    if (isClose(uv.y, 1.0)) color = blue;


    float x = (uv.x - .5);
    float y = formula(x);
    float y2 = formula(x + px*.5);
    float dy = max(px*4.0, abs(y2 - y));

    float modX = floor(.5+10.0*(uv.x-.5)) / 10.0;
    float fmodX = formula(modX);

    // 2d samples
    // ok but wonky and horribly inefficient
    float avg = 0.0;
    float screen_y = 0.0;
    float stroke = 1.0;
    float dist = 0.0;
    for (float step_x = -1.0; step_x < 1.0; step_x += .1)
    {
        x = (uv.x - .5 +3.0*stroke*(-step_x)*px);

        for (float step_y = -1.0; step_y < 1.0; step_y += .1)
        {

            y = formula(x);
            screen_y = uv.y + stroke*(-step_y)*py;
            dist = step_x*step_x + step_y*step_y;
            dist /= stroke*stroke;
            avg += (1.0 - min(1.0,(abs(screen_y-.5  - .5*y)/py))) /dist;
        }
    }
    avg /= 100.0;
    color.r -= avg;
    color.g -= avg; 
    color.b -= avg;
    fragColor = color;
}



// creates white noise in the range 0..1 including 0 excluding 1
float rand(vec2 p){
    p /= iResolution.xy;
    return fract(sin(dot(p.xy, vec2(12.9898, 78.2377))) * 43758.5453);
}

// creates white noise in the range 0..1 including 0 including 1
float rand_inclusive(vec2 p){
    return clamp(rand(p)*1.005, 0.0, 1.0); 
}


void applyBias(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(
        bias(rand_inclusive(fragCoord), bias_number)
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    if (isClose(uv.x, 0.5)) {
        fragColor = vec4(0.5);
    }
    else if (uv.x < 0.5) {
        uv += vec2(0.4, 0.3);
        uv -= vec2(0.5);
        uv *= vec2(3.0, 1.0);
        uv += vec2(0.5);
        //uv += vec2(0.5, 0.0);
        plot(fragColor, uv);
    }
    else {
        applyBias(fragColor, fragCoord);
    }
}

// Line Segment (bouncing) written 2015 by Jakob Thomsen

// mirror/bounce inside -1,+1
vec2 mirror(vec2 pos)
{
    return (2.0 * abs(2.0 * fract(pos) - 1.0) - 1.0);
}

// Cleaned up by Fabrice ;)
float PointLineSegDist2d(vec2 a, vec2 b, vec2 p)
{
    p -= a; b -= a;                        // go to A referential
    float q = dot(p, b) / dot(b, b) ;      // projection of P on line AB: normalized ordinate
    b *= clamp(q, 0., 1.);                 // point on segment AB closest to P 
    return length( p - b);                 // distance to P
}

void mainImage( out vec4 o, in vec2 I )
{
    vec2 speed0 = vec2(0.0432, 0.0123);
    vec2 speed1 = vec2(0.0257, 0.0332);
    I = 2. * I / iResolution.xy - 1.;
    vec2 a = mirror(iTime * speed0);
    vec2 b = mirror(iTime * speed1);
    float d = PointLineSegDist2d(a, b, I);
	o = vec4(1.0 - smoothstep(0.0, 0.01, d)); // line
    //o = vec4(pow(1.0 - d, 17.0)); // glow
}

//	TOOLS
vec2 rotate(vec2 pos, float angle) {
	float c = cos(angle);
	float s = sin(angle);

	return mat2(c, s, -s, c) * pos;
}

//	GEOMETRY
float sphere(vec3 pos, float radius) {	return length(pos) - radius;	}
float box(vec3 pos, vec3 size) {	return length(max(abs(pos) - size, 0.0)); }

float substract(float d1, float d2) {	return max(-d1,d2);	}
float intersection(float d1, float d2) { return max(d1, d2);	}


const float thickness = .05;
const float gap = .15;
const float numLayers = 7.0;
const float diff = .3;
const float maxDist = 3.6;
const int NUM_ITER = 100;

float shell(vec3 pos, float radius, float index) {
	pos.xz        = rotate(pos.xz, sin(index * diff-iTime*.35784));
	pos.yz        = rotate(pos.yz, cos(index * diff-iTime*.7845));
	float dOuter  = sphere(pos, radius);
	float dInner  = sphere(pos, radius-thickness);
	float dBox    = box(pos + vec3(0.0, 0.0, -radius), vec3(radius));

	float dSphere = substract(dInner, dOuter);
	float d       = intersection(dSphere, dBox);
	return d;
}


float map(vec3 pos) {
	float r = 1.5;
	float d = shell(pos, r, 0.0);
	float dShell;
	for(float i=1.0; i<numLayers; i+= 1.0) {
		r += gap;
		dShell = shell(pos, r, i);
		d = min(d, dShell);
	}

	return d;
}

vec3 computeNormal(vec3 pos) {
	vec2 eps = vec2(0.01, 0.0);

	vec3 normal = vec3(
		map(pos + eps.xyy) - map(pos - eps.xyy),
		map(pos + eps.yxy) - map(pos - eps.yxy),
		map(pos + eps.yyx) - map(pos - eps.yyx)
	);
	return normalize(normal);
}


//	LIGHTING

float diffuse(vec3 normal, vec3 lightDirection) {
	return max(dot(normal, normalize(lightDirection)), 0.0);
}

vec3 diffuse(vec3 normal, vec3 lightDirection, vec3 lightColor) {
	return lightColor * diffuse(normal, lightDirection);
}

float specular(vec3 normal, vec3 dir) {
	vec3 h = normalize(normal - dir);
	return pow(max(dot(h, normal), 0.0), 50.0);
}


const vec3 lightPos0 = vec3(1.0, .75, -1.0);
const vec3 lightColor0 = vec3(1.0, 1.0, .96);
const float lightWeight0 = 0.75;

const vec3 lightPos1 = vec3(-1.0, -0.75, 0.0);
const vec3 lightColor1 = vec3(.96, .96, 1.0);
const float lightWeight1 = 0.25;


vec4 getColor(vec3 pos, vec3 dir, vec3 normal) {
	float ambient = .2;
	vec3 diff0 = diffuse(normal, lightPos0, lightColor0) * lightWeight0;
	vec3 diff1 = diffuse(normal, lightPos1, lightColor1) * lightWeight1;

	// float spec = specular(normal, dir) * 1.;
	vec3 color = vec3(ambient) + diff0 + diff1;

	return vec4(color, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = -1.0 + uv * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    float focus = 2.25;
    vec3 pos   = vec3(0.0, 0.0, -10.0);		//	position of camera
	vec3 dir   = normalize(vec3(uv, focus));	//	ray
	vec4 color = vec4(.1, .1, .1, 1.0);
	float prec = .0001;
	float d;


	for(int i=0; i<NUM_ITER; i++) {
		d = map(pos);						//	distance to object

		if(d < prec) {						// 	if get's really close, set as hit the object
			color       = vec4(1.0);
			vec3 normal = computeNormal(pos);
			color       = getColor(pos, dir, normal);
			break;
		}

		pos += d * dir;						//	move forward by
		if(length(pos) > maxDist) break;
	}

    fragColor = color;
}

vec3 col1 = vec3( 0.938, 0.678, 0.375 );
vec3 col2 = vec3( 0.455, 0.456, 0.55 );

float hillSoftening = 0.01;
float reflectionSoftening = 0.1;



float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
        f = f*f*(3.0-2.0*f);
        float n = p.x + p.y*57.0;
        float res = mix(mix( hash(n+  0.0), hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
        return res;
}

float fbm( vec2 p )
{
        float f = 0.0;
        f += 0.50000*noise( p ); p = p*2.02;
        f += 0.25000*noise( p ); p = p*2.03;
        f += 0.12500*noise( p ); p = p*2.01;
        f += 0.06250*noise( p ); p = p*2.04;
        f += 0.03125*noise( p );
        return f/0.984375;
}



vec4 mod289(vec4 x)
{
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x)
{
    return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
    return 1.79284291400159 - 0.85373472095314 * r;
}

vec2 fade(vec2 t) {
    return t*t*t*(t*(t*6.0-15.0)+10.0);
}

float pnoise(vec2 P, vec2 rep)
{
    vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
    vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
    Pi = mod(Pi, rep.xyxy); // To create noise with explicit period
    Pi = mod289(Pi);        // To avoid truncation effects in permutation
    vec4 ix = Pi.xzxz;
    vec4 iy = Pi.yyww;
    vec4 fx = Pf.xzxz;
    vec4 fy = Pf.yyww;

    vec4 i = permute(permute(ix) + iy);

    vec4 gx = fract(i * (1.0 / 41.0)) * 2.0 - 1.0 ;
    vec4 gy = abs(gx) - 0.5 ;
    vec4 tx = floor(gx + 0.5);
    gx = gx - tx;

    vec2 g00 = vec2(gx.x,gy.x);
    vec2 g10 = vec2(gx.y,gy.y);
    vec2 g01 = vec2(gx.z,gy.z);
    vec2 g11 = vec2(gx.w,gy.w);

    vec4 norm = taylorInvSqrt(vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11)));
    g00 *= norm.x;  
    g01 *= norm.y;  
    g10 *= norm.z;  
    g11 *= norm.w;  

    float n00 = dot(g00, vec2(fx.x, fy.x));
    float n10 = dot(g10, vec2(fx.y, fy.y));
    float n01 = dot(g01, vec2(fx.z, fy.z));
    float n11 = dot(g11, vec2(fx.w, fy.w));

    vec2 fade_xy = fade(Pf.xy);
    vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
    float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
    return 2.3 * n_xy;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3( 0.0 );

    if (abs(uv.x) < 1.0)
    {


    	// add sky
    	col += mix( col1, col2, abs(uv.y) ) + 0.2;
    	col += mix( vec3(1.0), vec3(0.3), abs((uv.y - 0.45) * 1.5) ) * 0.3;

    	// add hills
    	for (int i = 0; i < 2; i++)
    	{
        	float edge = fbm( vec2(0.0, uv.x * 3.0 + float(i)) ) * 0.15 + 0.1;
    		if (uv.y < edge + hillSoftening && uv.y > 0.0)
        		col -= 0.8 * smoothstep( edge + hillSoftening, edge, uv.y );
    	}

    	// add vignette
    	float r = length( uv );
    	col -= r * r * 0.2;

    	float dist = 1.0 - abs(uv.y) * 0.9;
    	uv.y *= 0.9;

    	// add shadows
    	if (uv.y < 0.1)
    	{
        	uv.x *= dist * 1.2;
        	col -= 0.6 * smoothstep(-0.3, 0.1, uv.y);

        	float sand = pnoise(vec2(uv.x * 3.5 * dist + dist * 100.0, uv.y * 40.0 * dist), vec2(100.0, 100.0)) - 
            		 pnoise(vec2(uv.x * 5.0 * dist * dist * dist, uv.y * 1.0 * dist), vec2(100.0, 100.0));
     		if (sand > 0.4)
            	col -= smoothstep(0.4, 0.7, dist) * 0.2 * smoothstep( 0.4, 0.8, sand); 

    	}

    	// warp reflections
    	if (uv.y < 0.0)
    	{
    		uv.x += 0.05 * pnoise(vec2(uv.x * 10.0 * dist, uv.y * 100.0 * dist + iTime * 0.5), vec2(100.0, 100.0));
    		uv.y += 0.04 * pnoise(vec2(uv.x * 10.0 * dist, uv.y * (100.0 * dist + 20.0) + iTime * 0.5), vec2(50.0, 100.0)) * (0.5 + abs(uv.x) * 2.0) - 0.05;
    	}

   		// add mountain reflections
    	for (int i = 0; i < 2; i++)
    	{
        	float edge = fbm( vec2(0.0, uv.x * 3.0 + float(i)) ) * 0.1 + 0.1;

        	if (uv.y > -edge - reflectionSoftening && uv.y < 0.1)
        		col -= 0.3 * smoothstep( -edge - reflectionSoftening, -edge, uv.y );
    	}

    	// highlights
    	col -= smoothstep( 0.5, 0.0, abs(uv.y + 0.6) ) * 0.2;
    	col += smoothstep( 0.3, 0.0, abs(uv.y + 0.3) ) * 0.1;
    	col -= smoothstep( 0.6, 0.0, abs(uv.y + 1.0) ) * 0.1;
    	col.z += 0.05 * (1.0 - dist);
    }

	fragColor = vec4(col,1.0);
}

vec2 calcMandleBase(vec2 xy, vec2 c)
{
    return vec2(xy.x*xy.x-xy.y*xy.y + c.x, 2.0*xy.x*xy.y + c.y);
}

float calcMandelBrot(vec2 xy)
{
    float count = 0.0;
    float vecLength = 0.0;
    vec2 mandleBase = xy;
    for(int i = 0; i < 500; i++)
    {
    	mandleBase = calcMandleBase(mandleBase, xy);
    	vecLength = length(mandleBase);
        if(vecLength >= 2.0)
        {
         	break;   
        }
        count++;
    }

    return count;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 Position = (fragCoord - iResolution.xy/2.0) / iResolution.xy;
    float mandleBrot = mod(calcMandelBrot((Position+vec2(0.08802,0.1058)*pow(iTime * 10.0,2.0))*4.0/(pow(iTime* 10.0,2.0))),iTime * 10.0) / (iTime * 10.0);
    float mandleBrot2 = mod(calcMandelBrot((Position+vec2(0.08702,0.1055)*pow(iTime * 20.0,2.0))*4.0/(pow(iTime* 20.0,2.0))),iTime * 20.0) / (iTime * 20.0);
    float mandleBrot3 = mod(calcMandelBrot((Position+vec2(0.08602,0.1045)*pow(iTime * 40.0,2.0))*4.0/(pow(iTime* 40.0,2.0))),iTime * 5.0) / (iTime * 5.0);
    fragColor = vec4(mandleBrot, mandleBrot2 , mandleBrot3,1.0);
}

