float hash1( float n ) { return fract(sin(n)*158.5453123); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 2.5*cos(an), 1.0, 2.5*sin(an) );
    vec3 ta = vec3( 0.0, 1.0, 0.0 );
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

    // sphere center	
	vec3 sc = vec3(0.0,1.0,0.0);

    vec3 col = vec3(1.0);

    // raytrace
	float tmin = 10000.0;
	vec3  nor = vec3(0.0);
	float occ = 1.0;
	vec3  pos = vec3(0.0);

	// raytrace-plane
	float h = (0.0-ro.y)/rd.y;
	if( h>0.0 ) 
	{ 
		tmin = h; 
		nor = vec3(0.0,1.0,0.0); 
		pos = ro + h*rd;
		vec3 di = sc - pos;
		float l = length(di);
		occ = 1.0 - dot(nor,di/l)*1.0*1.0/(l*l); 
        col = vec3(1.0);
	}

	// raytrace-sphere
	vec3  ce = ro - sc;
	float b = dot( rd, ce );
	float c = dot( ce, ce ) - 1.0;
	h = b*b - c;
	if( h>0.0 )
	{
		h = -b - sqrt(h);
		if( h<tmin ) 
		{ 
			tmin=h; 
			nor = normalize(ro+h*rd-sc); 
			occ = 0.5 + 0.5*nor.y;
		}

        vec2 fi = inverseSF(nor);
        col = 0.5 + 0.5*sin( hash1(fi.x*13.0)*3.0 + 1.0 + vec3(0.0,1.0,1.0));
        col *= smoothstep(0.02, 0.03, fi.y);
        col *= mix( 1.0, 1.0 - smoothstep(0.12, 0.125, fi.y), smoothstep(-0.1,0.1,sin(iTime) )) ;
        col *= 1.0 + 0.1*sin(250.0*fi.y);
        col *= 1.5;
	}

	if( tmin<100.0 )
	{
	    pos = ro + tmin*rd;
		col *= occ;
		col = mix( col, vec3(1.0), 1.0-exp( -0.003*tmin*tmin ) );
	}

	col = sqrt( col );

	fragColor = vec4( col, 1.0 );
}

const float iter    = 64.,
            divAng  = 24. * 6.2831853/360.,
            circRad = .23, 
    	    rat     = .045/circRad;

float nearestMult(float v, float of) {
	float m = mod(v, of);
	v -= m * sign(of/2. - m);
	return v - mod(v,of);
}

//Color palette function taken from iq's shader @ https://www.shadertoy.com/view/ll2GD3
#define  pal(t) ( .5 + .5* cos( 6.283*( t + vec4(0,1,2,0)/3.) ) )

void mainImage( out vec4 o, vec2 uv ) {
    vec2 R = iResolution.xy,
         center = vec2(0.), p;

	float time = iTime,
          sCircRad = circRad*rat, 
          ds = (3.2+ 1.3*abs(sin(time/10.))) * rat,
          ang, dist,
	      M = max(R.x, R.y);

 	uv = ( uv -.5*R) / M / .9;
    o = vec4(0.0);
	for(float i=0.;i< iter;i+=1.) {
        p = uv-center;
		ang =  atan(p.y,p.x);		
        ang = nearestMult(ang, divAng);     
		center += sCircRad/rat* vec2(cos(ang), sin(ang));
		dist = distance( center, uv);

		if( dist <=sCircRad )
             o += 15.*dist * pal( fract(dist/sCircRad + abs(sin(time/2.))) );

  		sCircRad *= ds;
	}
}

vec2 softPolyOffset;
int softPolyIndex;

float poly(vec2 p, int n, float r)
{
    float d=0.;
    for(int i=0;i<8;++i)
    {
        float a=float(i)/float(n)*acos(-1.)*2.;
        float b=max(0.,dot(p,vec2(cos(a),sin(a)))-r);
        d+=b*b;
    }
    return sqrt(d);
}


float heart(vec2 p,float s)
{
    float d=max(dot(p, vec2(-1., -1.2)*8.), dot(p, vec2(1., -1.2)*8.));
    float u=abs(p.x) + 1.7;
    float v=max(0.0, p.y + .9);
    return length(vec2(d, length(vec2(u, v))))-1.8-s;
}

float softPoly(vec2 p, int n, float r, float s)
{
    if(softPolyIndex==12)
    {
        float d=heart(p,r-s);
        return clamp(smoothstep(0.,s*2.,d),0.,1.);
    }      
    p=abs(p);
    if(p.x>p.y)
        p=p.yx;
    float aa=float(n);
    p*=mat2(cos(aa),sin(aa),-sin(aa),cos(aa));
    p-=softPolyOffset;
    float d=poly(p,n,r-s);
    return clamp(smoothstep(0.,s*2.,d),0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 t=(uv-vec2(.5))*1.5;
    t.x*=iResolution.x/iResolution.y;
    vec3 col=vec3(0.2,0.15,0.1)*(cos((t.y*100.+sin(t.x+t.y*5.)*10.*cos(t.x*3.)*sin(t.y*20.))*2.)*.1+.9);
    float depth=0.;
    float shad0=1.,shad1=1.;

    for(int i=0;i<20;++i)
    {
        softPolyIndex=i;
        softPolyOffset=vec2(cos(float(i)+iTime*0.3),sin(float(i)*2.+iTime*0.1))*0.4;
        vec2 p=t.xy;
        vec2 p2=p;
        int n=3+int(mod(float(i),7.));
        float r=0.2;
        float a=1.0-softPoly(p2,n,r,0.003);
        float as0=softPoly(p2+2.0*vec2(0.002,0.005)*(1.0+float(i)-depth),n,r,0.01+0.003*(1.0+float(i)-depth));
        float as1=softPoly(p2,n,r,0.01+0.01*(1.0+float(i)-depth));
        shad0*=as0;
        shad1*=as1;
        shad0=mix(shad0,1.,a);
        shad1=mix(shad1,1.,a);
        vec3 c=(i==12)?vec3(1,.3,.6):vec3(1);
        col=mix(col,c,a);
        depth=mix(depth,float(i+1),a);
    }

    col=(.6*.5*col*mix(0.,1.,shad0)*vec3(1,1,.6)+.5*vec3(.8,.8,1)*col*mix(0.,1.,shad1));

    col+=pow(1.0-smoothstep(0.,3.,-t.y+1.),4.)*vec3(1,1,.6)*.3;

    fragColor.rgb=sqrt(col);
}

#define pi 3.14159265359

